-- Questions pour le livre des Actes des Apôtres
-- L'histoire de l'Église primitive

-- L'Ascension et la Pentecôte (Chapitres 1-2)
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation, verse_ref) VALUES
('act_001', 'ACT', 'À qui Luc adresse-t-il le livre des Actes ?', '["À Pierre","À Théophile","À Paul","À l''Église"]', 1, 'Luc adresse son second livre à Théophile, comme l''évangile.', 'Actes 1:1'),
('act_002', 'ACT', 'Combien de jours Jésus apparut-il aux disciples après sa résurrection ?', '["7 jours","40 jours","50 jours","3 jours"]', 1, 'Jésus se montra vivant pendant quarante jours, parlant du royaume de Dieu.', 'Actes 1:3'),
('act_003', 'ACT', 'Que devaient attendre les disciples à Jérusalem ?', '["Le Messie","La promesse du Père - le Saint-Esprit","Un ange","Le retour de Jésus"]', 1, 'Ils devaient attendre la promesse du Père, le baptême du Saint-Esprit.', 'Actes 1:4-5'),
('act_004', 'ACT', 'Où les disciples seraient-ils témoins de Jésus ?', '["Seulement à Jérusalem","À Jérusalem, en Judée, en Samarie, et jusqu''aux extrémités de la terre","En Galilée seulement","En Égypte"]', 1, 'Le témoignage devait s''étendre de Jérusalem jusqu''aux extrémités de la terre.', 'Actes 1:8'),
('act_005', 'ACT', 'Que se passa-t-il quand Jésus monta au ciel ?', '["Rien de visible","Une nuée le déroba à leurs yeux","Un éclair","Un tremblement de terre"]', 1, 'Une nuée le déroba à leurs regards.', 'Actes 1:9'),
('act_006', 'ACT', 'Qui apparut aux disciples après l''ascension ?', '["Moïse et Élie","Deux hommes en vêtements blancs","Des anges avec des trompettes","Jean-Baptiste"]', 1, 'Deux hommes en blanc leur annoncèrent que Jésus reviendrait de la même manière.', 'Actes 1:10-11'),
('act_007', 'ACT', 'Qui fut choisi pour remplacer Judas ?', '["Paul","Matthias","Barnabas","Silas"]', 1, 'Le sort tomba sur Matthias qui fut associé aux onze apôtres.', 'Actes 1:26'),
('act_008', 'ACT', 'Quel jour le Saint-Esprit fut-il répandu ?', '["Le jour du sabbat","Le jour de la Pentecôte","Le jour de Pâque","Le jour des Tabernacles"]', 1, 'Le jour de la Pentecôte, ils furent tous remplis du Saint-Esprit.', 'Actes 2:1-4'),
('act_009', 'ACT', 'Qu''est-ce qui apparut au-dessus des disciples à la Pentecôte ?', '["Des colombes","Des langues de feu qui se posèrent sur chacun d''eux","Des auréoles","Des étoiles"]', 1, 'Des langues semblables à du feu se posèrent sur chacun d''eux.', 'Actes 2:3'),
('act_010', 'ACT', 'Que firent les disciples quand ils furent remplis du Saint-Esprit ?', '["Ils prièrent silencieusement","Ils se mirent à parler en d''autres langues","Ils chantèrent","Ils dansèrent"]', 1, 'Ils se mirent à parler en d''autres langues selon que l''Esprit leur donnait de s''exprimer.', 'Actes 2:4'),
('act_011', 'ACT', 'Pourquoi la foule était-elle étonnée ?', '["À cause des miracles","Parce que chacun entendait parler dans sa propre langue","À cause du bruit","À cause du feu"]', 1, 'Chacun les entendait parler dans sa propre langue maternelle.', 'Actes 2:6'),
('act_012', 'ACT', 'Quel prophète Pierre cita-t-il dans son sermon ?', '["Ésaïe","Joël","Jérémie","Daniel"]', 1, 'Pierre cita la prophétie de Joël sur l''effusion de l''Esprit.', 'Actes 2:16-17'),
('act_013', 'ACT', 'Combien de personnes furent baptisées ce jour-là ?', '["120","500","Environ 3000","5000"]', 2, 'Environ trois mille personnes furent baptisées ce jour-là.', 'Actes 2:41'),
('act_014', 'ACT', 'Comment les premiers chrétiens vivaient-ils ?', '["Chacun pour soi","Ils avaient tout en commun","Isolés les uns des autres","En cachette"]', 1, 'Ils étaient ensemble et avaient tout en commun.', 'Actes 2:44'),
('act_015', 'ACT', 'Que faisaient les premiers chrétiens ensemble ?', '["Seulement prier","Persévérer dans l''enseignement, la communion fraternelle, la fraction du pain et les prières","Seulement manger","Travailler"]', 1, 'Ils persévéraient dans ces quatre activités essentielles.', 'Actes 2:42'),

-- Pierre et Jean, premiers miracles et persécution (Chapitres 3-5)
('act_016', 'ACT', 'Où Pierre et Jean guérirent-ils un boiteux ?', '["Au temple, à la Belle Porte","Au marché","Chez Pierre","Dans la rue"]', 0, 'L''homme était assis à la porte du temple appelée la Belle.', 'Actes 3:2'),
('act_017', 'ACT', 'Que dit Pierre au boiteux ?', '["Voici de l''argent","Je n''ai ni argent ni or, mais ce que j''ai, je te le donne: au nom de Jésus, lève-toi","Va en paix","Attends ici"]', 1, 'Pierre donna ce qu''il avait de plus précieux: la guérison au nom de Jésus.', 'Actes 3:6'),
('act_018', 'ACT', 'Qui arrêta Pierre et Jean ?', '["Les Romains","Les sacrificateurs et les sadducéens","Les pharisiens","La foule"]', 1, 'Les sacrificateurs et les sadducéens étaient mécontents de leur enseignement.', 'Actes 4:1'),
('act_019', 'ACT', 'Comment Pierre et Jean répondirent-ils au Sanhédrin ?', '["Nous allons nous taire","Il faut obéir à Dieu plutôt qu''aux hommes","Pardonnez-nous","Nous ne savons pas"]', 1, 'Ils affirmèrent leur obéissance à Dieu avant tout.', 'Actes 4:19'),
('act_020', 'ACT', 'Après leur libération, que demandèrent les disciples dans la prière ?', '["La protection","La hardiesse pour annoncer la Parole","La vengeance","La richesse"]', 1, 'Ils demandèrent de la hardiesse pour témoigner malgré les menaces.', 'Actes 4:29'),
('act_021', 'ACT', 'Qui vendit un champ et déposa l''argent aux pieds des apôtres ?', '["Pierre","Barnabas","Paul","Silas"]', 1, 'Joseph, surnommé Barnabas, donna généreusement.', 'Actes 4:36-37'),
('act_022', 'ACT', 'Quel péché Ananias et Saphira commirent-ils ?', '["Le vol","Mentir au Saint-Esprit en gardant une partie du prix","L''adultère","L''idolâtrie"]', 1, 'Ils mentirent sur le prix de leur propriété.', 'Actes 5:3-4'),
('act_023', 'ACT', 'Que leur arriva-t-il ?', '["Ils furent excommuniés","Ils moururent tous deux","Ils furent emprisonnés","Ils se repentirent"]', 1, 'Ananias puis Saphira tombèrent morts.', 'Actes 5:5,10'),
('act_024', 'ACT', 'Où les apôtres furent-ils emprisonnés ?', '["Dans une maison","Dans la prison publique","Chez le grand prêtre","Dans le temple"]', 1, 'Ils furent mis dans la prison publique.', 'Actes 5:18'),
('act_025', 'ACT', 'Comment furent-ils libérés ?', '["Par un tremblement de terre","Un ange ouvrit les portes de la prison pendant la nuit","Par Gamaliel","Par corruption"]', 1, 'Un ange du Seigneur ouvrit les portes et les fit sortir.', 'Actes 5:19'),
('act_026', 'ACT', 'Quel conseil Gamaliel donna-t-il au Sanhédrin ?', '["Tuez-les","Laissez-les; si cette œuvre est de Dieu, vous ne pourrez la détruire","Emprisonnez-les longtemps","Exilez-les"]', 1, 'Gamaliel conseilla la prudence et la patience.', 'Actes 5:38-39'),

-- Les diacres et Étienne (Chapitres 6-7)
('act_027', 'ACT', 'Pourquoi sept diacres furent-ils choisis ?', '["Pour prêcher","Pour servir aux tables et s''occuper des veuves","Pour guérir","Pour baptiser"]', 1, 'Les apôtres voulaient se consacrer à la prière et au ministère de la Parole.', 'Actes 6:2-4'),
('act_028', 'ACT', 'Quelles qualités devaient avoir les diacres ?', '["Être riches","Être remplis d''Esprit Saint et de sagesse","Être âgés","Être instruits"]', 1, 'Ils devaient être pleins de l''Esprit et de sagesse.', 'Actes 6:3'),
('act_029', 'ACT', 'Qui était le premier des sept diacres nommés ?', '["Philippe","Nicolas","Étienne","Prochore"]', 2, 'Étienne était un homme plein de foi et d''Esprit Saint.', 'Actes 6:5'),
('act_030', 'ACT', 'De quoi Étienne fut-il accusé ?', '["De vol","De blasphème contre Moïse et contre Dieu","De meurtre","De trahison"]', 1, 'De faux témoins l''accusèrent de blasphème.', 'Actes 6:11'),
('act_031', 'ACT', 'Que virent les membres du Sanhédrin en regardant Étienne ?', '["La colère","Son visage comme celui d''un ange","La peur","L''orgueil"]', 1, 'Son visage était comme celui d''un ange.', 'Actes 6:15'),
('act_032', 'ACT', 'Quel long discours Étienne prononça-t-il ?', '["Une courte prière","Un résumé de l''histoire d''Israël depuis Abraham","Une accusation contre Rome","Un chant"]', 1, 'Étienne rappela toute l''histoire du salut depuis Abraham.', 'Actes 7:2-53'),
('act_033', 'ACT', 'Que vit Étienne avant de mourir ?', '["Des anges","Les cieux ouverts et Jésus debout à la droite de Dieu","Une lumière","Rien"]', 1, 'Il vit la gloire de Dieu et Jésus debout à la droite de Dieu.', 'Actes 7:55-56'),
('act_034', 'ACT', 'Comment Étienne mourut-il ?', '["Décapité","Lapidé","Crucifié","Brûlé"]', 1, 'Ils le lapidèrent tandis qu''il priait.', 'Actes 7:59'),
('act_035', 'ACT', 'Quelle fut la dernière prière d''Étienne ?', '["Sauve-moi","Seigneur, ne leur impute pas ce péché","Venge-moi","Aide-moi"]', 1, 'Comme Jésus, il demanda le pardon pour ses bourreaux.', 'Actes 7:60'),
('act_036', 'ACT', 'Qui gardait les vêtements de ceux qui lapidaient Étienne ?', '["Pierre","Saul (Paul)","Barnabas","Un soldat romain"]', 1, 'Les témoins déposèrent leurs vêtements aux pieds d''un jeune homme nommé Saul.', 'Actes 7:58'),

-- Philippe et la conversion de l'eunuque (Chapitre 8)
('act_037', 'ACT', 'Qui persécutait l''Église après la mort d''Étienne ?', '["Les Romains","Saul","Les Grecs","Hérode"]', 1, 'Saul ravageait l''Église, entrant dans les maisons.', 'Actes 8:3'),
('act_038', 'ACT', 'Où Philippe alla-t-il prêcher ?', '["À Rome","En Samarie","En Égypte","À Babylone"]', 1, 'Philippe descendit dans une ville de Samarie et y prêcha.', 'Actes 8:5'),
('act_039', 'ACT', 'Qui était Simon le magicien ?', '["Un disciple","Un magicien qui voulait acheter le pouvoir du Saint-Esprit","Un pharisien","Un prêtre"]', 1, 'Simon offrit de l''argent pour recevoir le pouvoir de donner l''Esprit.', 'Actes 8:18-19'),
('act_040', 'ACT', 'Que Pierre dit-il à Simon ?', '["Tu es béni","Que ton argent périsse avec toi","Bienvenue parmi nous","Tu es pardonné"]', 1, 'Pierre le réprimanda sévèrement pour avoir pensé acquérir le don de Dieu.', 'Actes 8:20'),
('act_041', 'ACT', 'Qui l''ange envoya-t-il vers l''eunuque éthiopien ?', '["Pierre","Philippe","Paul","Barnabas"]', 1, 'Un ange dit à Philippe d''aller sur le chemin de Gaza.', 'Actes 8:26'),
('act_042', 'ACT', 'Qui était l''eunuque éthiopien ?', '["Un esclave","Un ministre de Candace, reine d''Éthiopie","Un soldat","Un marchand"]', 1, 'Il était un haut fonctionnaire de la reine Candace.', 'Actes 8:27'),
('act_043', 'ACT', 'Quel passage de l''Écriture l''eunuque lisait-il ?', '["Les Psaumes","Ésaïe 53, sur le serviteur souffrant","La Genèse","L''Exode"]', 1, 'Il lisait le passage sur l''agneau mené à la boucherie.', 'Actes 8:32-33'),
('act_044', 'ACT', 'Que demanda l''eunuque après avoir entendu la bonne nouvelle ?', '["Du temps pour réfléchir","Voici de l''eau; qu''est-ce qui empêche que je sois baptisé?","Des preuves","Un signe"]', 1, 'Il demanda immédiatement le baptême.', 'Actes 8:36'),
('act_045', 'ACT', 'Que se passa-t-il après le baptême de l''eunuque ?', '["Ils mangèrent ensemble","L''Esprit enleva Philippe et l''eunuque continua joyeux","Ils prièrent longtemps","Philippe resta avec lui"]', 1, 'L''Esprit enleva Philippe, et l''eunuque poursuivit son chemin joyeux.', 'Actes 8:39'),

-- La conversion de Saul (Chapitre 9)
('act_046', 'ACT', 'Où Saul allait-il quand il fut converti ?', '["À Jérusalem","À Damas pour arrêter les chrétiens","À Rome","En Galilée"]', 1, 'Saul allait à Damas avec des lettres pour arrêter les disciples.', 'Actes 9:1-2'),
('act_047', 'ACT', 'Que se passa-t-il sur le chemin de Damas ?', '["Un tremblement de terre","Une lumière du ciel l''enveloppa et une voix lui parla","Un ange apparut","Rien de spécial"]', 1, 'Une lumière resplendit du ciel et Jésus lui parla.', 'Actes 9:3-4'),
('act_048', 'ACT', 'Que dit la voix à Saul ?', '["Tu es béni","Saul, Saul, pourquoi me persécutes-tu?","Bienvenue","Va prêcher"]', 1, 'Jésus s''identifia aux chrétiens persécutés.', 'Actes 9:4'),
('act_049', 'ACT', 'Que répondit Saul ?', '["Laisse-moi","Qui es-tu, Seigneur?","Je ne comprends pas","Va-t''en"]', 1, 'Saul demanda l''identité de celui qui lui parlait.', 'Actes 9:5'),
('act_050', 'ACT', 'Combien de jours Saul resta-t-il aveugle ?', '["Un jour","Trois jours, sans manger ni boire","Sept jours","Quarante jours"]', 1, 'Il resta trois jours sans voir, sans manger ni boire.', 'Actes 9:9'),
('act_051', 'ACT', 'Qui fut envoyé pour guérir Saul ?', '["Pierre","Ananias de Damas","Barnabas","Philippe"]', 1, 'Le Seigneur envoya Ananias vers Saul.', 'Actes 9:10-11'),
('act_052', 'ACT', 'Qu''est-ce qui tomba des yeux de Saul ?', '["Rien","Comme des écailles, et il recouvra la vue","Des larmes","De la poussière"]', 1, 'Des écailles tombèrent de ses yeux et il fut guéri.', 'Actes 9:18'),
('act_053', 'ACT', 'Comment les disciples de Damas réagirent-ils ?', '["Avec joie immédiate","Avec crainte car Saul était connu comme persécuteur","Avec indifférence","En le chassant"]', 1, 'Ils eurent peur car ils connaissaient sa réputation.', 'Actes 9:26'),
('act_054', 'ACT', 'Qui présenta Paul aux apôtres à Jérusalem ?', '["Pierre","Barnabas","Jean","Jacques"]', 1, 'Barnabas le prit et le présenta aux apôtres.', 'Actes 9:27'),
('act_055', 'ACT', 'Comment Paul s''échappa-t-il de Damas ?', '["Par la porte principale","Descendu dans une corbeille le long de la muraille","Par un tunnel","Déguisé"]', 1, 'Les disciples le descendirent de nuit dans une corbeille.', 'Actes 9:25'),

-- Pierre et Corneille (Chapitre 10)
('act_056', 'ACT', 'Qui était Corneille ?', '["Un Juif pieux","Un centenier romain de la cohorte italienne","Un pharisien","Un esclave"]', 1, 'Corneille était un officier romain craignant Dieu.', 'Actes 10:1-2'),
('act_057', 'ACT', 'Quelle vision Pierre eut-il à Joppé ?', '["Une échelle","Une grande nappe descendant du ciel avec des animaux","Un ange","Une lumière"]', 1, 'Pierre vit une nappe avec toutes sortes d''animaux.', 'Actes 10:11-12'),
('act_058', 'ACT', 'Que Dieu dit-il à Pierre dans la vision ?', '["Prie davantage","Tue et mange - ce que Dieu a purifié, ne le regarde pas comme impur","Jeûne","Reste ici"]', 1, 'Dieu lui enseigna à ne plus considérer impur ce qu''il a purifié.', 'Actes 10:13-15'),
('act_059', 'ACT', 'Combien de fois la vision se répéta-t-elle ?', '["Une fois","Deux fois","Trois fois","Sept fois"]', 2, 'Cela arriva trois fois, puis la nappe fut retirée au ciel.', 'Actes 10:16'),
('act_060', 'ACT', 'Quelle vérité Pierre comprit-il ?', '["Manger de tout","Dieu ne fait pas acception de personnes - l''évangile est pour tous","Voyager davantage","Jeûner plus"]', 1, 'Pierre comprit que l''évangile était aussi pour les païens.', 'Actes 10:34-35'),
('act_061', 'ACT', 'Que se passa-t-il pendant que Pierre prêchait chez Corneille ?', '["Rien","Le Saint-Esprit descendit sur tous les auditeurs","Un tremblement de terre","Une lumière"]', 1, 'L''Esprit tomba sur les païens qui entendirent la Parole.', 'Actes 10:44'),
('act_062', 'ACT', 'Comment les croyants juifs réagirent-ils ?', '["Avec colère","Stupéfaits que l''Esprit soit donné aussi aux païens","Avec indifférence","En partant"]', 1, 'Ils furent étonnés que le don soit aussi pour les nations.', 'Actes 10:45'),
('act_063', 'ACT', 'Que fit alors Pierre ?', '["Il pria","Il les baptisa car ils avaient reçu l''Esprit comme eux","Il partit","Il refusa"]', 1, 'Pierre ordonna qu''ils soient baptisés.', 'Actes 10:47-48'),

-- L'Église d'Antioche et la famine (Chapitre 11)
('act_064', 'ACT', 'Où les disciples furent-ils appelés chrétiens pour la première fois ?', '["À Jérusalem","À Antioche","À Rome","À Damas"]', 1, 'C''est à Antioche que les disciples furent appelés chrétiens.', 'Actes 11:26'),
('act_065', 'ACT', 'Qui prophétisa une grande famine ?', '["Pierre","Agabus","Paul","Philippe"]', 1, 'Agabus prédit par l''Esprit une famine sous Claude.', 'Actes 11:28'),
('act_066', 'ACT', 'Que firent les disciples d''Antioche en réponse à la prophétie ?', '["Ils fuirent","Ils envoyèrent une aide aux frères de Judée","Ils prièrent seulement","Ils ignorèrent"]', 1, 'Ils envoyèrent une collecte par Barnabas et Saul.', 'Actes 11:29-30'),

-- Les voyages missionnaires de Paul (Chapitres 13-14)
('act_067', 'ACT', 'Qui envoya Barnabas et Saul en mission ?', '["Pierre","Le Saint-Esprit, par l''église d''Antioche","Les anciens de Jérusalem","Un ange"]', 1, 'Le Saint-Esprit dit: Mettez-moi à part Barnabas et Saul.', 'Actes 13:2'),
('act_068', 'ACT', 'Quelle île visitèrent-ils d''abord ?', '["Malte","Chypre","Crète","Rhodes"]', 1, 'Ils allèrent d''abord à Chypre.', 'Actes 13:4'),
('act_069', 'ACT', 'Qui les accompagnait au début ?', '["Silas","Jean-Marc","Timothée","Tite"]', 1, 'Jean-Marc les accompagnait comme aide.', 'Actes 13:5'),
('act_070', 'ACT', 'Qui était Sergius Paulus ?', '["Un magicien","Le proconsul de Chypre qui crut","Un Juif","Un prêtre"]', 1, 'Sergius Paulus, homme intelligent, crut après avoir vu un miracle.', 'Actes 13:7,12'),
('act_071', 'ACT', 'Qui s''opposa à eux à Chypre ?', '["Les Romains","Élymas le magicien","Les Juifs","Les prêtres"]', 1, 'Élymas cherchait à détourner le proconsul de la foi.', 'Actes 13:8'),
('act_072', 'ACT', 'Que Paul fit-il à Élymas ?', '["Il le bénit","Il le frappa de cécité temporaire","Il le chassa","Il l''ignora"]', 1, 'Paul le frappa d''aveuglement pour un temps.', 'Actes 13:11'),
('act_073', 'ACT', 'Où Paul prêcha-t-il un long sermon résumant l''histoire d''Israël ?', '["À Jérusalem","À la synagogue d''Antioche de Pisidie","À Rome","À Athènes"]', 1, 'Paul prêcha dans la synagogue d''Antioche de Pisidie.', 'Actes 13:14-41'),
('act_074', 'ACT', 'Pour qui les païens furent-ils appelés à croire ?', '["Pour eux-mêmes","Pour la vie éternelle - ils étaient destinés à croire","Pour les Juifs","Pour l''empire"]', 1, 'Tous ceux qui étaient destinés à la vie éternelle crurent.', 'Actes 13:48'),
('act_075', 'ACT', 'Pour qui la foule de Lystre prit-elle Barnabas et Paul ?', '["Des prophètes","Zeus et Hermès (des dieux)","Des anges","Des rois"]', 1, 'Ils crurent que les dieux étaient descendus sous forme humaine.', 'Actes 14:11-12'),
('act_076', 'ACT', 'Comment Paul et Barnabas réagirent-ils ?', '["Ils acceptèrent l''honneur","Ils déchirèrent leurs vêtements et protestèrent","Ils fuirent","Ils prièrent"]', 1, 'Ils déchirèrent leurs vêtements et proclamèrent qu''ils étaient des hommes.', 'Actes 14:14-15'),
('act_077', 'ACT', 'Que subit Paul à Lystre ?', '["Il fut honoré","Il fut lapidé et laissé pour mort","Il fut emprisonné","Il fut fouetté"]', 1, 'Des Juifs persuadèrent la foule de le lapider.', 'Actes 14:19'),
('act_078', 'ACT', 'Que fit Paul après avoir été lapidé ?', '["Il mourut","Il se releva et entra dans la ville","Il fut guéri par un ange","Il s''enfuit"]', 1, 'Entouré des disciples, il se leva et rentra dans la ville.', 'Actes 14:20'),

-- Le concile de Jérusalem (Chapitre 15)
('act_079', 'ACT', 'Quelle question provoqua le concile de Jérusalem ?', '["Le baptême","La circoncision et la loi de Moïse pour les païens","Le sabbat","Les dîmes"]', 1, 'Certains enseignaient que les païens devaient être circoncis.', 'Actes 15:1'),
('act_080', 'ACT', 'Quelle décision le concile prit-il ?', '["Les païens devaient être circoncis","Ne pas imposer la circoncision mais s''abstenir des idoles, du sang, etc.","Garder toute la loi","Pas de restriction"]', 1, 'Ils décidèrent de ne pas imposer de fardeau inutile aux païens.', 'Actes 15:28-29'),
('act_081', 'ACT', 'Qui prononça le discours décisif au concile ?', '["Paul","Pierre puis Jacques","Barnabas","Jean"]', 1, 'Pierre et Jacques parlèrent en faveur de la liberté des païens.', 'Actes 15:7-21'),
('act_082', 'ACT', 'Pourquoi Paul et Barnabas se séparèrent-ils ?', '["Une dispute doctrinale","Un désaccord sur Jean-Marc","La maladie","Un ordre de Dieu"]', 1, 'Barnabas voulait emmener Jean-Marc, mais Paul refusa.', 'Actes 15:37-39'),
('act_083', 'ACT', 'Qui accompagna Paul après la séparation ?', '["Barnabas","Silas","Timothée","Tite"]', 1, 'Paul choisit Silas et partit.', 'Actes 15:40'),

-- Le deuxième voyage missionnaire (Chapitres 16-18)
('act_084', 'ACT', 'Qui Paul trouva-t-il et circoncit à Lystre ?', '["Tite","Timothée, dont la mère était juive et le père grec","Jean-Marc","Apollos"]', 1, 'Paul prit Timothée avec lui et le circoncit à cause des Juifs.', 'Actes 16:1-3'),
('act_085', 'ACT', 'Quelle vision Paul eut-il à Troas ?', '["Un ange","Un Macédonien lui demandant: Passe en Macédoine et secours-nous","Jésus","Une lumière"]', 1, 'Un Macédonien le supplia de venir les aider.', 'Actes 16:9'),
('act_086', 'ACT', 'Qui fut la première convertie en Europe ?', '["Une esclave","Lydie, marchande de pourpre de Thyatire","Une Grecque","Une Romaine"]', 1, 'Lydie, femme craignant Dieu, fut baptisée avec sa maison.', 'Actes 16:14-15'),
('act_087', 'ACT', 'Pourquoi Paul et Silas furent-ils emprisonnés à Philippes ?', '["Pour avoir prêché","Pour avoir délivré une servante possédée, ruinant le profit de ses maîtres","Pour vol","Pour blasphème"]', 1, 'En chassant l''esprit de divination, ils privèrent les maîtres de leur gain.', 'Actes 16:16-24'),
('act_088', 'ACT', 'Que faisaient Paul et Silas en prison à minuit ?', '["Ils dormaient","Ils priaient et chantaient des hymnes à Dieu","Ils complotaient une évasion","Ils se lamentaient"]', 1, 'Ils priaient et chantaient, et les prisonniers les écoutaient.', 'Actes 16:25'),
('act_089', 'ACT', 'Que se passa-t-il soudainement ?', '["Rien","Un grand tremblement de terre ouvrit les portes et les chaînes","Un ange apparut","Un incendie"]', 1, 'Un tremblement de terre ébranla la prison et ouvrit tout.', 'Actes 16:26'),
('act_090', 'ACT', 'Quelle question le geôlier posa-t-il ?', '["Qui êtes-vous?","Que faut-il que je fasse pour être sauvé?","Pourquoi êtes-vous ici?","Où allez-vous?"]', 1, 'Le geôlier, tremblant, demanda comment être sauvé.', 'Actes 16:30'),
('act_091', 'ACT', 'Quelle fut la réponse de Paul ?', '["Fais de bonnes œuvres","Crois au Seigneur Jésus et tu seras sauvé, toi et ta famille","Suis la loi","Donne ton argent"]', 1, 'Paul annonça le salut par la foi en Jésus.', 'Actes 16:31'),
('act_092', 'ACT', 'Comment les Juifs de Thessalonique réagirent-ils ?', '["Ils crurent tous","Ils suscitèrent une émeute par jalousie","Ils ignorèrent Paul","Ils l''honorèrent"]', 1, 'Les Juifs jaloux provoquèrent des troubles dans la ville.', 'Actes 17:5'),
('act_093', 'ACT', 'Pourquoi les Béréens furent-ils loués ?', '["Leur richesse","Ils examinaient chaque jour les Écritures pour vérifier ce qu''on leur disait","Leur nombre","Leur force"]', 1, 'Ils recevaient la Parole avec empressement et vérifiaient dans les Écritures.', 'Actes 17:11'),
('act_094', 'ACT', 'Où Paul prêcha-t-il devant les philosophes ?', '["À Rome","À l''Aréopage d''Athènes","À Corinthe","À Éphèse"]', 1, 'Paul fut amené à l''Aréopage pour expliquer sa nouvelle doctrine.', 'Actes 17:19'),
('act_095', 'ACT', 'Quel dieu inconnu Paul utilisa-t-il comme point de départ ?', '["Zeus","Le Dieu inconnu - ce que vous adorez sans le connaître","Apollon","Hermès"]', 1, 'Paul trouva un autel au Dieu inconnu et leur annonça ce Dieu.', 'Actes 17:23'),
('act_096', 'ACT', 'Comment certains réagirent-ils au message de la résurrection ?', '["Tous crurent","Certains se moquèrent, d''autres voulurent en entendre davantage","Tous rejetèrent","Ils furent indifférents"]', 1, 'La résurrection provoqua des moqueries mais aussi de l''intérêt.', 'Actes 17:32'),
('act_097', 'ACT', 'Combien de temps Paul resta-t-il à Corinthe ?', '["3 mois","18 mois","1 an","3 ans"]', 1, 'Paul demeura un an et six mois à Corinthe.', 'Actes 18:11'),
('act_098', 'ACT', 'Qui étaient Aquilas et Priscille ?', '["Des Juifs de Corinthe","Des fabricants de tentes, expulsés de Rome, qui accueillirent Paul","Des Grecs convertis","Des magistrats"]', 1, 'Paul travailla avec eux car ils exerçaient le même métier.', 'Actes 18:2-3'),

-- Le troisième voyage et Paul à Jérusalem (Chapitres 19-21)
('act_099', 'ACT', 'Combien de temps Paul enseigna-t-il à Éphèse ?', '["6 mois","2 ans dans l''école de Tyrannus","3 mois","1 an"]', 1, 'Paul enseigna pendant deux ans dans l''école de Tyrannus.', 'Actes 19:9-10'),
('act_100', 'ACT', 'Qu''arriva-t-il aux fils de Scéva qui tentèrent d''exorciser ?', '["Ils réussirent","L''esprit mauvais les battit et ils s''enfuirent nus et blessés","Rien","Ils furent loués"]', 1, 'L''esprit mauvais dit: Je connais Jésus et Paul, mais vous?', 'Actes 19:14-16'),
('act_101', 'ACT', 'Que firent les convertis d''Éphèse avec leurs livres de magie ?', '["Ils les gardèrent","Ils les brûlèrent publiquement - valeur de 50000 pièces d''argent","Ils les vendirent","Ils les cachèrent"]', 1, 'Ils apportèrent leurs livres et les brûlèrent devant tous.', 'Actes 19:19'),
('act_102', 'ACT', 'Qui provoqua l''émeute à Éphèse ?', '["Les Juifs","Démétrius l''orfèvre dont le commerce d''idoles était menacé","Les magistrats","Paul"]', 1, 'Démétrius suscita une émeute car l''évangile ruinait son commerce.', 'Actes 19:24-28'),
('act_103', 'ACT', 'Que criait la foule pendant deux heures ?', '["Mort à Paul!","Grande est la Diane des Éphésiens!","Vive Rome!","À bas les chrétiens!"]', 1, 'La foule criait en l''honneur de leur déesse.', 'Actes 19:34'),
('act_104', 'ACT', 'Qui tomba d''une fenêtre pendant que Paul prêchait tard ?', '["Un vieillard","Eutyche, un jeune homme qui s''endormit","Un enfant","Une femme"]', 1, 'Eutyche s''endormit et tomba du troisième étage.', 'Actes 20:9'),
('act_105', 'ACT', 'Que fit Paul ?', '["Il pleura","Il le prit dans ses bras et dit: Son âme est en lui","Il pria de loin","Il appela un médecin"]', 1, 'Paul descendit, le prit dans ses bras et le ressuscita.', 'Actes 20:10'),
('act_106', 'ACT', 'Où Paul fit-il ses adieux aux anciens d''Éphèse ?', '["À Éphèse","À Milet","À Troas","À Corinthe"]', 1, 'Il les convoqua à Milet car il était pressé.', 'Actes 20:17'),
('act_107', 'ACT', 'Quelle prophétie Agabus fit-il concernant Paul ?', '["Tu mourras bientôt","Les Juifs de Jérusalem te lieront et te livreront aux païens","Tu seras roi","Tu retourneras à Antioche"]', 1, 'Agabus lia ses propres pieds et mains pour illustrer ce qui attendait Paul.', 'Actes 21:10-11'),
('act_108', 'ACT', 'Comment Paul répondit-il aux supplications de ne pas monter à Jérusalem ?', '["Il céda","Je suis prêt à être lié et à mourir pour le nom du Seigneur Jésus","Il hésita","Il pria pour une direction"]', 1, 'Paul était déterminé malgré les avertissements.', 'Actes 21:13'),

-- L'arrestation et les procès de Paul (Chapitres 22-26)
('act_109', 'ACT', 'Pourquoi Paul fut-il arrêté au temple ?', '["Il prêchait","On l''accusa faussement d''avoir introduit un païen dans le temple","Il priait trop fort","Il blasphéma"]', 1, 'On l''accusa à tort d''avoir souillé le temple.', 'Actes 21:28-29'),
('act_110', 'ACT', 'Quelle était la citoyenneté de Paul ?', '["Juif seulement","Citoyen romain de naissance","Grec","Égyptien"]', 1, 'Paul était citoyen romain, ce qui lui donnait des droits.', 'Actes 22:27-28'),
('act_111', 'ACT', 'Comment Paul divisa-t-il le Sanhédrin ?', '["Par un miracle","En soulevant la question de la résurrection des morts","Par une accusation","En s''enfuyant"]', 1, 'Pharisiens et sadducéens se disputèrent sur la résurrection.', 'Actes 23:6-7'),
('act_112', 'ACT', 'Qui avertit Paul d''un complot contre sa vie ?', '["Un ange","Le fils de sa sœur","Pierre","Un soldat"]', 1, 'Son neveu entendit le complot et l''avertit.', 'Actes 23:16'),
('act_113', 'ACT', 'Où Paul fut-il transféré pour sa sécurité ?', '["À Rome","À Césarée, chez le gouverneur Félix","À Antioche","À Damas"]', 1, 'Paul fut envoyé à Césarée avec une escorte militaire.', 'Actes 23:23-24'),
('act_114', 'ACT', 'Devant quels gouverneurs Paul comparut-il à Césarée ?', '["Pilate et Hérode","Félix puis Festus","César et Auguste","Tibère et Claude"]', 1, 'Paul comparut devant Félix, puis Festus le remplaça.', 'Actes 24:1; 25:1'),
('act_115', 'ACT', 'À qui Paul fit-il appel ?', '["Au Sanhédrin","À César - comme citoyen romain","Au grand prêtre","À Festus"]', 1, 'Paul invoqua son droit d''appel à l''empereur.', 'Actes 25:11'),
('act_116', 'ACT', 'Devant quel roi Paul témoigna-t-il ?', '["Hérode le Grand","Le roi Agrippa II et Bérénice","César","David"]', 1, 'Agrippa vint à Césarée et entendit Paul.', 'Actes 25:23; 26:1'),
('act_117', 'ACT', 'Que dit Agrippa à Paul ?', '["Tu es fou","Tu me persuades presque d''être chrétien","Je te condamne","Continue"]', 1, 'Agrippa reconnut la puissance de l''argumentation de Paul.', 'Actes 26:28'),

-- Le voyage à Rome et le naufrage (Chapitres 27-28)
('act_118', 'ACT', 'Qui accompagna Paul vers Rome ?', '["Pierre","Luc (nous dans le texte) et Aristarque","Barnabas","Silas"]', 1, 'Le narrateur (Luc) et Aristarque accompagnèrent Paul.', 'Actes 27:1-2'),
('act_119', 'ACT', 'Que Paul avertit-il concernant le voyage ?', '["Tout irait bien","Le voyage serait dangereux avec des pertes","Ils arriveraient rapidement","Le temps serait beau"]', 1, 'Paul prédit des dommages pour la cargaison, le navire et les vies.', 'Actes 27:10'),
('act_120', 'ACT', 'Quel vent violent frappa le navire ?', '["Un ouragan","Euraquilon (vent du nord-est)","Un typhon","Une tornade"]', 1, 'Le vent Euraquilon se déchaîna contre eux.', 'Actes 27:14'),
('act_121', 'ACT', 'Combien de jours dura la tempête ?', '["3 jours","14 jours sans voir le soleil ni les étoiles","7 jours","40 jours"]', 1, 'Pendant quatorze jours, ils dérivèrent dans l''Adriatique.', 'Actes 27:27'),
('act_122', 'ACT', 'Quelle promesse Paul fit-il aux passagers ?', '["Certains mourront","Aucune vie ne sera perdue, seulement le navire","Nous retournerons","Le voyage sera annulé"]', 1, 'Un ange assura Paul que tous seraient sauvés.', 'Actes 27:22-24'),
('act_123', 'ACT', 'Combien de personnes étaient sur le navire ?', '["100","276 personnes","500","50"]', 1, 'Nous étions en tout 276 personnes sur le navire.', 'Actes 27:37'),
('act_124', 'ACT', 'Où le navire fit-il naufrage ?', '["En Crète","À Malte","En Sicile","À Chypre"]', 1, 'Ils échouèrent sur l''île de Malte.', 'Actes 28:1'),
('act_125', 'ACT', 'Qu''arriva-t-il quand une vipère mordit Paul ?', '["Il mourut","Rien - les habitants pensèrent d''abord qu''il était un meurtrier, puis un dieu","Il tomba malade","Il fut guéri par Luc"]', 1, 'Paul secoua la vipère sans être affecté.', 'Actes 28:3-6'),
('act_126', 'ACT', 'Qui Paul guérit-il à Malte ?', '["Un lépreux","Le père de Publius et d''autres malades de l''île","Un aveugle","Un paralytique"]', 1, 'Paul guérit le père de Publius puis d''autres malades.', 'Actes 28:8-9'),
('act_127', 'ACT', 'Combien de temps Paul resta-t-il en résidence surveillée à Rome ?', '["6 mois","2 ans entiers","1 an","3 ans"]', 1, 'Paul demeura deux ans à Rome dans sa propre demeure.', 'Actes 28:30'),
('act_128', 'ACT', 'Que faisait Paul pendant ce temps ?', '["Il se reposait","Il prêchait le royaume de Dieu et enseignait sur Jésus-Christ","Il écrivait seulement","Il attendait"]', 1, 'Il prêchait et enseignait avec une entière liberté.', 'Actes 28:31'),
('act_129', 'ACT', 'Comment le livre des Actes se termine-t-il ?', '["Par la mort de Paul","Par Paul prêchant sans empêchement","Par un miracle","Par une vision"]', 1, 'Le livre se termine sur Paul enseignant librement à Rome.', 'Actes 28:31'),
('act_130', 'ACT', 'Quel est le thème principal des Actes ?', '["La vie de Pierre","L''expansion de l''Église par la puissance du Saint-Esprit","Les miracles","Les voyages"]', 1, 'Les Actes montrent l''Église grandissant par l''Esprit de Jérusalem à Rome.', 'Actes 1:8');

-- Mise à jour du nombre de questions pour Actes
UPDATE book_quizzes SET question_count = (SELECT COUNT(*) FROM book_quiz_questions WHERE book_id = 'ACT') WHERE book_id = 'ACT';
