-- Questions pour le livre du Deutéronome
-- Les discours de Moïse avant l'entrée en Canaan

-- Premier discours - Rappel historique (Chapitres 1-4)
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation, verse_ref) VALUES
('deu_001', 'DEU', 'Où Moïse prononça-t-il les discours du Deutéronome ?', '["En Égypte","Dans les plaines de Moab, au-delà du Jourdain","Au mont Sinaï","À Jérusalem"]', 1, 'Ces paroles furent prononcées dans les plaines de Moab, en face de Canaan.', 'Deutéronome 1:1'),
('deu_002', 'DEU', 'Combien de temps s''est écoulé depuis le départ du mont Horeb jusqu''à Kadès-Barnéa ?', '["Un mois","Onze jours","Quarante jours","Un an"]', 1, 'Il y a onze journées depuis Horeb jusqu''à Kadès-Barnéa par le chemin du mont Séir.', 'Deutéronome 1:2'),
('deu_003', 'DEU', 'Quand Moïse prononça-t-il ces paroles ?', '["Au début du voyage","La quarantième année, le onzième mois","La première année","La dixième année"]', 1, 'C''était la quarantième année, le premier jour du onzième mois.', 'Deutéronome 1:3'),
('deu_004', 'DEU', 'Pourquoi Moïse avait-il établi des chefs sur le peuple ?', '["Pour les contrôler","Parce que le peuple était devenu trop nombreux pour lui seul","Pour les punir","Par ordre du pharaon"]', 1, 'Moïse expliqua qu''il ne pouvait plus porter seul un peuple si nombreux.', 'Deutéronome 1:9-13'),
('deu_005', 'DEU', 'Comment Dieu a-t-il porté Israël selon Moïse ?', '["Comme un serviteur","Comme un homme porte son fils","Comme un roi","Comme un maître"]', 1, 'L''Éternel t''a porté comme un homme porte son fils dans tout le désert.', 'Deutéronome 1:31'),
('deu_006', 'DEU', 'Quels pays Israël ne devait-il pas attaquer ?', '["Canaan","Édom (Ésaü), Moab (Lot) et Ammon (Lot)","L''Égypte","La Philistie"]', 1, 'Dieu donna des terres à Édom, Moab et Ammon; Israël ne devait pas les attaquer.', 'Deutéronome 2:4-5,9,19'),
('deu_007', 'DEU', 'Qui étaient Sihon et Og ?', '["Des prophètes","Des rois amoréens vaincus par Israël","Des anciens d''Israël","Des sacrificateurs"]', 1, 'Sihon, roi de Hesbon, et Og, roi de Basan, furent vaincus par Israël.', 'Deutéronome 2:26-3:11'),
('deu_008', 'DEU', 'Quelle était la taille du lit de Og roi de Basan ?', '["5 coudées","9 coudées de long sur 4 de large en fer","7 coudées","12 coudées"]', 1, 'Le lit de fer de Og mesurait neuf coudées de long et quatre de large.', 'Deutéronome 3:11'),
('deu_009', 'DEU', 'Pourquoi Moïse ne pouvait-il pas entrer en Canaan ?', '["Son âge","À cause du péché aux eaux de Mériba où Dieu se mit en colère","Sa fatigue","Son refus"]', 1, 'L''Éternel s''irrita contre Moïse à cause du peuple et lui interdit d''entrer.', 'Deutéronome 3:26'),
('deu_010', 'DEU', 'D''où Moïse put-il contempler le pays promis ?', '["Du mont Sinaï","Du sommet du Pisga","Du bord du Jourdain","D''une tour"]', 1, 'Moïse monta au sommet du Pisga pour contempler le pays.', 'Deutéronome 3:27'),
('deu_011', 'DEU', 'Que Moïse interdit-il concernant la représentation de Dieu ?', '["La prière","Faire des images taillées de quelque forme que ce soit","Les sacrifices","Le chant"]', 1, 'Vous n''avez vu aucune figure au Sinaï; ne faites pas d''image taillée.', 'Deutéronome 4:15-18'),
('deu_012', 'DEU', 'Combien de villes de refuge Moïse établit-il à l''est du Jourdain ?', '["Une","Deux","Trois","Six"]', 2, 'Moïse désigna trois villes de refuge de l''autre côté du Jourdain, à l''orient.', 'Deutéronome 4:41-43'),

-- Les Dix Commandements répétés (Chapitre 5)
('deu_013', 'DEU', 'À qui s''adressaient les commandements selon Moïse ?', '["Aux ancêtres seulement","À nous qui sommes vivants aujourd''hui","Aux enfants seulement","Aux nations"]', 1, 'L''Éternel a traité cette alliance non avec nos pères, mais avec nous qui sommes vivants aujourd''hui.', 'Deutéronome 5:3'),
('deu_014', 'DEU', 'Quel est le premier commandement ?', '["Honore ton père et ta mère","Tu n''auras pas d''autres dieux devant ma face","Tu ne tueras point","Tu ne voleras point"]', 1, 'Tu n''auras pas d''autres dieux devant ma face.', 'Deutéronome 5:7'),
('deu_015', 'DEU', 'Quelle raison est donnée pour le sabbat dans Deutéronome ?', '["La création en six jours","Le souvenir de la libération de l''esclavage en Égypte","La bénédiction de Dieu","Le repos des animaux"]', 1, 'Souviens-toi que tu as été esclave en Égypte, c''est pourquoi tu observeras le sabbat.', 'Deutéronome 5:15'),
('deu_016', 'DEU', 'Quel commandement vient avec une promesse de longévité ?', '["Ne pas voler","Honorer père et mère","Ne pas mentir","Ne pas tuer"]', 1, 'Honore ton père et ta mère afin que tes jours se prolongent.', 'Deutéronome 5:16'),
('deu_017', 'DEU', 'Comment le peuple réagit-il en entendant la voix de Dieu au Sinaï ?', '["Avec joie","Avec crainte, demandant que Moïse parle à leur place","Avec indifférence","En fuyant"]', 1, 'Le peuple eut peur et demanda à Moïse de leur transmettre les paroles de Dieu.', 'Deutéronome 5:25-27'),
('deu_018', 'DEU', 'Que souhaita Dieu concernant le cœur du peuple ?', '["Qu''ils aient toujours ce cœur qui le craint et garde ses commandements","Qu''ils oublient l''Égypte","Qu''ils construisent un temple","Qu''ils conquièrent rapidement"]', 0, 'Oh! s''ils avaient toujours ce même cœur pour me craindre et garder mes commandements!', 'Deutéronome 5:29'),

-- Le Shema et les commandements fondamentaux (Chapitre 6)
('deu_019', 'DEU', 'Quel est le verset le plus célèbre du chapitre 6 ?', '["Le premier","Écoute, Israël! L''Éternel notre Dieu est le seul Éternel","Le dernier","Aucun"]', 1, 'Le Shema: Écoute, Israël! L''Éternel, notre Dieu, est le seul Éternel.', 'Deutéronome 6:4'),
('deu_020', 'DEU', 'Comment doit-on aimer l''Éternel ?', '["Parfois","De tout ton cœur, de toute ton âme et de toute ta force","Seulement le sabbat","En pensée uniquement"]', 1, 'Tu aimeras l''Éternel de tout ton cœur, de toute ton âme et de toute ta force.', 'Deutéronome 6:5'),
('deu_021', 'DEU', 'Quand devait-on enseigner les commandements aux enfants ?', '["Seulement à l''école","Quand tu seras dans ta maison, quand tu marcheras, quand tu te coucheras et quand tu te lèveras","Seulement le sabbat","Une fois par an"]', 1, 'Tu les inculqueras à tes enfants à tout moment de la journée.', 'Deutéronome 6:7'),
('deu_022', 'DEU', 'Où devait-on lier les commandements comme signe ?', '["Sur la tête seulement","Sur la main et comme fronteau entre les yeux","Sur le cœur","Sur les pieds"]', 1, 'Tu les lieras comme un signe sur tes mains et ils seront comme des fronteaux entre tes yeux.', 'Deutéronome 6:8'),
('deu_023', 'DEU', 'Où devait-on écrire les commandements ?', '["Dans un livre seulement","Sur les poteaux de la maison et sur les portes","Sur les vêtements","Sur les pierres"]', 1, 'Tu les écriras sur les poteaux de ta maison et sur tes portes.', 'Deutéronome 6:9'),
('deu_024', 'DEU', 'Contre quoi Moïse met-il en garde une fois en Canaan ?', '["La paresse","D''oublier l''Éternel après avoir joui de la prospérité","Les mariages","Les voyages"]', 1, 'Garde-toi d''oublier l''Éternel quand tu auras mangé et te seras rassasié.', 'Deutéronome 6:10-12'),

-- Les nations de Canaan (Chapitre 7)
('deu_025', 'DEU', 'Combien de nations Dieu allait-il chasser devant Israël ?', '["Trois","Cinq","Sept nations plus grandes et plus puissantes","Douze"]', 2, 'L''Éternel chassera sept nations plus nombreuses et plus puissantes qu''Israël.', 'Deutéronome 7:1'),
('deu_026', 'DEU', 'Quelles alliances Israël devait-il éviter avec ces nations ?', '["Alliances commerciales seulement","Toute alliance, y compris le mariage","Alliances militaires seulement","Aucune restriction"]', 1, 'Tu ne traiteras point d''alliance avec elles et tu ne t''allieras point avec elles par mariage.', 'Deutéronome 7:2-3'),
('deu_027', 'DEU', 'Pourquoi Dieu a-t-il choisi Israël ?', '["Parce qu''ils étaient nombreux","Par amour et pour tenir le serment fait aux pères","Pour leur puissance","Pour leur sagesse"]', 1, 'C''est parce que l''Éternel vous aime et garde le serment fait à vos pères.', 'Deutéronome 7:7-8'),
('deu_028', 'DEU', 'Que ferait Dieu aux maladies d''Égypte si Israël obéissait ?', '["Rien","Il ne les mettrait pas sur eux mais sur leurs ennemis","Il les guérirait","Il les diminuerait"]', 1, 'L''Éternel éloignera de toi toute maladie et mettra sur tes ennemis toutes les maladies d''Égypte.', 'Deutéronome 7:15'),
('deu_029', 'DEU', 'Pourquoi Dieu chasserait-il les nations peu à peu ?', '["Par pitié","Pour éviter que les bêtes sauvages ne se multiplient","Par stratégie","Par lenteur"]', 1, 'L''Éternel les chassera peu à peu pour éviter que les bêtes des champs ne se multiplient.', 'Deutéronome 7:22'),

-- Rappel de la providence divine (Chapitre 8)
('deu_030', 'DEU', 'Pourquoi Dieu conduisit-il Israël 40 ans dans le désert ?', '["Pour les punir seulement","Pour les humilier, les éprouver et connaître leur cœur","Parce qu''ils étaient perdus","Par erreur"]', 1, 'L''Éternel voulait t''humilier, t''éprouver et connaître ce qui était dans ton cœur.', 'Deutéronome 8:2'),
('deu_031', 'DEU', 'Quelle leçon la manne devait-elle enseigner ?', '["La cuisine","L''homme ne vit pas de pain seulement mais de toute parole de Dieu","L''économie","La patience"]', 1, 'L''homme ne vit pas de pain seulement, mais de tout ce qui sort de la bouche de l''Éternel.', 'Deutéronome 8:3'),
('deu_032', 'DEU', 'Qu''est-il arrivé aux vêtements et aux pieds des Israélites pendant 40 ans ?', '["Ils s''usèrent rapidement","Les vêtements ne s''usèrent pas et les pieds n''enflèrent pas","Ils furent remplacés régulièrement","Rien de spécial"]', 1, 'Ton vêtement ne s''est pas usé et ton pied n''a pas enflé pendant ces quarante années.', 'Deutéronome 8:4'),
('deu_033', 'DEU', 'Comment le pays promis est-il décrit ?', '["Désertique","Bon pays avec des eaux, du blé, de l''orge, des vignes, des figuiers, du miel et du fer","Ordinaire","Petit"]', 1, 'Un bon pays avec des ruisseaux, des sources, du froment, de l''orge, des vignes et du miel.', 'Deutéronome 8:7-9'),
('deu_034', 'DEU', 'Que doit faire Israël après avoir mangé à sa faim ?', '["Oublier le passé","Bénir l''Éternel pour le bon pays","Dormir","Travailler plus"]', 1, 'Lorsque tu mangeras et te rassasieras, tu béniras l''Éternel pour le bon pays.', 'Deutéronome 8:10'),
('deu_035', 'DEU', 'Quel danger spirituel Moïse prédit-il ?', '["La guerre","Penser que sa propre force a produit la richesse et oublier Dieu","La maladie","La famine"]', 1, 'Garde-toi de dire: c''est ma force qui m''a acquis ces richesses.', 'Deutéronome 8:17'),

-- L'obstination d'Israël (Chapitre 9)
('deu_036', 'DEU', 'Pourquoi Israël hériterait-il du pays selon Moïse ?', '["Leur justice","À cause de la méchanceté des nations chassées, non leur propre justice","Leur force","Leur nombre"]', 1, 'Ce n''est pas à cause de ta justice; c''est à cause de la méchanceté de ces nations.', 'Deutéronome 9:5'),
('deu_037', 'DEU', 'Comment Moïse décrit-il le peuple ?', '["Juste","Un peuple au cou roide (obstiné)","Humble","Sage"]', 1, 'C''est un peuple au cou roide - rebelle et obstiné.', 'Deutéronome 9:6'),
('deu_038', 'DEU', 'Combien de temps Moïse passa-t-il sur la montagne la première fois ?', '["Sept jours","Quarante jours et quarante nuits sans manger ni boire","Un mois","Dix jours"]', 1, 'Je demeurai sur la montagne quarante jours et quarante nuits sans manger ni boire.', 'Deutéronome 9:9'),
('deu_039', 'DEU', 'Que fit Moïse avec les tables de pierre quand il vit le veau d''or ?', '["Il les cacha","Il les brisa en les jetant","Il les garda","Il les donna à Aaron"]', 1, 'Je pris les deux tables et je les brisai sous vos yeux.', 'Deutéronome 9:17'),
('deu_040', 'DEU', 'Combien de temps Moïse intercéda-t-il pour le peuple après le veau d''or ?', '["Une semaine","Quarante jours et quarante nuits, prosterné devant l''Éternel","Un jour","Trois jours"]', 1, 'Je me prosternai devant l''Éternel pendant quarante jours et quarante nuits.', 'Deutéronome 9:25'),

-- Les secondes tables et ce que Dieu demande (Chapitre 10)
('deu_041', 'DEU', 'Qui tailla les secondes tables de pierre ?', '["Dieu","Moïse, sur ordre de Dieu","Aaron","Les artisans"]', 1, 'L''Éternel dit: Taille deux tables de pierre comme les premières.', 'Deutéronome 10:1'),
('deu_042', 'DEU', 'Où furent placées les tables de la loi ?', '["Dans la tente de Moïse","Dans l''arche que Moïse avait faite","Sous l''autel","Dans une grotte"]', 1, 'Moïse mit les tables dans l''arche qu''il avait faite.', 'Deutéronome 10:5'),
('deu_043', 'DEU', 'Que demande l''Éternel d''Israël en résumé ?', '["Beaucoup de sacrifices","Craindre l''Éternel, marcher dans ses voies, l''aimer et le servir de tout son cœur","De l''or","Des conquêtes"]', 1, 'Que demande de toi l''Éternel, si ce n''est de le craindre, de l''aimer et de le servir?', 'Deutéronome 10:12'),
('deu_044', 'DEU', 'Que signifie circoncire le cœur ?', '["Un rituel physique","Enlever l''obstination du cœur et être fidèle à Dieu","Une cérémonie","Un sacrifice"]', 1, 'Circoncisez votre cœur et ne raidissez plus votre cou.', 'Deutéronome 10:16'),
('deu_045', 'DEU', 'Envers qui Dieu a-t-il une attention particulière ?', '["Les riches","L''orphelin, la veuve et l''étranger","Les puissants","Les sages"]', 1, 'L''Éternel fait droit à l''orphelin et à la veuve, et il aime l''étranger.', 'Deutéronome 10:18'),

-- Bénédictions et malédictions (Chapitre 11)
('deu_046', 'DEU', 'Quelle différence entre le pays de Canaan et l''Égypte ?', '["Canaan avait le Nil","Canaan dépendait de la pluie du ciel, pas de l''irrigation","Pas de différence","Canaan était plus petit"]', 1, 'Le pays de Canaan boit l''eau de la pluie des cieux, contrairement à l''Égypte irriguée.', 'Deutéronome 11:10-11'),
('deu_047', 'DEU', 'Sur quelles montagnes les bénédictions et malédictions seraient-elles proclamées ?', '["Sinaï et Horeb","Garizim pour la bénédiction et Ébal pour la malédiction","Carmel et Hermon","Pisga et Nebo"]', 1, 'La bénédiction sur le mont Garizim, la malédiction sur le mont Ébal.', 'Deutéronome 11:29'),

-- Le lieu choisi par Dieu (Chapitre 12)
('deu_048', 'DEU', 'Que devait faire Israël aux lieux de culte païens ?', '["Les conserver","Détruire complètement leurs autels, stèles et images","Les transformer","Les ignorer"]', 1, 'Vous détruirez tous les lieux où les nations servaient leurs dieux.', 'Deutéronome 12:2-3'),
('deu_049', 'DEU', 'Où devait-on apporter les sacrifices ?', '["N''importe où","Au lieu que l''Éternel choisira parmi toutes les tribus","Dans chaque ville","Chez soi"]', 1, 'Vous le chercherez au lieu que l''Éternel choisira pour y faire résider son nom.', 'Deutéronome 12:5'),
('deu_050', 'DEU', 'Pouvait-on manger de la viande n''importe où ?', '["Jamais","Oui, mais pas le sang - la viande profane pouvait être mangée n''importe où","Seulement au sanctuaire","Seulement chez soi"]', 1, 'Tu pourras manger de la viande partout, mais tu n''en mangeras pas le sang.', 'Deutéronome 12:15-16'),

-- Faux prophètes et idolâtrie (Chapitres 13-14)
('deu_051', 'DEU', 'Que faire si un prophète fait des miracles mais conduit à l''idolâtrie ?', '["Le suivre","Ne pas l''écouter - c''est une épreuve de l''Éternel","L''honorer","Attendre"]', 1, 'C''est une épreuve pour savoir si vous aimez l''Éternel de tout votre cœur.', 'Deutéronome 13:1-3'),
('deu_052', 'DEU', 'Quelle était la sentence pour celui qui incitait à l''idolâtrie ?', '["L''exil","La mort par lapidation","Une amende","L''exclusion temporaire"]', 1, 'Celui qui incite à l''idolâtrie devait être mis à mort.', 'Deutéronome 13:6-10'),
('deu_053', 'DEU', 'Que devait-il arriver à une ville entièrement tombée dans l''idolâtrie ?', '["Un avertissement","Être entièrement détruite et brûlée, ne jamais être reconstruite","Une punition légère","Un changement de chef"]', 1, 'La ville devait être entièrement détruite et jamais reconstruite.', 'Deutéronome 13:12-16'),
('deu_054', 'DEU', 'Comment le peuple devait-il montrer son deuil ?', '["Par des tatouages","Ne pas se faire d''incisions ou de tonsure entre les yeux pour un mort","Par des sacrifices humains","Par des jeûnes extrêmes"]', 1, 'Ces pratiques païennes de deuil étaient interdites.', 'Deutéronome 14:1'),

-- Dîmes et années de libération (Chapitres 14-15)
('deu_055', 'DEU', 'Quelle portion devait être mise à part comme dîme ?', '["Un cinquième","Un dixième de tout le produit","Un quart","La moitié"]', 1, 'Tu prélèveras la dîme de tout ce que produira ta semence.', 'Deutéronome 14:22'),
('deu_056', 'DEU', 'Que faire de la dîme tous les trois ans ?', '["La brûler","La mettre dans les villes pour le Lévite, l''étranger, l''orphelin et la veuve","La vendre","La garder"]', 1, 'Chaque troisième année, la dîme servait aux Lévites et aux nécessiteux locaux.', 'Deutéronome 14:28-29'),
('deu_057', 'DEU', 'Tous les combien d''années les dettes devaient-elles être remises ?', '["Chaque année","Tous les sept ans - l''année de relâche","Tous les dix ans","Tous les cinquante ans"]', 1, 'À la fin de chaque septième année, tu feras relâche des dettes.', 'Deutéronome 15:1'),
('deu_058', 'DEU', 'Comment traiter un esclave hébreu à sa libération ?', '["Le renvoyer les mains vides","Ne pas le renvoyer à vide mais lui donner généreusement","Lui donner peu","Exiger un paiement"]', 1, 'Tu ne le renverras point à vide; tu lui feras des présents généreux.', 'Deutéronome 15:13-14'),
('deu_059', 'DEU', 'Que faire si un esclave voulait rester avec son maître ?', '["Le forcer à partir","Lui percer l''oreille avec un poinçon contre la porte","Refuser","Le punir"]', 1, 'Tu lui perceras l''oreille et il sera ton esclave pour toujours.', 'Deutéronome 15:17'),

-- Les fêtes (Chapitre 16)
('deu_060', 'DEU', 'Combien de fois par an tous les mâles devaient-ils se présenter devant l''Éternel ?', '["Une fois","Deux fois","Trois fois: Pâque, Semaines et Tabernacles","Quatre fois"]', 2, 'Trois fois par an, tous les mâles se présenteront devant l''Éternel.', 'Deutéronome 16:16'),
('deu_061', 'DEU', 'Que ne devait-on jamais planter près de l''autel de l''Éternel ?', '["Des fleurs","Un arbre ou un poteau d''Astarté","Des légumes","Rien de spécial"]', 1, 'Tu ne planteras aucun arbre pour servir d''idole près de l''autel de l''Éternel.', 'Deutéronome 16:21'),
('deu_062', 'DEU', 'Comment les juges devaient-ils exercer la justice ?', '["En favorisant les riches","Avec droiture, sans partialité ni corruption","En suivant l''opinion populaire","Rapidement"]', 1, 'Tu ne feras point fléchir le droit et tu n''accepteras pas de présent.', 'Deutéronome 16:19'),

-- Le roi futur (Chapitre 17)
('deu_063', 'DEU', 'Quelle restriction s''appliquait au futur roi d''Israël ?', '["Pas de restriction","Il devait être un Israélite choisi par Dieu, pas un étranger","Il pouvait être n''importe qui","Il devait être riche"]', 1, 'Tu ne pourras établir un étranger; ce sera quelqu''un choisi par l''Éternel.', 'Deutéronome 17:15'),
('deu_064', 'DEU', 'Que le roi ne devait-il pas multiplier ?', '["Les serviteurs","Chevaux, femmes et or/argent","Les maisons","Les troupeaux"]', 1, 'Le roi ne devait pas multiplier chevaux, femmes, or ni argent.', 'Deutéronome 17:16-17'),
('deu_065', 'DEU', 'Que devait faire le roi tous les jours ?', '["Administrer la justice seulement","Lire une copie de la loi tous les jours de sa vie","Commander l''armée","Percevoir les impôts"]', 1, 'Il écrira une copie de cette loi et la lira tous les jours de sa vie.', 'Deutéronome 17:18-19'),

-- Les Lévites et les prophètes (Chapitre 18)
('deu_066', 'DEU', 'Quel héritage les sacrificateurs lévitiques avaient-ils ?', '["Comme les autres tribus","L''Éternel lui-même était leur héritage","De l''or","De grandes terres"]', 1, 'L''Éternel est leur héritage, comme il le leur a dit.', 'Deutéronome 18:2'),
('deu_067', 'DEU', 'Quelles pratiques occultes sont interdites ?', '["Seulement les sacrifices humains","Divination, magie, enchantement, spiritisme et consultation des morts","Seulement la sorcellerie","Aucune"]', 1, 'Toutes ces pratiques de divination, magie et spiritisme sont en abomination.', 'Deutéronome 18:10-12'),
('deu_068', 'DEU', 'Quel prophète Dieu promit-il de susciter ?', '["Un prophète comme Élie","Un prophète semblable à Moïse d''entre les frères","Un prophète étranger","Un prophète ange"]', 1, 'L''Éternel suscitera du milieu de toi un prophète comme moi.', 'Deutéronome 18:15'),
('deu_069', 'DEU', 'Comment reconnaître un faux prophète ?', '["Par son apparence","Si ce qu''il prédit n''arrive pas","Par sa popularité","Par ses miracles"]', 1, 'Si ce que le prophète dit au nom de l''Éternel n''arrive pas, c''est un faux prophète.', 'Deutéronome 18:22'),

-- Villes de refuge et lois de guerre (Chapitres 19-20)
('deu_070', 'DEU', 'Combien de témoins fallait-il pour établir une accusation ?', '["Un seul suffit","Deux ou trois témoins","Quatre témoins","Le nombre n''importait pas"]', 1, 'Un seul témoin ne suffira pas; il faut deux ou trois témoins.', 'Deutéronome 19:15'),
('deu_071', 'DEU', 'Quelle était la loi du talion ?', '["Pardonner toujours","Vie pour vie, œil pour œil, dent pour dent","Punir selon la richesse","Double peine"]', 1, 'Vie pour vie, œil pour œil, dent pour dent, main pour main, pied pour pied.', 'Deutéronome 19:21'),
('deu_072', 'DEU', 'Qui était exempté du service militaire ?', '["Personne","Celui qui avait bâti une maison neuve, planté une vigne, fiancé une femme, ou qui avait peur","Seulement les malades","Les vieux"]', 1, 'Ces exemptions permettaient à certains de ne pas aller à la guerre.', 'Deutéronome 20:5-8'),
('deu_073', 'DEU', 'Que devait-on faire avant d''attaquer une ville éloignée ?', '["L''attaquer immédiatement","Lui proposer la paix","La brûler","L''encercler seulement"]', 1, 'On devait d''abord lui offrir la paix avant toute attaque.', 'Deutéronome 20:10'),
('deu_074', 'DEU', 'Quels arbres ne devait-on pas couper pendant un siège ?', '["Aucun arbre","Les arbres fruitiers","Tous les arbres","Les grands arbres"]', 1, 'Tu ne détruiras pas les arbres fruitiers car ils servent à nourrir.', 'Deutéronome 20:19'),

-- Lois diverses (Chapitres 21-25)
('deu_075', 'DEU', 'Que faire pour un meurtre non élucidé ?', '["Rien","Les anciens de la ville la plus proche sacrifiaient une génisse et se lavaient les mains","Une enquête infinie","Punir tout le monde"]', 1, 'Un rituel de purification avec une génisse était requis pour expier le sang innocent.', 'Deutéronome 21:1-9'),
('deu_076', 'DEU', 'Comment devait-on traiter une captive de guerre qu''on voulait épouser ?', '["L''épouser immédiatement","Elle devait se raser, se couper les ongles et pleurer un mois avant le mariage","La vendre","La renvoyer"]', 1, 'Elle devait avoir un mois de deuil avant de pouvoir être épousée.', 'Deutéronome 21:12-13'),
('deu_077', 'DEU', 'Que faire d''un fils rebelle et incorrigible ?', '["L''exiler","Les parents l''amenaient aux anciens et il pouvait être lapidé","Le pardonner toujours","Le vendre"]', 1, 'Un fils rebelle incorrigible était amené devant les anciens pour jugement.', 'Deutéronome 21:18-21'),
('deu_078', 'DEU', 'Que faire d''un homme pendu à un bois ?', '["Le laisser plusieurs jours","L''enterrer le jour même car un pendu est maudit de Dieu","Le brûler","Le jeter"]', 1, 'Son corps ne devait pas rester la nuit; il fallait l''enterrer le jour même.', 'Deutéronome 21:22-23'),
('deu_079', 'DEU', 'Que devait-on faire si on trouvait l''animal égaré de son ennemi ?', '["L''ignorer","Le ramener à son propriétaire","Le garder","Le tuer"]', 1, 'Tu le ramèneras à ton frère, même s''il est ton ennemi.', 'Deutéronome 22:1'),
('deu_080', 'DEU', 'Qu''était interdit concernant les vêtements ?', '["Porter du lin","Une femme ne portera pas de vêtements d''homme et vice versa","Porter du blanc","Porter des couleurs"]', 1, 'Le travestissement était interdit comme abomination.', 'Deutéronome 22:5'),
('deu_081', 'DEU', 'Si on trouvait un nid avec la mère et les petits, que faire ?', '["Prendre tout","Laisser partir la mère et prendre les petits seulement","Tout laisser","Détruire le nid"]', 1, 'Tu laisseras aller la mère et tu ne prendras que les petits.', 'Deutéronome 22:6-7'),
('deu_082', 'DEU', 'Qu''était interdit de semer ensemble ?', '["Blé et orge","Deux espèces de semences dans ta vigne","Légumes différents","Rien"]', 1, 'Tu ne sèmeras pas ta vigne de deux espèces de semences.', 'Deutéronome 22:9'),
('deu_083', 'DEU', 'Qui ne pouvait pas entrer dans l''assemblée de l''Éternel ?', '["Les pauvres","L''Ammonite et le Moabite, même à la dixième génération","Les bergers","Les artisans"]', 1, 'L''Ammonite et le Moabite n''entreront pas dans l''assemblée jusqu''à la dixième génération.', 'Deutéronome 23:3'),
('deu_084', 'DEU', 'Pourquoi le camp devait-il être propre ?', '["Pour l''hygiène","Parce que l''Éternel marche au milieu du camp","Pour les visiteurs","Par habitude"]', 1, 'L''Éternel marche au milieu de ton camp; ton camp sera saint.', 'Deutéronome 23:14'),
('deu_085', 'DEU', 'Quelle limite de coups pour une punition corporelle ?', '["Dix","Quarante coups maximum","Cent","Illimité"]', 1, 'On ne donnera pas plus de quarante coups pour ne pas avilir son frère.', 'Deutéronome 25:3'),
('deu_086', 'DEU', 'Quelle était la loi du lévirat ?', '["Une taxe","Le frère du défunt épousait sa veuve pour donner un héritier à son frère","Un héritage","Une fête"]', 1, 'Si un homme meurt sans enfant, son frère prendra sa veuve comme femme.', 'Deutéronome 25:5'),
('deu_087', 'DEU', 'Que devait-on ne pas museler ?', '["Un chien","Le bœuf quand il foule le grain","Un âne","Un chameau"]', 1, 'Tu ne muselleras pas le bœuf quand il foule le grain - il peut manger pendant qu''il travaille.', 'Deutéronome 25:4'),

-- Prémices et confession (Chapitre 26)
('deu_088', 'DEU', 'Quelle confession accompagnait l''offrande des prémices ?', '["Une prière silencieuse","Mon père était un Araméen errant - rappel de l''histoire d''Israël","Un chant","Un sacrifice"]', 1, 'La confession commençait par: Mon père était un Araméen nomade.', 'Deutéronome 26:5'),
('deu_089', 'DEU', 'Quelle déclaration accompagnait la dîme de la troisième année ?', '["Rien","Une déclaration d''intégrité et d''obéissance aux commandements","Un hymne","Une danse"]', 1, 'On déclarait avoir obéi aux commandements et donné au Lévite et au nécessiteux.', 'Deutéronome 26:13-14'),

-- Les malédictions de l'Ébal (Chapitre 27)
('deu_090', 'DEU', 'Sur quoi les commandements devaient-ils être écrits en entrant en Canaan ?', '["Du papyrus","De grandes pierres enduites de chaux","Des tablettes d''argile","Du bois"]', 1, 'Tu dresseras de grandes pierres et tu les enduiras de chaux pour y écrire toutes les paroles.', 'Deutéronome 27:2-3'),
('deu_091', 'DEU', 'Combien de malédictions sont prononcées dans ce chapitre ?', '["Cinq","Douze","Vingt","Sept"]', 1, 'Douze malédictions sont prononcées, chacune avec la réponse du peuple: Amen!', 'Deutéronome 27:15-26'),
('deu_092', 'DEU', 'Quelle était la réponse du peuple à chaque malédiction ?', '["Nous obéirons","Amen!","D''accord","Oui"]', 1, 'Tout le peuple répondait: Amen! à chaque malédiction.', 'Deutéronome 27:15-26'),

-- Bénédictions et malédictions (Chapitre 28)
('deu_093', 'DEU', 'Quelle condition apportait les bénédictions ?', '["La richesse","Obéir à la voix de l''Éternel et observer ses commandements","Le nombre","La force"]', 1, 'Si tu obéis à la voix de l''Éternel, toutes ces bénédictions viendront sur toi.', 'Deutéronome 28:1-2'),
('deu_094', 'DEU', 'Comment Israël serait-il béni ?', '["Partiellement","Béni dans la ville, les champs, les enfants, les récoltes, les troupeaux - en tout","Seulement à la guerre","Seulement dans la terre"]', 1, 'Tu seras béni dans la ville et dans les champs, dans toutes tes entreprises.', 'Deutéronome 28:3-6'),
('deu_095', 'DEU', 'Quelle position Israël occuperait-il parmi les nations s''il obéissait ?', '["Égale aux autres","La tête et non la queue, au-dessus et non au-dessous","En dessous","Isolé"]', 1, 'L''Éternel te mettra à la tête et non à la queue, toujours en haut.', 'Deutéronome 28:13'),
('deu_096', 'DEU', 'Quelles malédictions viendraient en cas de désobéissance ?', '["Quelques difficultés","Maladies, sécheresse, défaites, folie, oppression, exil et cannibalisme en cas de siège","Rien de grave","Seulement l''exil"]', 1, 'Les malédictions incluaient tous les maux imaginables.', 'Deutéronome 28:15-68'),
('deu_097', 'DEU', 'Où le peuple serait-il dispersé selon les malédictions ?', '["Nulle part","Parmi tous les peuples, d''une extrémité de la terre à l''autre","En Égypte seulement","En Babylone seulement"]', 1, 'L''Éternel te dispersera parmi tous les peuples d''un bout de la terre à l''autre.', 'Deutéronome 28:64'),

-- Le renouvellement de l'alliance (Chapitres 29-30)
('deu_098', 'DEU', 'Cette alliance était-elle la même que celle du Sinaï ?', '["Oui","Non - c''est une alliance distincte conclue dans les plaines de Moab","Partiellement","On ne sait pas"]', 1, 'Ce sont les paroles de l''alliance que l''Éternel ordonna en Moab, outre l''alliance de l''Horeb.', 'Deutéronome 29:1'),
('deu_099', 'DEU', 'Quelle promesse concernait le retour de l''exil ?', '["Pas de retour possible","Dieu ramènerait les exilés et les bénirait s''ils se repentaient","Seulement quelques-uns","Après 1000 ans"]', 1, 'Même si tes exilés sont à l''extrémité du ciel, l''Éternel les rassemblera.', 'Deutéronome 30:4'),
('deu_100', 'DEU', 'Que ferait Dieu au cœur du peuple après le retour ?', '["Rien","Circoncire leur cœur pour qu''ils l''aiment de tout leur cœur","Les punir encore","Les tester"]', 1, 'L''Éternel circoncira ton cœur pour que tu aimes l''Éternel de tout ton cœur.', 'Deutéronome 30:6'),
('deu_101', 'DEU', 'Où se trouve le commandement selon Moïse ?', '["Au ciel","Pas au ciel ni au-delà de la mer, mais tout près, dans ta bouche et dans ton cœur","Très loin","Caché"]', 1, 'La parole est tout près de toi, dans ta bouche et dans ton cœur.', 'Deutéronome 30:14'),
('deu_102', 'DEU', 'Quel choix Moïse place-t-il devant le peuple ?', '["Richesse ou pauvreté","La vie et le bonheur, la mort et le malheur - choisis la vie!","La paix ou la guerre","Le repos ou le travail"]', 1, 'J''ai mis devant toi la vie et la mort, la bénédiction et la malédiction. Choisis la vie!', 'Deutéronome 30:19'),

-- Les derniers jours de Moïse (Chapitres 31-34)
('deu_103', 'DEU', 'Quel âge avait Moïse à la fin ?', '["100 ans","120 ans","130 ans","150 ans"]', 1, 'Moïse avait 120 ans quand il mourut.', 'Deutéronome 34:7'),
('deu_104', 'DEU', 'Qui fut officiellement établi comme successeur de Moïse ?', '["Aaron","Josué fils de Nun, rempli de l''esprit de sagesse","Caleb","Éléazar"]', 1, 'Josué, fils de Nun, fut rempli de l''esprit de sagesse car Moïse lui avait imposé les mains.', 'Deutéronome 34:9'),
('deu_105', 'DEU', 'Que prédit Moïse concernant l''avenir d''Israël ?', '["Fidélité parfaite","Rébellion et idolâtrie après sa mort","Prospérité constante","Paix éternelle"]', 1, 'Moïse savait que le peuple se corromprait et se détournerait après sa mort.', 'Deutéronome 31:29'),
('deu_106', 'DEU', 'Qu''est-ce que le cantique de Moïse (chapitre 32) ?', '["Une prière","Un poème/chant témoignant contre Israël et célébrant Dieu","Une loi","Une bénédiction"]', 1, 'Le cantique est un témoin poétique rappelant la fidélité de Dieu et l''infidélité du peuple.', 'Deutéronome 31:21; 32:1-43'),
('deu_107', 'DEU', 'Comment Dieu est-il décrit dans le cantique ?', '["Sévère seulement","Le Rocher dont les œuvres sont parfaites, juste et droit","Lointain","Indifférent"]', 1, 'Il est le Rocher; ses œuvres sont parfaites, car toutes ses voies sont justes.', 'Deutéronome 32:4'),
('deu_108', 'DEU', 'Quel chapitre contient les bénédictions tribales de Moïse ?', '["Chapitre 30","Chapitre 33","Chapitre 31","Chapitre 34"]', 1, 'Le chapitre 33 contient les bénédictions prophétiques sur les tribus.', 'Deutéronome 33'),
('deu_109', 'DEU', 'D''où Moïse contempla-t-il le pays promis avant de mourir ?', '["Le mont Sinaï","Le sommet du mont Nebo, sur le Pisga","Le bord du Jourdain","Une colline quelconque"]', 1, 'Moïse monta sur le mont Nebo, au sommet du Pisga, en face de Jéricho.', 'Deutéronome 34:1'),
('deu_110', 'DEU', 'Qui enterra Moïse ?', '["Josué","L''Éternel lui-même dans la vallée de Moab","Aaron","Les anciens"]', 1, 'L''Éternel l''enterra dans la vallée du pays de Moab; personne ne connaît son sépulcre.', 'Deutéronome 34:6'),
('deu_111', 'DEU', 'Comment était la vue et la vigueur de Moïse à 120 ans ?', '["Affaiblies","Sa vue n''avait pas baissé et sa vigueur n''était pas épuisée","Normales pour son âge","Très diminuées"]', 1, 'Sa vue n''était pas affaiblie et sa vigueur n''était pas épuisée à 120 ans.', 'Deutéronome 34:7'),
('deu_112', 'DEU', 'Combien de temps Israël pleura-t-il Moïse ?', '["Une semaine","Trente jours","Quarante jours","Sept jours"]', 1, 'Les enfants d''Israël pleurèrent Moïse pendant trente jours.', 'Deutéronome 34:8'),
('deu_113', 'DEU', 'Comment Moïse est-il qualifié par rapport aux autres prophètes ?', '["Semblable aux autres","Il n''a plus paru de prophète semblable à Moïse que l''Éternel connaissait face à face","Inférieur","Égal"]', 1, 'Il n''a plus paru en Israël de prophète semblable à Moïse, que l''Éternel connaissait face à face.', 'Deutéronome 34:10');

