-- L'OPPRESSION EN ÉGYPTE (Chapitres 1-2)
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('exo_001', 'EXO', 'Pourquoi le nouveau pharaon craignait-il les Israélites ?', 'Ils étaient riches', 'Ils étaient trop nombreux et puissants', 'Ils adoraient un autre dieu', 'Ils refusaient de travailler', 1, 'Les enfants d''Israël étaient féconds et très nombreux.', 'Exode 1:7-9'),
('exo_002', 'EXO', 'Que fit Pharaon pour opprimer les Israélites ?', 'Il les chassa', 'Il leur imposa des travaux forcés', 'Il les emprisonna', 'Il les vendit', 1, 'On établit sur eux des chefs de corvées.', 'Exode 1:11'),
('exo_003', 'EXO', 'Quelles villes les Israélites construisirent-ils ?', 'Thèbes et Memphis', 'Pithom et Ramsès', 'Le Caire et Alexandrie', 'Goshen et Thèbes', 1, 'Ils bâtirent pour Pharaon des villes à provisions, Pithom et Ramsès.', 'Exode 1:11'),
('exo_004', 'EXO', 'Quel ordre Pharaon donna-t-il aux sages-femmes ?', 'De bien traiter les naissances', 'De tuer les garçons hébreux à la naissance', 'De compter les naissances', 'De les faire travailler', 1, 'Si c''est un garçon, faites-le mourir.', 'Exode 1:16'),
('exo_005', 'EXO', 'Comment s''appelaient les deux sages-femmes ?', 'Marie et Rachel', 'Schiphra et Pua', 'Léa et Rebecca', 'Sara et Agar', 1, 'Il y avait deux sages-femmes, Schiphra et Pua.', 'Exode 1:15'),
('exo_006', 'EXO', 'Pourquoi les sages-femmes n''obéirent-elles pas ?', 'Elles avaient peur des Hébreux', 'Elles craignaient Dieu', 'Elles étaient payées par les Hébreux', 'Elles n''avaient pas entendu l''ordre', 1, 'Les sages-femmes craignirent Dieu et ne firent pas ce que leur avait dit le roi.', 'Exode 1:17'),
('exo_007', 'EXO', 'Quel nouvel ordre Pharaon donna-t-il à tout son peuple ?', 'De chasser les Hébreux', 'De jeter les garçons dans le fleuve', 'De les faire travailler plus dur', 'De les affamer', 1, 'Jetez dans le fleuve tout garçon qui naîtra.', 'Exode 1:22'),
('exo_008', 'EXO', 'De quelle tribu étaient les parents de Moïse ?', 'Juda', 'Benjamin', 'Lévi', 'Ruben', 2, 'Un homme de la maison de Lévi prit pour femme une fille de Lévi.', 'Exode 2:1'),
('exo_009', 'EXO', 'Combien de temps la mère de Moïse le cacha-t-elle ?', 'Un mois', 'Trois mois', 'Six mois', 'Un an', 1, 'Elle le cacha pendant trois mois.', 'Exode 2:2'),
('exo_010', 'EXO', 'Dans quoi la mère de Moïse le mit-elle sur le fleuve ?', 'Un bateau', 'Une corbeille de jonc', 'Un panier de bois', 'Un coffre', 1, 'Elle le mit dans une corbeille de jonc enduite de bitume et de poix.', 'Exode 2:3'),
('exo_011', 'EXO', 'Qui trouva Moïse dans le fleuve ?', 'Pharaon', 'La fille de Pharaon', 'Un pêcheur', 'Un soldat', 1, 'La fille de Pharaon descendit au fleuve et vit la corbeille.', 'Exode 2:5'),
('exo_012', 'EXO', 'Comment s''appelait la sœur de Moïse ?', 'Marie', 'Myriam', 'Déborah', 'Rachel', 1, 'Myriam était la sœur de Moïse.', 'Exode 15:20'),
('exo_013', 'EXO', 'Qui devint la nourrice de Moïse ?', 'Une Égyptienne', 'Sa propre mère', 'La fille de Pharaon', 'Une servante', 1, 'La mère de l''enfant vint et l''allaita.', 'Exode 2:8-9'),
('exo_014', 'EXO', 'Que signifie le nom Moïse ?', 'Fils du Nil', 'Sauvé des eaux', 'Prince d''Égypte', 'Enfant béni', 1, 'Elle l''appela Moïse car elle l''avait retiré des eaux.', 'Exode 2:10'),
('exo_015', 'EXO', 'Pourquoi Moïse s''enfuit-il d''Égypte ?', 'Il voulait voyager', 'Il avait tué un Égyptien', 'Pharaon le renvoya', 'Il cherchait sa famille', 1, 'Moïse tua l''Égyptien et le cacha dans le sable.', 'Exode 2:12'),
('exo_016', 'EXO', 'Où Moïse s''enfuit-il ?', 'En Canaan', 'Au pays de Madian', 'À Babylone', 'En Assyrie', 1, 'Moïse s''enfuit et s''établit au pays de Madian.', 'Exode 2:15'),
('exo_017', 'EXO', 'Qui Moïse épousa-t-il à Madian ?', 'Marie', 'Séphora', 'Rebecca', 'Léa', 1, 'Moïse prit pour femme Séphora, fille de Réuel.', 'Exode 2:21'),
('exo_018', 'EXO', 'Comment s''appelait le beau-père de Moïse ?', 'Jéthro/Réuel', 'Aaron', 'Caleb', 'Josué', 0, 'Réuel, aussi appelé Jéthro, était prêtre de Madian.', 'Exode 2:18; 3:1'),
('exo_019', 'EXO', 'Quel était le métier de Moïse à Madian ?', 'Forgeron', 'Berger', 'Laboureur', 'Marchand', 1, 'Moïse faisait paître le troupeau de Jéthro.', 'Exode 3:1'),
('exo_020', 'EXO', 'Comment s''appelait le fils de Moïse ?', 'Aaron', 'Guershom', 'Josué', 'Caleb', 1, 'Elle enfanta un fils qu''il appela Guershom.', 'Exode 2:22'),

-- L'APPEL DE MOÏSE (Chapitres 3-4)
('exo_021', 'EXO', 'Comment Dieu apparut-il à Moïse ?', 'Dans un songe', 'Dans un buisson ardent', 'Par un ange', 'Dans une nuée', 1, 'L''ange de l''Éternel lui apparut dans une flamme de feu, au milieu d''un buisson.', 'Exode 3:2'),
('exo_022', 'EXO', 'Sur quelle montagne Moïse vit-il le buisson ardent ?', 'Mont Sinaï', 'Mont Horeb', 'Mont Nébo', 'Mont Carmel', 1, 'Il mena le troupeau au-delà du désert, à Horeb, la montagne de Dieu.', 'Exode 3:1'),
('exo_023', 'EXO', 'Pourquoi le buisson ne se consumait-il pas ?', 'C''était un mirage', 'La présence de Dieu', 'Il pleuvait', 'Le feu était froid', 1, 'Le buisson était en feu mais ne se consumait point.', 'Exode 3:2'),
('exo_024', 'EXO', 'Que dit Dieu à Moïse de faire avec ses sandales ?', 'Les garder', 'Les ôter car le lieu était saint', 'Les laver', 'Les brûler', 1, 'Ôte tes souliers car le lieu où tu te tiens est une terre sainte.', 'Exode 3:5'),
('exo_025', 'EXO', 'Quel nom Dieu révéla-t-il à Moïse ?', 'El Shaddai', 'JE SUIS CELUI QUI SUIS (YHWH)', 'Adonaï', 'Élohim', 1, 'Dieu dit à Moïse : Je suis celui qui suis.', 'Exode 3:14'),
('exo_026', 'EXO', 'Quelle mission Dieu donna-t-il à Moïse ?', 'Construire un temple', 'Faire sortir Israël d''Égypte', 'Devenir roi', 'Écrire la loi', 1, 'Je t''enverrai vers Pharaon et tu feras sortir d''Égypte mon peuple.', 'Exode 3:10'),
('exo_027', 'EXO', 'Quelle excuse Moïse donna-t-il d''abord ?', 'Je suis malade', 'Qui suis-je pour aller vers Pharaon ?', 'Je n''ai pas le temps', 'Je suis trop vieux', 1, 'Qui suis-je pour aller vers Pharaon ?', 'Exode 3:11'),
('exo_028', 'EXO', 'Quel problème Moïse invoqua-t-il concernant sa parole ?', 'Il était sourd', 'Il avait la bouche et la langue embarrassées', 'Il était muet', 'Il parlait une autre langue', 1, 'Je n''ai pas la parole facile.', 'Exode 4:10'),
('exo_029', 'EXO', 'Qui Dieu donna-t-il à Moïse pour l''aider à parler ?', 'Josué', 'Aaron son frère', 'Caleb', 'Myriam', 1, 'Aaron le Lévite est ton frère, je sais qu''il parle facilement.', 'Exode 4:14'),
('exo_030', 'EXO', 'Quel premier signe Dieu donna-t-il à Moïse ?', 'L''eau changée en sang', 'Le bâton changé en serpent', 'Le feu du ciel', 'La terre qui tremble', 1, 'Jette ton bâton par terre. Il le jeta et le bâton devint un serpent.', 'Exode 4:3'),
('exo_031', 'EXO', 'Quel deuxième signe Dieu donna-t-il ?', 'La main devenue lépreuse puis guérie', 'L''eau changée en vin', 'Le feu dans sa main', 'La voix du tonnerre', 0, 'Mets ta main dans ton sein. Il la retira couverte de lèpre.', 'Exode 4:6'),
('exo_032', 'EXO', 'Quel troisième signe Dieu promit-il ?', 'Changer l''eau en sang', 'Ouvrir la mer', 'Faire pleuvoir du feu', 'Arrêter le soleil', 0, 'Tu prendras de l''eau du fleuve et elle deviendra du sang sur la terre.', 'Exode 4:9'),

-- LES DIX PLAIES (Chapitres 7-12)
('exo_033', 'EXO', 'Que demanda Moïse à Pharaon ?', 'De l''or', 'De laisser partir le peuple pour adorer Dieu', 'Des terres', 'La liberté des prisonniers', 1, 'Laisse aller mon peuple pour qu''il célèbre une fête en mon honneur.', 'Exode 5:1'),
('exo_034', 'EXO', 'Comment Pharaon réagit-il à la demande de Moïse ?', 'Il accepta', 'Il refusa et augmenta le travail', 'Il réfléchit', 'Il consulta ses conseillers', 1, 'Pharaon dit : Qui est l''Éternel pour que j''obéisse ?', 'Exode 5:2'),
('exo_035', 'EXO', 'Quelle fut la première plaie ?', 'Les grenouilles', 'L''eau changée en sang', 'Les ténèbres', 'Les mouches', 1, 'Les eaux du fleuve furent changées en sang.', 'Exode 7:20'),
('exo_036', 'EXO', 'Combien de jours dura la première plaie ?', '3 jours', '7 jours', '10 jours', '40 jours', 1, 'Sept jours s''écoulèrent après que l''Éternel eut frappé le fleuve.', 'Exode 7:25'),
('exo_037', 'EXO', 'Quelle fut la deuxième plaie ?', 'Les grenouilles', 'Les poux', 'Les mouches', 'La grêle', 0, 'Les grenouilles montèrent et couvrirent le pays d''Égypte.', 'Exode 8:6'),
('exo_038', 'EXO', 'Quelle fut la troisième plaie ?', 'Les mouches', 'Les poux (moustiques)', 'La peste', 'Les ulcères', 1, 'La poussière se changea en poux sur les hommes et sur les animaux.', 'Exode 8:17'),
('exo_039', 'EXO', 'Que dirent les magiciens concernant la troisième plaie ?', 'Nous pouvons faire pareil', 'C''est le doigt de Dieu', 'C''est une coïncidence', 'Nous allons réessayer', 1, 'Les magiciens dirent à Pharaon : C''est le doigt de Dieu.', 'Exode 8:19'),
('exo_040', 'EXO', 'Quelle fut la quatrième plaie ?', 'Les mouches venimeuses', 'La grêle', 'Les sauterelles', 'Les ulcères', 0, 'Des mouches venimeuses en grande quantité.', 'Exode 8:24'),
('exo_041', 'EXO', 'Quelle région fut épargnée à partir de la quatrième plaie ?', 'Memphis', 'Le pays de Goshen', 'Thèbes', 'Le Nil', 1, 'Je distinguerai le pays de Goshen où habite mon peuple.', 'Exode 8:22'),
('exo_042', 'EXO', 'Quelle fut la cinquième plaie ?', 'La peste sur les troupeaux', 'Les ulcères', 'Les sauterelles', 'Les ténèbres', 0, 'Une peste très forte sur les troupeaux.', 'Exode 9:3'),
('exo_043', 'EXO', 'Quelle fut la sixième plaie ?', 'La grêle', 'Les ulcères sur les hommes et les bêtes', 'Les ténèbres', 'La mort des premiers-nés', 1, 'Des ulcères formant des pustules sur les hommes et sur les animaux.', 'Exode 9:10'),
('exo_044', 'EXO', 'Quelle fut la septième plaie ?', 'Les sauterelles', 'La grêle', 'Les ténèbres', 'Les mouches', 1, 'L''Éternel fit pleuvoir de la grêle sur le pays d''Égypte.', 'Exode 9:23'),
('exo_045', 'EXO', 'Quelle fut la huitième plaie ?', 'Les ténèbres', 'Les sauterelles', 'La peste', 'Le feu', 1, 'Les sauterelles couvrirent toute la surface du pays.', 'Exode 10:14'),
('exo_046', 'EXO', 'Quelle fut la neuvième plaie ?', 'Les ténèbres pendant trois jours', 'La mort des premiers-nés', 'Le feu du ciel', 'La famine', 0, 'Il y eut d''épaisses ténèbres pendant trois jours.', 'Exode 10:22'),
('exo_047', 'EXO', 'Quelle fut la dixième et dernière plaie ?', 'Le tremblement de terre', 'La mort des premiers-nés', 'L''inondation', 'Le feu', 1, 'Je frapperai de mort tous les premiers-nés du pays d''Égypte.', 'Exode 11:5'),
('exo_048', 'EXO', 'Que devaient faire les Israélites pour être protégés ?', 'Fuir', 'Mettre le sang d''un agneau sur les montants des portes', 'Prier', 'Se cacher', 1, 'Ils prendront du sang et le mettront sur les deux poteaux et le linteau.', 'Exode 12:7'),
('exo_049', 'EXO', 'Comment s''appelle la fête commémorant cette délivrance ?', 'La Pentecôte', 'La Pâque', 'Les Tabernacles', 'Pourim', 1, 'C''est la Pâque de l''Éternel.', 'Exode 12:11'),
('exo_050', 'EXO', 'Comment devait-on manger l''agneau pascal ?', 'Cru', 'Rôti au feu, avec des pains sans levain et des herbes amères', 'Bouilli', 'Séché', 1, 'Vous le mangerez rôti au feu avec des pains sans levain et des herbes amères.', 'Exode 12:8'),
('exo_051', 'EXO', 'Comment les Israélites devaient-ils manger le repas pascal ?', 'Assis et détendus', 'Debout, les reins ceints, les sandales aux pieds, le bâton à la main', 'Couchés', 'Agenouillés', 1, 'Vous le mangerez à la hâte.', 'Exode 12:11'),
('exo_052', 'EXO', 'À quelle heure Dieu frappa-t-il les premiers-nés ?', 'À l''aube', 'Au milieu de la nuit', 'Au coucher du soleil', 'À midi', 1, 'Au milieu de la nuit, l''Éternel frappa tous les premiers-nés.', 'Exode 12:29'),
('exo_053', 'EXO', 'Combien de temps les Israélites étaient-ils restés en Égypte ?', '200 ans', '300 ans', '430 ans', '500 ans', 2, 'Le séjour des enfants d''Israël en Égypte fut de 430 ans.', 'Exode 12:40'),
('exo_054', 'EXO', 'Combien d''hommes à pied sortirent d''Égypte ?', '100 000', '400 000', '600 000', '1 million', 2, 'Environ 600 000 hommes de pied, sans les enfants.', 'Exode 12:37'),

-- LA TRAVERSÉE DE LA MER (Chapitres 13-15)
('exo_055', 'EXO', 'Comment Dieu guidait-il les Israélites le jour ?', 'Par une étoile', 'Par une colonne de nuée', 'Par un ange visible', 'Par le vent', 1, 'L''Éternel allait devant eux, le jour dans une colonne de nuée.', 'Exode 13:21'),
('exo_056', 'EXO', 'Comment Dieu les guidait-il la nuit ?', 'Par la lune', 'Par une colonne de feu', 'Par les étoiles', 'Par une voix', 1, 'La nuit dans une colonne de feu pour les éclairer.', 'Exode 13:21'),
('exo_057', 'EXO', 'Pourquoi Pharaon poursuivit-il les Israélites ?', 'Il voulait l''or', 'Il regretta de les avoir laissés partir', 'Il voulait se venger', 'Un conseiller l''y poussa', 1, 'Le cœur de Pharaon changea à l''égard du peuple.', 'Exode 14:5'),
('exo_058', 'EXO', 'Avec combien de chars Pharaon poursuivit-il Israël ?', '100', '300', '600', '1000', 2, 'Il prit 600 chars d''élite et tous les chars de l''Égypte.', 'Exode 14:7'),
('exo_059', 'EXO', 'Où les Israélites campèrent-ils face à la mer ?', 'Pi-Hahiroth', 'Goshen', 'Ramsès', 'Succoth', 0, 'Ils campèrent devant Pi-Hahiroth, entre Migdol et la mer.', 'Exode 14:2'),
('exo_060', 'EXO', 'Comment le peuple réagit-il en voyant les Égyptiens ?', 'Ils eurent peur et crièrent à Moïse', 'Ils se préparèrent au combat', 'Ils restèrent calmes', 'Ils prièrent en silence', 0, 'Les enfants d''Israël eurent une grande peur et crièrent.', 'Exode 14:10'),
('exo_061', 'EXO', 'Que dit Moïse au peuple face à la mer ?', 'Préparez-vous à mourir', 'Ne craignez rien, l''Éternel combattra pour vous', 'Courez !', 'Nagez !', 1, 'N''ayez pas peur, tenez-vous là et voyez la délivrance de l''Éternel.', 'Exode 14:13'),
('exo_062', 'EXO', 'Que fit Moïse pour ouvrir la mer ?', 'Il pria', 'Il étendit sa main avec son bâton sur la mer', 'Il cria', 'Il frappa l''eau', 1, 'Toi, lève ton bâton et étends ta main sur la mer.', 'Exode 14:16'),
('exo_063', 'EXO', 'Qu''est-ce qui se plaça entre les Israélites et les Égyptiens ?', 'Un mur de feu', 'La colonne de nuée', 'Des anges', 'Un fossé', 1, 'La colonne de nuée se plaça entre le camp de l''Égypte et le camp d''Israël.', 'Exode 14:19-20'),
('exo_064', 'EXO', 'Comment les Israélites traversèrent-ils la mer ?', 'En nageant', 'À sec, les eaux formant une muraille', 'Sur des bateaux', 'Sur un pont', 1, 'Les eaux leur formaient une muraille à droite et à gauche.', 'Exode 14:22'),
('exo_065', 'EXO', 'Qu''arriva-t-il aux Égyptiens dans la mer ?', 'Ils traversèrent aussi', 'Ils furent engloutis par les eaux', 'Ils firent demi-tour', 'Ils s''arrêtèrent', 1, 'Les eaux revinrent et couvrirent les chars et les cavaliers.', 'Exode 14:28'),
('exo_066', 'EXO', 'Quel cantique Moïse et les Israélites chantèrent-ils ?', 'Un chant de tristesse', 'Un chant de victoire célébrant l''Éternel', 'Un chant de marche', 'Un chant de prière', 1, 'Je chanterai à l''Éternel car il a fait éclater sa gloire.', 'Exode 15:1'),
('exo_067', 'EXO', 'Qui dirigea les femmes dans un chant avec un tambourin ?', 'Séphora', 'Myriam', 'Une Égyptienne', 'La fille de Pharaon', 1, 'Myriam prit un tambourin et toutes les femmes la suivirent.', 'Exode 15:20'),

-- DANS LE DÉSERT (Chapitres 15-18)
('exo_068', 'EXO', 'Pourquoi l''eau de Mara était-elle imbuvable ?', 'Elle était salée', 'Elle était amère', 'Elle était boueuse', 'Elle était polluée', 1, 'Ils ne purent boire l''eau de Mara car elle était amère.', 'Exode 15:23'),
('exo_069', 'EXO', 'Comment Moïse rendit-il l''eau de Mara douce ?', 'En priant', 'En y jetant un bois que Dieu lui montra', 'En attendant', 'En la filtrant', 1, 'L''Éternel lui indiqua un bois qu''il jeta dans l''eau.', 'Exode 15:25'),
('exo_070', 'EXO', 'Qu''est-ce que la manne ?', 'Un fruit', 'Le pain que Dieu donna du ciel', 'Un animal', 'Une plante du désert', 1, 'Le matin, il y avait une couche de rosée. C''est le pain que l''Éternel vous donne.', 'Exode 16:14-15'),
('exo_071', 'EXO', 'Que signifie le mot "manne" ?', 'Nourriture divine', 'Qu''est-ce que c''est ?', 'Pain du ciel', 'Bénédiction', 1, 'Ils se dirent l''un à l''autre : Qu''est-ce que c''est ? (Man hou)' , 'Exode 16:15'),
('exo_072', 'EXO', 'Combien de manne devait-on ramasser chaque jour ?', 'Autant qu''on voulait', 'Un omer par personne', 'Deux omers par personne', 'Un kilo par famille', 1, 'Un omer par tête selon le nombre de personnes.', 'Exode 16:16'),
('exo_073', 'EXO', 'Pourquoi devait-on ramasser double le sixième jour ?', 'Pour faire des réserves', 'Car le septième jour était le sabbat', 'Par superstition', 'Pour les invités', 1, 'Le sixième jour, ils ramassèrent le double, car le septième était le sabbat.', 'Exode 16:22-26'),
('exo_074', 'EXO', 'Combien d''années les Israélites mangèrent-ils la manne ?', '10 ans', '20 ans', '40 ans', '70 ans', 2, 'Les enfants d''Israël mangèrent la manne pendant 40 ans.', 'Exode 16:35'),
('exo_075', 'EXO', 'Quel autre aliment Dieu leur donna-t-il ?', 'Du poisson', 'Des cailles', 'Du bœuf', 'Des fruits', 1, 'Le soir, il survint des cailles qui couvrirent le camp.', 'Exode 16:13'),
('exo_076', 'EXO', 'À Rephidim, de quoi le peuple manquait-il ?', 'De nourriture', 'D''eau', 'De vêtements', 'D''armes', 1, 'Il n''y avait point d''eau à boire pour le peuple.', 'Exode 17:1'),
('exo_077', 'EXO', 'Comment Moïse fit-il jaillir l''eau du rocher ?', 'En priant', 'En frappant le rocher avec son bâton', 'En creusant', 'En soufflant dessus', 1, 'Tu frapperas le rocher et il en sortira de l''eau.', 'Exode 17:6'),
('exo_078', 'EXO', 'Quel peuple attaqua Israël à Rephidim ?', 'Les Égyptiens', 'Les Amalécites', 'Les Philistins', 'Les Moabites', 1, 'Amalek vint combattre Israël à Rephidim.', 'Exode 17:8'),
('exo_079', 'EXO', 'Qui combattit les Amalécites ?', 'Moïse', 'Josué', 'Aaron', 'Caleb', 1, 'Moïse dit à Josué : Choisis-nous des hommes et combats Amalek.', 'Exode 17:9'),
('exo_080', 'EXO', 'Que se passait-il quand Moïse levait les mains ?', 'Le vent soufflait', 'Israël était le plus fort', 'La pluie tombait', 'Le soleil brillait', 1, 'Quand Moïse élevait sa main, Israël était le plus fort.', 'Exode 17:11'),
('exo_081', 'EXO', 'Qui soutenait les mains de Moïse ?', 'Josué et Caleb', 'Aaron et Hur', 'Ses fils', 'Des soldats', 1, 'Aaron et Hur soutenaient ses mains, l''un d''un côté, l''autre de l''autre.', 'Exode 17:12'),
('exo_082', 'EXO', 'Quel conseil Jéthro donna-t-il à Moïse ?', 'De construire un temple', 'D''établir des juges sur le peuple', 'De retourner en Égypte', 'De faire la guerre', 1, 'Choisis des hommes capables et établis-les comme chefs.', 'Exode 18:21'),

-- L''ALLIANCE AU SINAÏ (Chapitres 19-24)
('exo_083', 'EXO', 'Quel mois les Israélites arrivèrent-ils au Sinaï ?', 'Le premier mois', 'Le troisième mois', 'Le sixième mois', 'Le douzième mois', 1, 'Le troisième mois après la sortie d''Égypte, ils arrivèrent au Sinaï.', 'Exode 19:1'),
('exo_084', 'EXO', 'Que dit Dieu qu''Israël serait pour lui ?', 'Un peuple ordinaire', 'Un royaume de sacrificateurs et une nation sainte', 'Une armée', 'Des serviteurs', 1, 'Vous serez pour moi un royaume de sacrificateurs et une nation sainte.', 'Exode 19:6'),
('exo_085', 'EXO', 'Comment Dieu descendit-il sur le mont Sinaï ?', 'Silencieusement', 'Dans le feu, avec tonnerre, éclairs et trompette', 'Dans un vent léger', 'Dans une nuée blanche', 1, 'Il y eut des tonnerres, des éclairs et une épaisse nuée sur la montagne.', 'Exode 19:16'),
('exo_086', 'EXO', 'Que risquait celui qui touchait la montagne ?', 'Une maladie', 'La mort', 'L''exil', 'Une amende', 1, 'Quiconque touchera la montagne sera puni de mort.', 'Exode 19:12'),
('exo_087', 'EXO', 'Quel est le premier des dix commandements ?', 'Tu ne tueras point', 'Tu n''auras pas d''autres dieux devant ma face', 'Tu honoreras ton père et ta mère', 'Tu ne voleras point', 1, 'Tu n''auras pas d''autres dieux devant ma face.', 'Exode 20:3'),
('exo_088', 'EXO', 'Quel commandement interdit de faire des images pour les adorer ?', 'Le premier', 'Le deuxième', 'Le troisième', 'Le quatrième', 1, 'Tu ne te feras point d''image taillée.', 'Exode 20:4'),
('exo_089', 'EXO', 'Quel commandement concerne le nom de Dieu ?', 'Le premier', 'Le deuxième', 'Le troisième', 'Le quatrième', 2, 'Tu ne prendras point le nom de l''Éternel ton Dieu en vain.', 'Exode 20:7'),
('exo_090', 'EXO', 'Quel commandement concerne le sabbat ?', 'Le troisième', 'Le quatrième', 'Le cinquième', 'Le sixième', 1, 'Souviens-toi du jour du repos pour le sanctifier.', 'Exode 20:8'),
('exo_091', 'EXO', 'Quel commandement concerne les parents ?', 'Le quatrième', 'Le cinquième', 'Le sixième', 'Le septième', 1, 'Honore ton père et ta mère.', 'Exode 20:12'),
('exo_092', 'EXO', 'Quel commandement interdit le meurtre ?', 'Le cinquième', 'Le sixième', 'Le septième', 'Le huitième', 1, 'Tu ne tueras point.', 'Exode 20:13'),
('exo_093', 'EXO', 'Quel commandement interdit l''adultère ?', 'Le sixième', 'Le septième', 'Le huitième', 'Le neuvième', 1, 'Tu ne commettras point d''adultère.', 'Exode 20:14'),
('exo_094', 'EXO', 'Quel commandement interdit le vol ?', 'Le septième', 'Le huitième', 'Le neuvième', 'Le dixième', 1, 'Tu ne déroberas point.', 'Exode 20:15'),
('exo_095', 'EXO', 'Quel commandement interdit le faux témoignage ?', 'Le huitième', 'Le neuvième', 'Le dixième', 'Le septième', 1, 'Tu ne porteras point de faux témoignage.', 'Exode 20:16'),
('exo_096', 'EXO', 'Quel commandement interdit la convoitise ?', 'Le neuvième', 'Le dixième', 'Le huitième', 'Le septième', 1, 'Tu ne convoiteras point.', 'Exode 20:17'),
('exo_097', 'EXO', 'Combien de jours Moïse resta-t-il sur la montagne ?', '7 jours', '40 jours et 40 nuits', '21 jours', '12 jours', 1, 'Moïse entra au milieu de la nuée et demeura 40 jours et 40 nuits.', 'Exode 24:18'),
('exo_098', 'EXO', 'Qui monta sur la montagne avec Moïse au début ?', 'Aaron seul', 'Aaron, Nadab, Abihu et 70 anciens', 'Josué seul', 'Tout le peuple', 1, 'Moïse monta avec Aaron, Nadab, Abihu et 70 des anciens d''Israël.', 'Exode 24:9'),
('exo_099', 'EXO', 'Que virent-ils sous les pieds de Dieu ?', 'Du feu', 'Comme un ouvrage de saphir transparent', 'Des nuages', 'De l''or', 1, 'Sous ses pieds, c''était comme un ouvrage de saphir transparent.', 'Exode 24:10'),

-- LE VEAU D''OR (Chapitre 32)
('exo_100', 'EXO', 'Que fit le peuple voyant que Moïse tardait ?', 'Ils prièrent', 'Ils demandèrent à Aaron de leur faire un dieu', 'Ils partirent', 'Ils montèrent sur la montagne', 1, 'Le peuple vit que Moïse tardait et dit à Aaron : Fais-nous un dieu.', 'Exode 32:1'),
('exo_101', 'EXO', 'Que demanda Aaron au peuple ?', 'De l''argent', 'Leurs anneaux d''or', 'Du bois', 'Des pierres', 1, 'Ôtez les anneaux d''or qui sont aux oreilles de vos femmes.', 'Exode 32:2'),
('exo_102', 'EXO', 'Quelle idole Aaron fabriqua-t-il ?', 'Un serpent', 'Un veau d''or', 'Un lion', 'Un aigle', 1, 'Il façonna l''or avec un burin et fit un veau en fonte.', 'Exode 32:4'),
('exo_103', 'EXO', 'Comment Dieu décrivit-il le peuple à Moïse ?', 'Fidèle', 'Un peuple au cou raide', 'Obéissant', 'Saint', 1, 'C''est un peuple au cou raide.', 'Exode 32:9'),
('exo_104', 'EXO', 'Comment Moïse intercéda-t-il pour le peuple ?', 'Il pria pour leur pardon', 'Il rappela les promesses de Dieu à Abraham', 'Il offrit un sacrifice', 'Il pleura', 1, 'Souviens-toi d''Abraham, d''Isaac et d''Israël, tes serviteurs.', 'Exode 32:13'),
('exo_105', 'EXO', 'Que fit Moïse en voyant le veau et les danses ?', 'Il pleura', 'Il brisa les tables de la loi', 'Il s''enfuit', 'Il dansa avec eux', 1, 'Sa colère s''enflamma; il jeta les tables et les brisa au pied de la montagne.', 'Exode 32:19'),
('exo_106', 'EXO', 'Que fit Moïse du veau d''or ?', 'Il le garda', 'Il le brûla, le réduisit en poudre et le fit boire', 'Il le jeta dans le fleuve', 'Il l''enterra', 1, 'Il brûla le veau, le réduisit en poudre et le répandit sur l''eau qu''il fit boire.', 'Exode 32:20'),
('exo_107', 'EXO', 'Quelle tribu se rangea du côté de Moïse ?', 'Juda', 'Lévi', 'Benjamin', 'Ruben', 1, 'Tous les enfants de Lévi se rassemblèrent auprès de lui.', 'Exode 32:26'),
('exo_108', 'EXO', 'Combien de personnes moururent ce jour-là ?', '1000', '3000', '10000', '30000', 1, 'Il tomba du peuple environ 3000 hommes.', 'Exode 32:28'),

-- LE TABERNACLE (Chapitres 25-31, 35-40)
('exo_109', 'EXO', 'Comment s''appelle le sanctuaire portatif ?', 'Le temple', 'Le tabernacle (tente d''assignation)', 'La synagogue', 'L''autel', 1, 'Ils me feront un sanctuaire et j''habiterai au milieu d''eux.', 'Exode 25:8'),
('exo_110', 'EXO', 'Quel était l''objet le plus sacré du tabernacle ?', 'L''autel', 'L''arche de l''alliance', 'Le chandelier', 'La table', 1, 'Ils feront une arche de bois d''acacia.', 'Exode 25:10'),
('exo_111', 'EXO', 'De quel bois était faite l''arche ?', 'Cèdre', 'Acacia', 'Olivier', 'Chêne', 1, 'Ils feront une arche de bois d''acacia.', 'Exode 25:10'),
('exo_112', 'EXO', 'De quoi l''arche était-elle recouverte ?', 'D''argent', 'D''or pur', 'De bronze', 'De tissu', 1, 'Tu la couvriras d''or pur, au-dedans et au-dehors.', 'Exode 25:11'),
('exo_113', 'EXO', 'Que représentaient les chérubins sur l''arche ?', 'Des animaux', 'Des êtres célestes aux ailes déployées', 'Des humains', 'Des étoiles', 1, 'Deux chérubins d''or aux deux extrémités du propitiatoire.', 'Exode 25:18'),
('exo_114', 'EXO', 'Qu''est-ce que le propitiatoire ?', 'L''autel', 'Le couvercle de l''arche', 'La table', 'Le chandelier', 1, 'Tu feras un propitiatoire d''or pur.', 'Exode 25:17'),
('exo_115', 'EXO', 'Que contenait l''arche ?', 'De l''or', 'Les tables de la loi', 'Des vêtements', 'Des offrandes', 1, 'Tu mettras dans l''arche le témoignage que je te donnerai.', 'Exode 25:16'),
('exo_116', 'EXO', 'Combien de branches avait le chandelier d''or ?', '5', '6', '7', '12', 2, 'Tu feras un chandelier d''or pur avec six branches sortant de ses côtés.', 'Exode 25:31-32'),
('exo_117', 'EXO', 'Que séparait le voile du tabernacle ?', 'L''entrée de la sortie', 'Le lieu saint du lieu très saint', 'Le tabernacle du camp', 'Les hommes des femmes', 1, 'Le voile vous servira de séparation entre le lieu saint et le lieu très saint.', 'Exode 26:33'),
('exo_118', 'EXO', 'Quel était l''autel de bronze ?', 'Pour brûler l''encens', 'Pour les holocaustes', 'Pour le pain', 'Pour l''eau', 1, 'Tu feras l''autel de bois d''acacia pour les holocaustes.', 'Exode 27:1'),
('exo_119', 'EXO', 'Qui fut consacré comme souverain sacrificateur ?', 'Moïse', 'Aaron', 'Josué', 'Caleb', 1, 'Fais approcher Aaron ton frère pour qu''il soit à mon service dans le sacerdoce.', 'Exode 28:1'),
('exo_120', 'EXO', 'Combien de pierres étaient sur le pectoral du souverain sacrificateur ?', '7', '10', '12', '24', 2, 'Tu y enchâsseras des pierres, 4 rangées de pierres.', 'Exode 28:17-21'),
('exo_121', 'EXO', 'Que représentaient ces 12 pierres ?', 'Les mois de l''année', 'Les 12 tribus d''Israël', 'Les apôtres', 'Les prophètes', 1, 'Les pierres seront aux noms des fils d''Israël; il y en aura 12.', 'Exode 28:21'),
('exo_122', 'EXO', 'Qui étaient les artisans principaux du tabernacle ?', 'Moïse et Aaron', 'Betsaleel et Oholiab', 'Josué et Caleb', 'Les Lévites', 1, 'J''ai choisi Betsaleel et j''ai mis auprès de lui Oholiab.', 'Exode 31:2-6'),
('exo_123', 'EXO', 'De quelle tribu était Betsaleel ?', 'Lévi', 'Juda', 'Benjamin', 'Dan', 1, 'Betsaleel, fils d''Uri, fils de Hur, de la tribu de Juda.', 'Exode 31:2'),
('exo_124', 'EXO', 'Que fit Moïse quand le tabernacle fut terminé ?', 'Il y entra', 'Il le consacra avec de l''huile', 'Il pria', 'Il dansa', 1, 'Moïse oignit le tabernacle et tout ce qui s''y trouvait.', 'Exode 40:9'),
('exo_125', 'EXO', 'Que se passa-t-il quand le tabernacle fut dressé ?', 'Rien', 'La nuée le couvrit et la gloire de l''Éternel le remplit', 'Il y eut un tremblement de terre', 'La foudre tomba', 1, 'La nuée couvrit la tente d''assignation et la gloire de l''Éternel remplit le tabernacle.', 'Exode 40:34'),
('exo_126', 'EXO', 'Moïse pouvait-il entrer quand la gloire remplissait le tabernacle ?', 'Oui, toujours', 'Non, il ne pouvait pas y entrer', 'Seulement le matin', 'Seulement le soir', 1, 'Moïse ne pouvait pas entrer car la nuée reposait dessus.', 'Exode 40:35'),
('exo_127', 'EXO', 'Comment les Israélites savaient-ils quand partir ?', 'Par le son d''une trompette', 'Quand la nuée s''élevait au-dessus du tabernacle', 'Par ordre de Moïse', 'Au lever du soleil', 1, 'Quand la nuée s''élevait, les enfants d''Israël partaient.', 'Exode 40:36'),
('exo_128', 'EXO', 'Comment s''appelait la pièce la plus sacrée ?', 'Le parvis', 'Le lieu très saint (Saint des Saints)', 'L''entrée', 'La cour', 1, 'Le lieu très saint, où se trouvait l''arche de l''alliance.', 'Exode 26:34'),
('exo_129', 'EXO', 'Qui pouvait entrer dans le lieu très saint ?', 'Tout le peuple', 'Tous les prêtres', 'Le souverain sacrificateur une fois par an', 'Moïse seulement', 2, 'Aaron n''entrera pas en tout temps dans le sanctuaire.', 'Lévitique 16:2'),
('exo_130', 'EXO', 'Sur quelles tables Dieu écrivit-il les commandements la seconde fois ?', 'Des tables de bois', 'Des tables de pierre que Moïse tailla', 'Des tables d''or', 'Des parchemins', 1, 'Taille deux tables de pierre comme les premières.', 'Exode 34:1'),

-- MOÏSE ET LA GLOIRE DE DIEU (Chapitres 33-34)
('exo_131', 'EXO', 'Que demanda Moïse à voir ?', 'L''Égypte', 'La gloire de Dieu', 'La terre promise', 'Les anges', 1, 'Moïse dit : Fais-moi voir ta gloire.', 'Exode 33:18'),
('exo_132', 'EXO', 'Dieu dit que l''homme ne peut voir quoi et vivre ?', 'Les anges', 'Sa face', 'Le ciel', 'Le tabernacle', 1, 'Tu ne pourras pas voir ma face, car l''homme ne peut me voir et vivre.', 'Exode 33:20'),
('exo_133', 'EXO', 'Où Dieu plaça-t-il Moïse pour voir sa gloire ?', 'Au sommet de la montagne', 'Dans le creux du rocher', 'Dans le tabernacle', 'Au bord de la mer', 1, 'Je te mettrai dans le creux du rocher et je te couvrirai de ma main.', 'Exode 33:22'),
('exo_134', 'EXO', 'Qu''arriva-t-il au visage de Moïse après avoir parlé avec Dieu ?', 'Il était triste', 'Son visage rayonnait', 'Il était fatigué', 'Il pleurait', 1, 'Moïse ne savait pas que la peau de son visage rayonnait.', 'Exode 34:29'),
('exo_135', 'EXO', 'Que mit Moïse sur son visage ?', 'De la peinture', 'Un voile', 'De l''huile', 'De la poussière', 1, 'Moïse remit le voile sur son visage.', 'Exode 34:33'),

-- RÉCAPITULATIF ET QUESTIONS SUPPLÉMENTAIRES
('exo_136', 'EXO', 'Quel était le nom hébreu de Moïse ?', 'Moshe', 'Aaron', 'Yehoshua', 'Levi', 0, 'En hébreu, Moïse se dit Moshe.', 'Exode 2:10'),
('exo_137', 'EXO', 'Quelle fête commémore la sortie d''Égypte ?', 'Pourim', 'La Pâque (Pessah)', 'Hanoukka', 'Soukkot', 1, 'C''est la Pâque de l''Éternel, qui a épargné les maisons des Israélites.', 'Exode 12:27'),
('exo_138', 'EXO', 'Que signifie le mot "Exode" ?', 'Voyage', 'Sortie', 'Libération', 'Alliance', 1, 'Exode vient du grec "exodos" signifiant sortie.', 'Titre du livre'),
('exo_139', 'EXO', 'Combien de plaies Dieu envoya-t-il sur l''Égypte ?', '7', '9', '10', '12', 2, 'Dieu envoya 10 plaies pour contraindre Pharaon à libérer Israël.', 'Exode 7-12'),
('exo_140', 'EXO', 'Quel titre portait Aaron ?', 'Prophète', 'Souverain sacrificateur', 'Roi', 'Juge', 1, 'Aaron servait comme souverain sacrificateur.', 'Exode 28:1'),
('exo_141', 'EXO', 'Quelle était la fonction des Lévites ?', 'Guerriers', 'Service du tabernacle', 'Agriculteurs', 'Marchands', 1, 'Les Lévites étaient chargés du service du tabernacle.', 'Nombres 1:50'),
('exo_142', 'EXO', 'Quel animal ne devait pas briser les os de l''agneau pascal ?', 'Le bœuf', 'L''agneau lui-même', 'Le sacrifice', 'Le prêtre', 1, 'Vous ne briserez aucun os.', 'Exode 12:46'),
('exo_143', 'EXO', 'Quel océan/mer les Israélites traversèrent-ils ?', 'La mer Méditerranée', 'La mer Rouge (mer des Roseaux)', 'Le Nil', 'Le Jourdain', 1, 'Les enfants d''Israël traversèrent la mer Rouge à sec.', 'Exode 14:22'),
('exo_144', 'EXO', 'Combien de sources et de palmiers trouvèrent-ils à Élim ?', '7 sources, 40 palmiers', '12 sources, 70 palmiers', '10 sources, 50 palmiers', '3 sources, 30 palmiers', 1, 'Ils arrivèrent à Élim où il y avait 12 sources d''eau et 70 palmiers.', 'Exode 15:27'),
('exo_145', 'EXO', 'Quel jour les Israélites arrivèrent-ils au Sinaï ?', 'Le premier jour du premier mois', 'Le premier jour du troisième mois', 'Le quinzième jour', 'Le septième jour', 1, 'Le troisième mois, ils arrivèrent au désert du Sinaï.', 'Exode 19:1'),
('exo_146', 'EXO', 'Que devait contenir le tabernacle dans le lieu saint ?', 'L''arche seulement', 'Le chandelier, la table des pains, l''autel des parfums', 'Les vêtements des prêtres', 'Les offrandes', 1, 'Le lieu saint contenait ces trois éléments.', 'Exode 25-30'),
('exo_147', 'EXO', 'Comment s''appelaient les deux fils d''Aaron ?', 'Moïse et Josué', 'Nadab et Abihu', 'Caleb et Josué', 'Éléazar et Ithamar', 1, 'Les fils d''Aaron : Nadab, Abihu, Éléazar et Ithamar.', 'Exode 28:1'),
('exo_148', 'EXO', 'Quel vêtement spécial Aaron portait-il ?', 'Une couronne', 'L''éphod', 'Une cape rouge', 'Des sandales d''or', 1, 'Ils feront l''éphod d''or, de fil bleu, pourpre et cramoisi.', 'Exode 28:6'),
('exo_149', 'EXO', 'Que portait Aaron sur le front ?', 'Une plume', 'Une lame d''or avec "Sainteté à l''Éternel"', 'Un bijou', 'Une marque', 1, 'Tu graveras sur la lame : Sainteté à l''Éternel.', 'Exode 28:36'),
('exo_150', 'EXO', 'Quel jour de la semaine était sanctifié ?', 'Le premier jour', 'Le septième jour (sabbat)', 'Le troisième jour', 'Le cinquième jour', 1, 'Souviens-toi du jour du sabbat pour le sanctifier.', 'Exode 20:8');
