INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('gen_061', 'GEN', 'Comment s''appelait Abraham avant que Dieu change son nom ?', 'Abraham', 'Abram', 'Isaac', 'Jacob', 1, 'Au début, il s''appelait Abram, qui signifie "père élevé".', 'Genèse 11:26'),
('gen_062', 'GEN', 'D''où venait Abraham ?', 'Canaan', 'Ur en Chaldée', 'Égypte', 'Babylone', 1, 'Abraham venait d''Ur des Chaldéens.', 'Genèse 11:31'),
('gen_063', 'GEN', 'Qui était le père d''Abraham ?', 'Noé', 'Térach', 'Nachor', 'Sem', 1, 'Térach engendra Abram, Nachor et Haran.', 'Genèse 11:26'),
('gen_064', 'GEN', 'Quel âge avait Abraham quand il quitta Charan ?', '50 ans', '65 ans', '75 ans', '100 ans', 2, 'Abram était âgé de 75 ans lorsqu''il sortit de Charan.', 'Genèse 12:4'),
('gen_065', 'GEN', 'Qui accompagna Abraham quand il quitta Charan ?', 'Isaac', 'Lot', 'Jacob', 'Ésaü', 1, 'Abram prit Saraï sa femme et Lot, fils de son frère.', 'Genèse 12:5'),
('gen_066', 'GEN', 'Quelle promesse Dieu fit-il à Abraham ?', 'De le rendre célèbre', 'De faire de lui une grande nation', 'De lui donner beaucoup d''or', 'De lui donner un palais', 1, 'Je ferai de toi une grande nation et je te bénirai.', 'Genèse 12:2'),
('gen_067', 'GEN', 'Dans quel pays Abraham descendit-il à cause de la famine ?', 'Assyrie', 'Babylone', 'Égypte', 'Moab', 2, 'Il y eut une famine dans le pays; Abram descendit en Égypte.', 'Genèse 12:10'),
('gen_068', 'GEN', 'Que dit Abraham concernant Sara au pharaon ?', 'C''est ma femme', 'C''est ma sœur', 'C''est ma servante', 'C''est ma cousine', 1, 'Dis que tu es ma sœur, afin que je sois bien traité à cause de toi.', 'Genèse 12:13'),
('gen_069', 'GEN', 'Pourquoi Abraham et Lot se séparèrent-ils ?', 'Ils se disputèrent', 'Le pays ne pouvait les contenir ensemble', 'Lot voulait partir', 'Dieu l''ordonna', 1, 'Le pays n''était pas suffisant pour qu''ils demeurent ensemble.', 'Genèse 13:6'),
('gen_070', 'GEN', 'Quelle région Lot choisit-il ?', 'Les montagnes', 'La plaine du Jourdain', 'Le désert', 'Canaan', 1, 'Lot choisit pour lui toute la plaine du Jourdain.', 'Genèse 13:11'),
('gen_071', 'GEN', 'Près de quelle ville Lot s''établit-il ?', 'Jérusalem', 'Sodome', 'Babylone', 'Ur', 1, 'Lot dressa ses tentes jusqu''à Sodome.', 'Genèse 13:12'),
('gen_072', 'GEN', 'Qui était Melchisédek ?', 'Un roi ennemi', 'Roi de Salem et sacrificateur', 'Le père de Lot', 'Un prophète', 1, 'Melchisédek, roi de Salem, sacrificateur du Dieu Très-Haut.', 'Genèse 14:18'),
('gen_073', 'GEN', 'Que donna Abraham à Melchisédek ?', 'De l''or', 'La dîme de tout', 'Des animaux', 'Des vêtements', 1, 'Abram lui donna la dîme de tout.', 'Genèse 14:20'),
('gen_074', 'GEN', 'Dans quelle vision Dieu parla-t-il à Abraham ?', 'Dans un songe', 'Ne crains point, je suis ton bouclier', 'Par un ange', 'Dans un buisson ardent', 1, 'Ne crains point, Abram; je suis ton bouclier.', 'Genèse 15:1'),
('gen_075', 'GEN', 'À quoi Dieu compara-t-il la descendance d''Abraham ?', 'Aux grains de sable', 'Aux étoiles du ciel', 'Aux arbres', 'Aux oiseaux', 1, 'Compte les étoiles... Telle sera ta postérité.', 'Genèse 15:5'),
('gen_076', 'GEN', 'Comment Abraham fut-il déclaré juste ?', 'Par ses œuvres', 'Par sa foi', 'Par ses sacrifices', 'Par son obéissance', 1, 'Abram crut à l''Éternel, qui le lui imputa à justice.', 'Genèse 15:6'),
('gen_077', 'GEN', 'Comment s''appelait la servante de Sara ?', 'Bilha', 'Zilpa', 'Agar', 'Rachel', 2, 'Saraï avait une servante égyptienne nommée Agar.', 'Genèse 16:1'),
('gen_078', 'GEN', 'Pourquoi Sara donna-t-elle Agar à Abraham ?', 'Pour faire le ménage', 'Pour avoir un enfant par elle', 'Pour l''aider au travail', 'Pour la libérer', 1, 'Va vers ma servante; peut-être aurai-je par elle des enfants.', 'Genèse 16:2'),
('gen_079', 'GEN', 'Comment s''appela le fils d''Abraham et Agar ?', 'Isaac', 'Ismaël', 'Jacob', 'Ésaü', 1, 'Tu enfanteras un fils, et tu l''appelleras Ismaël.', 'Genèse 16:11'),
('gen_080', 'GEN', 'Quel âge avait Abraham quand Ismaël naquit ?', '75 ans', '86 ans', '99 ans', '100 ans', 1, 'Abram était âgé de 86 ans lorsqu''Agar enfanta Ismaël.', 'Genèse 16:16'),
('gen_081', 'GEN', 'Quel signe d''alliance Dieu établit-il avec Abraham ?', 'Le sabbat', 'La circoncision', 'Le sacrifice', 'Le baptême', 1, 'Vous vous circoncirez; ce sera un signe d''alliance.', 'Genèse 17:11'),
('gen_082', 'GEN', 'Pourquoi Dieu changea-t-il le nom d''Abram en Abraham ?', 'Pour le punir', 'Car il serait père d''une multitude de nations', 'Pour l''honorer', 'Par tradition', 1, 'Tu seras père d''une multitude de nations.', 'Genèse 17:5'),
('gen_083', 'GEN', 'Comment Dieu changea-t-il le nom de Saraï ?', 'En Marie', 'En Sara', 'En Rebecca', 'En Rachel', 1, 'Tu ne l''appelleras plus Saraï, mais Sara.', 'Genèse 17:15'),
('gen_084', 'GEN', 'Combien de visiteurs Abraham reçut-il à Mamré ?', 'Un', 'Deux', 'Trois', 'Quatre', 2, 'Trois hommes se tenaient devant lui.', 'Genèse 18:2'),
('gen_085', 'GEN', 'Que fit Sara quand elle entendit qu''elle aurait un fils ?', 'Elle pleura', 'Elle rit', 'Elle pria', 'Elle dansa', 1, 'Sara rit en elle-même.', 'Genèse 18:12'),
('gen_086', 'GEN', 'Pour quelle ville Abraham intercéda-t-il auprès de Dieu ?', 'Jérusalem', 'Sodome', 'Babylone', 'Ninive', 1, 'Abraham intercéda pour Sodome.', 'Genèse 18:23-33'),
('gen_087', 'GEN', 'À combien de justes Abraham descendit-il dans sa négociation ?', '50', '20', '10', '5', 2, 'Abraham négocia jusqu''à 10 justes.', 'Genèse 18:32'),
('gen_088', 'GEN', 'Qui alla à Sodome pour avertir Lot ?', 'Abraham', 'Deux anges', 'Dieu lui-même', 'Un prophète', 1, 'Les deux anges arrivèrent à Sodome sur le soir.', 'Genèse 19:1'),
('gen_089', 'GEN', 'Que dit l''ange à Lot pour fuir ?', 'Regarde en arrière', 'Ne regarde pas derrière toi', 'Cours vers Sodome', 'Cache-toi', 1, 'Sauve-toi; ne regarde pas derrière toi.', 'Genèse 19:17'),
('gen_090', 'GEN', 'Qu''arriva-t-il à la femme de Lot ?', 'Elle mourut de faim', 'Elle devint une statue de sel', 'Elle fut emportée', 'Elle se perdit', 1, 'La femme de Lot regarda en arrière et devint une statue de sel.', 'Genèse 19:26'),
('gen_091', 'GEN', 'Comment Dieu détruisit-il Sodome et Gomorrhe ?', 'Par un tremblement de terre', 'Par le feu et le soufre', 'Par l''eau', 'Par la famine', 1, 'L''Éternel fit pleuvoir du ciel sur Sodome du soufre et du feu.', 'Genèse 19:24'),
('gen_092', 'GEN', 'Quel âge avait Abraham quand Isaac naquit ?', '86 ans', '90 ans', '99 ans', '100 ans', 3, 'Abraham était âgé de 100 ans à la naissance d''Isaac.', 'Genèse 21:5'),
('gen_093', 'GEN', 'Que signifie le nom Isaac ?', 'Béni', 'Il rit', 'Promis', 'Aimé', 1, 'Isaac signifie "il rit", car Sara avait ri.', 'Genèse 21:6'),
('gen_094', 'GEN', 'Pourquoi Sara voulut-elle chasser Agar et Ismaël ?', 'Ils étaient méchants', 'Ismaël se moquait d''Isaac', 'Ils mangeaient trop', 'Ils désobéissaient', 1, 'Sara vit rire le fils d''Agar.', 'Genèse 21:9'),
('gen_095', 'GEN', 'Où Agar et Ismaël allèrent-ils ?', 'En Égypte', 'Au désert de Beer-Schéba', 'À Canaan', 'À Babylone', 1, 'Elle s''en alla et s''égara dans le désert de Beer-Schéba.', 'Genèse 21:14'),
('gen_096', 'GEN', 'Qui sauva Agar et Ismaël dans le désert ?', 'Abraham', 'Un ange de Dieu', 'Un voyageur', 'Isaac', 1, 'L''ange de Dieu appela Agar du ciel.', 'Genèse 21:17'),
('gen_097', 'GEN', 'Où Dieu demanda-t-il à Abraham d''aller sacrifier Isaac ?', 'Mont Sinaï', 'Mont Morija', 'Mont Carmel', 'Mont Horeb', 1, 'Offre-le en holocauste sur une montagne que je te dirai.', 'Genèse 22:2'),
('gen_098', 'GEN', 'Combien de jours de marche Abraham fit-il vers le lieu du sacrifice ?', 'Un jour', 'Deux jours', 'Trois jours', 'Sept jours', 2, 'Le troisième jour, Abraham leva les yeux et vit le lieu.', 'Genèse 22:4'),
('gen_099', 'GEN', 'Que dit Isaac à Abraham concernant le sacrifice ?', 'Où est le bois ?', 'Où est l''agneau ?', 'Où est le feu ?', 'Où allons-nous ?', 1, 'Voici le feu et le bois; mais où est l''agneau pour l''holocauste ?', 'Genèse 22:7'),
('gen_100', 'GEN', 'Que répondit Abraham à Isaac ?', 'Tu es l''agneau', 'Dieu se pourvoira de l''agneau', 'Nous n''avons pas besoin d''agneau', 'Cherchons-en un', 1, 'Mon fils, Dieu se pourvoira lui-même de l''agneau.', 'Genèse 22:8'),
('gen_101', 'GEN', 'Qui arrêta Abraham au moment de sacrifier Isaac ?', 'Sara', 'L''ange de l''Éternel', 'Isaac lui-même', 'Un prophète', 1, 'L''ange de l''Éternel l''appela des cieux et dit : Abraham !', 'Genèse 22:11'),
('gen_102', 'GEN', 'Quel animal Abraham sacrifia-t-il à la place d''Isaac ?', 'Un agneau', 'Un bélier', 'Un taureau', 'Une chèvre', 1, 'Abraham vit un bélier retenu par les cornes dans un buisson.', 'Genèse 22:13'),
('gen_103', 'GEN', 'Comment Abraham appela-t-il ce lieu ?', 'Béthel', 'L''Éternel pourvoira', 'Salem', 'Morija', 1, 'Abraham donna à ce lieu le nom de Yahvé-Yiré (L''Éternel pourvoira).', 'Genèse 22:14'),
('gen_104', 'GEN', 'Quel âge avait Sara quand elle mourut ?', '90 ans', '100 ans', '127 ans', '150 ans', 2, 'Sara vécut 127 ans.', 'Genèse 23:1'),
('gen_105', 'GEN', 'Où Sara fut-elle enterrée ?', 'En Égypte', 'À Hébron, dans la caverne de Macpéla', 'À Béthel', 'À Beer-Schéba', 1, 'Abraham enterra Sara dans la caverne du champ de Macpéla.', 'Genèse 23:19'),
('gen_106', 'GEN', 'Qui Abraham envoya-t-il chercher une femme pour Isaac ?', 'Lot', 'Son serviteur Éliézer', 'Un prophète', 'Un ange', 1, 'Abraham dit à son serviteur, le plus ancien de sa maison.', 'Genèse 24:2'),
('gen_107', 'GEN', 'Où le serviteur alla-t-il chercher une femme pour Isaac ?', 'En Égypte', 'En Mésopotamie, à Nachor', 'À Sodome', 'En Canaan', 1, 'Tu iras dans mon pays et vers ma parenté.', 'Genèse 24:4'),
('gen_108', 'GEN', 'Quel signe le serviteur demanda-t-il pour reconnaître la femme ?', 'Qu''elle soit belle', 'Qu''elle offre à boire aux chameaux', 'Qu''elle chante', 'Qu''elle porte un voile', 1, 'Que celle qui dira : Bois, et j''abreuverai tes chameaux, soit la femme.', 'Genèse 24:14'),
('gen_109', 'GEN', 'Comment s''appelait la femme choisie pour Isaac ?', 'Rachel', 'Léa', 'Rebecca', 'Sara', 2, 'La jeune fille s''appelait Rebecca.', 'Genèse 24:15'),
('gen_110', 'GEN', 'Qui était le père de Rebecca ?', 'Laban', 'Béthuel', 'Nachor', 'Abraham', 1, 'Je suis fille de Béthuel, fils de Milca et de Nachor.', 'Genèse 24:24'),
('gen_111', 'GEN', 'Quel âge avait Abraham quand il mourut ?', '150 ans', '175 ans', '200 ans', '180 ans', 1, 'Abraham vécut 175 ans.', 'Genèse 25:7'),
('gen_112', 'GEN', 'Qui enterra Abraham ?', 'Isaac seul', 'Isaac et Ismaël', 'Les serviteurs', 'Les voisins', 1, 'Isaac et Ismaël, ses fils, l''enterrèrent.', 'Genèse 25:9'),
