-- Questions pour l'Évangile selon Jean
-- L'évangile du Fils de Dieu

-- Le Prologue (Chapitre 1)
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation, verse_ref) VALUES
('joh_001', 'JHN', 'Comment l''évangile de Jean commence-t-il ?', '["Par la généalogie de Jésus","Au commencement était la Parole, et la Parole était avec Dieu","Par la naissance de Jésus","Par le ministère de Jean-Baptiste"]', 1, 'Jean commence par le Logos éternel, la Parole divine.', 'Jean 1:1'),
('joh_002', 'JHN', 'Qui est "la Parole" dans le prologue ?', '["La Bible","Jésus-Christ, qui était Dieu et s''est fait chair","Un ange","Moïse"]', 1, 'La Parole était Dieu... et la Parole a été faite chair.', 'Jean 1:1,14'),
('joh_003', 'JHN', 'Qu''est-ce qui a été fait par la Parole ?', '["Quelques choses","Toutes choses - rien n''a été fait sans elle","Seulement l''homme","Seulement le ciel"]', 1, 'Toutes choses ont été faites par elle, et rien n''a été fait sans elle.', 'Jean 1:3'),
('joh_004', 'JHN', 'Qu''était la vie dans la Parole ?', '["La force","La lumière des hommes","L''énergie","Le mouvement"]', 1, 'En elle était la vie, et la vie était la lumière des hommes.', 'Jean 1:4'),
('joh_005', 'JHN', 'Comment les ténèbres ont-elles réagi à la lumière ?', '["Elles l''ont accueillie","Elles ne l''ont pas comprise/reçue","Elles l''ont éteinte","Elles ont disparu"]', 1, 'La lumière luit dans les ténèbres, et les ténèbres ne l''ont point reçue.', 'Jean 1:5'),
('joh_006', 'JHN', 'Pourquoi Jean-Baptiste fut-il envoyé ?', '["Pour baptiser seulement","Pour rendre témoignage à la lumière afin que tous croient","Pour prêcher la loi","Pour annoncer la fin"]', 1, 'Il vint pour rendre témoignage à la lumière.', 'Jean 1:7'),
('joh_007', 'JHN', 'Comment les siens ont-ils reçu la Parole ?', '["Avec joie","Ils ne l''ont pas reçue","Avec respect","Avec peur"]', 1, 'Il est venu chez les siens, et les siens ne l''ont point reçu.', 'Jean 1:11'),
('joh_008', 'JHN', 'Que reçoivent ceux qui croient en son nom ?', '["De l''argent","Le pouvoir de devenir enfants de Dieu","La sagesse humaine","La richesse"]', 1, 'À tous ceux qui l''ont reçu, il a donné le pouvoir de devenir enfants de Dieu.', 'Jean 1:12'),
('joh_009', 'JHN', 'Comment les enfants de Dieu sont-ils nés ?', '["Du sang ou de la volonté humaine","De Dieu, non du sang ni de la volonté de l''homme","De parents pieux","D''eux-mêmes"]', 1, 'Lesquels sont nés, non du sang, ni de la chair, mais de Dieu.', 'Jean 1:13'),
('joh_010', 'JHN', 'Où la Parole faite chair a-t-elle habité ?', '["Dans le temple","Parmi nous, et nous avons contemplé sa gloire","Au ciel seulement","En Égypte"]', 1, 'La Parole a été faite chair et a habité parmi nous.', 'Jean 1:14'),
('joh_011', 'JHN', 'De quoi la Parole faite chair était-elle pleine ?', '["De puissance seulement","De grâce et de vérité","De jugement","De colère"]', 1, 'Pleine de grâce et de vérité.', 'Jean 1:14'),
('joh_012', 'JHN', 'Par qui la grâce et la vérité sont-elles venues ?', '["Par Moïse","Par Jésus-Christ","Par les prophètes","Par les anges"]', 1, 'La loi a été donnée par Moïse, la grâce et la vérité par Jésus-Christ.', 'Jean 1:17'),
('joh_013', 'JHN', 'Qui a fait connaître Dieu que personne n''a jamais vu ?', '["Moïse","Le Fils unique, qui est dans le sein du Père","Les prophètes","Les anges"]', 1, 'Le Fils unique, qui est dans le sein du Père, l''a fait connaître.', 'Jean 1:18'),
('joh_014', 'JHN', 'Comment Jean-Baptiste se décrivit-il ?', '["Le Messie","Une voix qui crie dans le désert","Élie","Un prophète"]', 1, 'Je suis la voix de celui qui crie dans le désert.', 'Jean 1:23'),
('joh_015', 'JHN', 'Comment Jean-Baptiste appela-t-il Jésus ?', '["Maître","L''Agneau de Dieu qui ôte le péché du monde","Prophète","Roi"]', 1, 'Voici l''Agneau de Dieu, qui ôte le péché du monde.', 'Jean 1:29'),
('joh_016', 'JHN', 'Comment Jean sut-il que Jésus était le Messie ?', '["Par révélation directe","Il vit l''Esprit descendre et demeurer sur lui","Par les Écritures","Par un rêve"]', 1, 'J''ai vu l''Esprit descendre du ciel comme une colombe et s''arrêter sur lui.', 'Jean 1:32'),
('joh_017', 'JHN', 'Qui furent les premiers disciples de Jésus selon Jean ?', '["Pierre et Jacques","André et un autre disciple de Jean-Baptiste","Philippe et Nathanaël","Matthieu et Thomas"]', 1, 'André et probablement Jean suivirent Jésus les premiers.', 'Jean 1:37-40'),
('joh_018', 'JHN', 'Que dit Jésus à Nathanaël sous le figuier ?', '["Bienvenue","Avant que Philippe t''appelât, je t''ai vu sous le figuier","Tu es sage","Tu es pharisien"]', 1, 'Jésus démontra sa connaissance divine.', 'Jean 1:48'),
('joh_019', 'JHN', 'Que dit Nathanaël après cette révélation ?', '["Je ne comprends pas","Rabbi, tu es le Fils de Dieu, le Roi d''Israël!","Qui es-tu?","Aide-moi"]', 1, 'Nathanaël reconnut immédiatement la divinité de Jésus.', 'Jean 1:49'),
('joh_020', 'JHN', 'Que promit Jésus de montrer à Nathanaël ?', '["Des richesses","De plus grandes choses - le ciel ouvert et les anges montant et descendant","Des signes","La terre promise"]', 1, 'Vous verrez le ciel ouvert et les anges montant et descendant sur le Fils de l''homme.', 'Jean 1:51'),

-- Les noces de Cana et purification du temple (Chapitres 2-3)
('joh_021', 'JHN', 'Où Jésus fit-il son premier miracle ?', '["À Jérusalem","À Cana en Galilée","À Capernaüm","À Nazareth"]', 1, 'Tel fut le premier des miracles de Jésus, à Cana en Galilée.', 'Jean 2:11'),
('joh_022', 'JHN', 'Quel était ce premier miracle ?', '["Guérir un malade","Changer l''eau en vin lors d''un mariage","Multiplier les pains","Calmer la tempête"]', 1, 'Jésus changea l''eau en vin aux noces de Cana.', 'Jean 2:9'),
('joh_023', 'JHN', 'Combien de jarres d''eau furent changées en vin ?', '["Trois","Six jarres de pierre","Douze","Une seule"]', 1, 'Il y avait là six jarres de pierre contenant chacune deux ou trois mesures.', 'Jean 2:6'),
('joh_024', 'JHN', 'Comment le maître du repas qualifia-t-il le vin ?', '["Ordinaire","Le bon vin gardé jusqu''à maintenant","Acceptable","Médiocre"]', 1, 'Le maître du repas dit que c''était le meilleur vin, servi en dernier.', 'Jean 2:10'),
('joh_025', 'JHN', 'Que fit Jésus dans le temple de Jérusalem ?', '["Il pria seulement","Il chassa les vendeurs avec un fouet de cordes","Il enseigna seulement","Il offrit un sacrifice"]', 1, 'Il fit un fouet de cordes et chassa tous les vendeurs du temple.', 'Jean 2:15'),
('joh_026', 'JHN', 'Quel signe Jésus promit-il concernant le temple ?', '["Sa reconstruction","Détruisez ce temple et en trois jours je le relèverai","Son agrandissement","Sa purification"]', 1, 'Jésus parlait du temple de son corps et de sa résurrection.', 'Jean 2:19,21'),
('joh_027', 'JHN', 'Qui vint voir Jésus de nuit ?', '["Un pécheur","Nicodème, un pharisien, chef des Juifs","Un centenier","Un collecteur d''impôts"]', 1, 'Nicodème vint vers Jésus de nuit.', 'Jean 3:1-2'),
('joh_028', 'JHN', 'Que doit-il arriver pour voir le royaume de Dieu ?', '["Être juste","Naître de nouveau (d''en haut)","Payer la dîme","Observer la loi"]', 1, 'Si un homme ne naît de nouveau, il ne peut voir le royaume de Dieu.', 'Jean 3:3'),
('joh_029', 'JHN', 'De quoi doit-on naître pour entrer dans le royaume ?', '["De parents justes","D''eau et d''Esprit","De la chair seulement","De la volonté humaine"]', 1, 'Si un homme ne naît d''eau et d''Esprit, il ne peut entrer dans le royaume.', 'Jean 3:5'),
('joh_030', 'JHN', 'À quoi Jésus compara-t-il le vent ?', '["Au feu","À l''œuvre mystérieuse de l''Esprit","À l''eau","Au tonnerre"]', 1, 'Le vent souffle où il veut; ainsi en est-il de tout homme né de l''Esprit.', 'Jean 3:8'),
('joh_031', 'JHN', 'À quoi Jésus fit-il référence concernant Moïse ?', '["Aux tables de la loi","Au serpent d''airain élevé dans le désert","Au buisson ardent","À la manne"]', 1, 'Comme Moïse éleva le serpent, ainsi le Fils de l''homme doit être élevé.', 'Jean 3:14'),
('joh_032', 'JHN', 'Quel est le verset le plus célèbre de la Bible ?', '["Genèse 1:1","Jean 3:16 - Car Dieu a tant aimé le monde qu''il a donné son Fils unique","Psaume 23:1","Exode 20:1"]', 1, 'Car Dieu a tant aimé le monde qu''il a donné son Fils unique.', 'Jean 3:16'),
('joh_033', 'JHN', 'Pourquoi Dieu a-t-il envoyé son Fils ?', '["Pour condamner le monde","Pour que le monde soit sauvé par lui","Pour juger les pécheurs","Pour détruire les méchants"]', 1, 'Dieu n''a pas envoyé son Fils pour juger, mais pour que le monde soit sauvé.', 'Jean 3:17'),
('joh_034', 'JHN', 'Pourquoi les hommes sont-ils condamnés ?', '["Leurs œuvres","Ils n''ont pas cru au nom du Fils unique de Dieu","Leur naissance","Leur ignorance"]', 1, 'Celui qui ne croit pas est déjà jugé parce qu''il n''a pas cru.', 'Jean 3:18'),
('joh_035', 'JHN', 'Pourquoi les hommes préfèrent-ils les ténèbres ?', '["Par hasard","Parce que leurs œuvres sont mauvaises","Par ignorance","Par tradition"]', 1, 'Les hommes ont préféré les ténèbres parce que leurs œuvres étaient mauvaises.', 'Jean 3:19'),
('joh_036', 'JHN', 'Que dit Jean-Baptiste sur lui-même et le Christ ?', '["Nous sommes égaux","Il faut qu''il croisse et que je diminue","Je suis plus grand","Nous sommes différents"]', 1, 'Il faut qu''il croisse, et que je diminue.', 'Jean 3:30'),

-- La Samaritaine et le fils de l'officier (Chapitre 4)
('joh_037', 'JHN', 'Pourquoi Jésus devait-il passer par la Samarie ?', '["C''était le chemin le plus court","Il devait (nécessité divine) y passer","Par hasard","Pour éviter Jérusalem"]', 1, 'Il lui fallait passer par la Samarie - une nécessité divine.', 'Jean 4:4'),
('joh_038', 'JHN', 'Où Jésus rencontra-t-il la femme samaritaine ?', '["Au temple","Près du puits de Jacob, à Sychar","Dans une maison","Au marché"]', 1, 'Il arriva près du puits de Jacob, où la Samaritaine vint puiser.', 'Jean 4:5-6'),
('joh_039', 'JHN', 'Pourquoi la femme fut-elle surprise que Jésus lui parle ?', '["Il était prophète","Les Juifs n''avaient pas de relations avec les Samaritains","Il était fatigué","Elle ne le connaissait pas"]', 1, 'Les Juifs n''ont pas de relations avec les Samaritains.', 'Jean 4:9'),
('joh_040', 'JHN', 'Quelle eau Jésus offrit-il ?', '["L''eau du puits","L''eau vive qui devient une source jaillissant en vie éternelle","L''eau de pluie","L''eau du Jourdain"]', 1, 'L''eau que je lui donnerai deviendra une source jaillissant jusque dans la vie éternelle.', 'Jean 4:14'),
('joh_041', 'JHN', 'Que révéla Jésus sur la vie personnelle de la femme ?', '["Rien","Elle avait eu cinq maris et vivait avec un homme qui n''était pas son mari","Ses péchés","Son avenir"]', 1, 'Jésus connaissait toute sa vie passée.', 'Jean 4:18'),
('joh_042', 'JHN', 'Comment les vrais adorateurs adoreront-ils le Père ?', '["Sur cette montagne","En esprit et en vérité","À Jérusalem seulement","Dans le temple"]', 1, 'Les vrais adorateurs adoreront le Père en esprit et en vérité.', 'Jean 4:23'),
('joh_043', 'JHN', 'Qu''est Dieu selon cette conversation ?', '["Un être lointain","Dieu est Esprit","Un juge sévère","Un roi"]', 1, 'Dieu est Esprit, et il faut que ceux qui l''adorent l''adorent en esprit et en vérité.', 'Jean 4:24'),
('joh_044', 'JHN', 'Que dit Jésus quand la femme mentionna le Messie ?', '["Il viendra bientôt","Je le suis, moi qui te parle","Je le connais","Attends-le"]', 1, 'Jésus révéla clairement son identité messianique.', 'Jean 4:26'),
('joh_045', 'JHN', 'Quelle était la nourriture de Jésus ?', '["Le pain","Faire la volonté de celui qui m''a envoyé et accomplir son œuvre","La viande","Les fruits"]', 1, 'Ma nourriture est de faire la volonté de celui qui m''a envoyé.', 'Jean 4:34'),
('joh_046', 'JHN', 'Combien de jours Jésus resta-t-il en Samarie ?', '["Un jour","Deux jours","Une semaine","Un mois"]', 1, 'Il demeura là deux jours, et beaucoup crurent.', 'Jean 4:40'),
('joh_047', 'JHN', 'Qui Jésus guérit-il à distance à Cana ?', '["Un lépreux","Le fils d''un officier du roi","Un aveugle","Un paralytique"]', 1, 'Jésus dit: Ton fils vit. Et le garçon fut guéri à cette heure-là.', 'Jean 4:50-53'),

-- La piscine de Béthesda et le Fils et le Père (Chapitre 5)
('joh_048', 'JHN', 'Où Jésus guérit-il un paralytique depuis 38 ans ?', '["Au temple","À la piscine de Béthesda","Au Jourdain","À Siloé"]', 1, 'Il y avait là un homme malade depuis trente-huit ans.', 'Jean 5:5'),
('joh_049', 'JHN', 'Quel jour Jésus guérit-il cet homme ?', '["Un jour de fête","Le jour du sabbat","Le premier jour de la semaine","Un jour ordinaire"]', 1, 'Or, c''était un jour de sabbat.', 'Jean 5:9'),
('joh_050', 'JHN', 'Pourquoi les Juifs persécutèrent-ils Jésus ?', '["Il blasphéma","Il guérissait le jour du sabbat et se faisait égal à Dieu","Il désobéissait à la loi","Il était étranger"]', 1, 'Il violait le sabbat et appelait Dieu son propre Père.', 'Jean 5:18'),
('joh_051', 'JHN', 'Que peut faire le Fils par lui-même ?', '["Tout","Rien - il ne fait que ce qu''il voit faire au Père","Quelques choses","Les miracles seulement"]', 1, 'Le Fils ne peut rien faire de lui-même, il ne fait que ce qu''il voit faire au Père.', 'Jean 5:19'),
('joh_052', 'JHN', 'Qui a la vie en lui-même ?', '["Les prophètes","Le Père et le Fils","Les anges","Les justes"]', 1, 'Comme le Père a la vie en lui-même, il a donné au Fils d''avoir la vie en lui-même.', 'Jean 5:26'),
('joh_053', 'JHN', 'Qui témoigne en faveur de Jésus ?', '["Les disciples seulement","Jean-Baptiste, les œuvres, le Père et les Écritures","Moïse seulement","Les pharisiens"]', 1, 'Quatre témoins sont cités: Jean, les œuvres, le Père et les Écritures.', 'Jean 5:33-39'),
('joh_054', 'JHN', 'De qui les Écritures rendent-elles témoignage ?', '["De Moïse","De moi (Jésus)","Des prophètes","D''Israël"]', 1, 'Vous sondez les Écritures... ce sont elles qui rendent témoignage de moi.', 'Jean 5:39'),

-- La multiplication des pains et le Pain de vie (Chapitre 6)
('joh_055', 'JHN', 'Combien de pains d''orge un garçon avait-il ?', '["Sept","Cinq pains et deux poissons","Trois","Dix"]', 1, 'Il y a ici un jeune garçon qui a cinq pains d''orge et deux poissons.', 'Jean 6:9'),
('joh_056', 'JHN', 'Combien d''hommes furent nourris (sans compter femmes et enfants) ?', '["Mille","Environ cinq mille hommes","Trois mille","Dix mille"]', 1, 'Ces hommes étaient au nombre d''environ cinq mille.', 'Jean 6:10'),
('joh_057', 'JHN', 'Combien de paniers de restes furent ramassés ?', '["Sept","Douze paniers pleins","Cinq","Aucun"]', 1, 'Ils remplirent douze paniers avec les morceaux des cinq pains d''orge.', 'Jean 6:13'),
('joh_058', 'JHN', 'Comment Jésus rejoignit-il les disciples sur la mer ?', '["En bateau","En marchant sur l''eau","En nageant","En volant"]', 1, 'Ils virent Jésus marchant sur la mer.', 'Jean 6:19'),
('joh_059', 'JHN', 'Quelle œuvre de Dieu les gens devaient-ils accomplir ?', '["Garder la loi","Croire en celui qu''il a envoyé","Faire des sacrifices","Payer la dîme"]', 1, 'L''œuvre de Dieu, c''est que vous croyiez en celui qu''il a envoyé.', 'Jean 6:29'),
('joh_060', 'JHN', 'Quel est le vrai pain du ciel ?', '["La manne","Celui qui descend du ciel et donne la vie au monde (Jésus)","Le pain quotidien","La Torah"]', 1, 'Le vrai pain du ciel est celui qui descend du ciel et donne la vie.', 'Jean 6:32-33'),
('joh_061', 'JHN', 'Que dit Jésus de lui-même concernant le pain ?', '["Je fais du pain","Je suis le pain de vie","Je mange du pain","Je donne du pain"]', 1, 'Je suis le pain de vie. Celui qui vient à moi n''aura jamais faim.', 'Jean 6:35'),
('joh_062', 'JHN', 'Que fera Jésus au dernier jour pour ceux qui croient ?', '["Rien","Les ressuscitera","Les jugera","Les récompensera"]', 1, 'Je le ressusciterai au dernier jour.', 'Jean 6:40'),
('joh_063', 'JHN', 'Que signifie manger la chair et boire le sang de Jésus ?', '["Un rite cannibale","Croire en lui et s''approprier son sacrifice","Manger du pain spécial","Un sacrifice animal"]', 1, 'C''est une métaphore pour croire pleinement en lui et son sacrifice.', 'Jean 6:53-56'),
('joh_064', 'JHN', 'Quelle réaction beaucoup de disciples eurent-ils à cet enseignement ?', '["Ils acceptèrent tout","Ils trouvèrent cette parole dure et se retirèrent","Ils applaudirent","Ils restèrent silencieux"]', 1, 'Beaucoup de ses disciples se retirèrent et cessèrent de le suivre.', 'Jean 6:66'),
('joh_065', 'JHN', 'Que dit Pierre quand Jésus demanda si les Douze voulaient partir ?', '["Nous restons par obligation","Seigneur, à qui irions-nous? Tu as les paroles de la vie éternelle","Nous ne savons pas","Peut-être"]', 1, 'Tu as les paroles de la vie éternelle. Nous croyons que tu es le Saint de Dieu.', 'Jean 6:68-69'),

-- La fête des Tabernacles et la femme adultère (Chapitres 7-8)
('joh_066', 'JHN', 'Pourquoi Jésus ne voulut-il pas d''abord monter à la fête ?', '["Fatigue","Son temps n''était pas encore venu","Peur","Maladie"]', 1, 'Mon temps n''est pas encore venu.', 'Jean 7:6'),
('joh_067', 'JHN', 'Que dit Jésus au milieu de la fête ?', '["Rien de spécial","Si quelqu''un a soif, qu''il vienne à moi et qu''il boive","Adorez Dieu","Repentez-vous"]', 1, 'Si quelqu''un a soif, qu''il vienne à moi et qu''il boive.', 'Jean 7:37'),
('joh_068', 'JHN', 'De quoi parla Jésus concernant les fleuves d''eau vive ?', '["D''eau littérale","Du Saint-Esprit que recevraient les croyants","De la pluie","Du Jourdain"]', 1, 'Il dit cela de l''Esprit que devaient recevoir ceux qui croiraient.', 'Jean 7:39'),
('joh_069', 'JHN', 'Pourquoi les gardes n''arrêtèrent-ils pas Jésus ?', '["Ils avaient peur","Jamais homme n''a parlé comme cet homme","Ils l''admiraient","Ordre annulé"]', 1, 'Les gardes furent impressionnés par ses paroles.', 'Jean 7:46'),
('joh_070', 'JHN', 'Qu''écrivit Jésus sur le sol quand on lui amena la femme adultère ?', '["Le texte n''est pas précisé","Nous ne savons pas exactement","Les péchés des accusateurs","La loi"]', 1, 'Jésus écrivit sur le sol, mais le texte ne précise pas quoi.', 'Jean 8:6'),
('joh_071', 'JHN', 'Qui devait jeter la première pierre ?', '["Le plus vieux","Celui qui est sans péché parmi vous","Le plus jeune","Le sacrificateur"]', 1, 'Que celui d''entre vous qui est sans péché jette le premier la pierre.', 'Jean 8:7'),
('joh_072', 'JHN', 'Que dit Jésus à la femme adultère ?', '["Tu es pardonnée","Je ne te condamne pas non plus; va et ne pèche plus","Tu es punie","Va-t''en"]', 1, 'Je ne te condamne pas non plus; va et ne pèche plus.', 'Jean 8:11'),
('joh_073', 'JHN', 'Que déclara Jésus être pour le monde ?', '["Un prophète","La lumière du monde","Un enseignant","Un roi"]', 1, 'Je suis la lumière du monde. Celui qui me suit ne marchera pas dans les ténèbres.', 'Jean 8:12'),
('joh_074', 'JHN', 'Qu''est-ce qui rend libre selon Jésus ?', '["L''argent","La vérité vous rendra libres","La loi","La force"]', 1, 'Vous connaîtrez la vérité, et la vérité vous rendra libres.', 'Jean 8:32'),
('joh_075', 'JHN', 'Qui est esclave selon Jésus ?', '["Le pauvre","Quiconque commet le péché est esclave du péché","Le prisonnier","L''étranger"]', 1, 'Quiconque commet le péché est esclave du péché.', 'Jean 8:34'),
('joh_076', 'JHN', 'Que prétendit Jésus sur son existence ?', '["J''ai 30 ans","Avant qu''Abraham fût, je suis","Je suis vieux","Je suis jeune"]', 1, 'En vérité, avant qu''Abraham fût, je suis.', 'Jean 8:58'),

-- L'aveugle-né et le Bon Berger (Chapitres 9-10)
('joh_077', 'JHN', 'Pourquoi cet homme était-il né aveugle ?', '["À cause de ses péchés","Pour que les œuvres de Dieu soient manifestées en lui","À cause des péchés de ses parents","Par malédiction"]', 1, 'C''est afin que les œuvres de Dieu soient manifestées en lui.', 'Jean 9:3'),
('joh_078', 'JHN', 'Comment Jésus guérit-il l''aveugle-né ?', '["Par une parole","En faisant de la boue et en l''envoyant se laver à Siloé","En touchant ses yeux","Par la prière"]', 1, 'Jésus fit de la boue, l''appliqua sur ses yeux et l''envoya se laver.', 'Jean 9:6-7'),
('joh_079', 'JHN', 'Que dit l''homme guéri aux pharisiens sur Jésus ?', '["Je ne sais pas","C''est un prophète","C''est un pécheur","C''est un magicien"]', 1, 'L''homme dit: C''est un prophète.', 'Jean 9:17'),
('joh_080', 'JHN', 'Que répondit l''homme aux pharisiens qui niaient Jésus ?', '["Vous avez raison","Je ne sais qu''une chose: j''étais aveugle et maintenant je vois","Je ne comprends pas","Laissez-moi"]', 1, 'Son témoignage était simple mais puissant.', 'Jean 9:25'),
('joh_081', 'JHN', 'Comment entre-t-on dans la bergerie ?', '["Par n''importe quelle ouverture","Par la porte - Jésus est la porte","Par le toit","Par derrière"]', 1, 'Je suis la porte des brebis.', 'Jean 10:7'),
('joh_082', 'JHN', 'Pourquoi Jésus est-il venu selon Jean 10 ?', '["Pour juger","Pour que les brebis aient la vie et l''aient en abondance","Pour régner","Pour punir"]', 1, 'Je suis venu afin que les brebis aient la vie et qu''elles l''aient en abondance.', 'Jean 10:10'),
('joh_083', 'JHN', 'Que fait le bon berger pour ses brebis ?', '["Il les vend","Il donne sa vie pour ses brebis","Il les abandonne","Il les bat"]', 1, 'Le bon berger donne sa vie pour ses brebis.', 'Jean 10:11'),
('joh_084', 'JHN', 'Que fait le mercenaire quand le loup vient ?', '["Il protège les brebis","Il s''enfuit et abandonne les brebis","Il combat","Il appelle au secours"]', 1, 'Le mercenaire s''enfuit parce qu''il n''est qu''un mercenaire.', 'Jean 10:12-13'),
('joh_085', 'JHN', 'Y a-t-il d''autres brebis que Jésus doit amener ?', '["Non","Oui, d''autres brebis qui ne sont pas de cette bergerie (les païens)","Peut-être","On ne sait pas"]', 1, 'J''ai encore d''autres brebis qui ne sont pas de cette bergerie.', 'Jean 10:16'),
('joh_086', 'JHN', 'Qui peut arracher les brebis de la main de Jésus ?', '["Le diable","Personne","Les loups","Les voleurs"]', 1, 'Personne ne les ravira de ma main.', 'Jean 10:28'),
('joh_087', 'JHN', 'Que déclara Jésus sur sa relation avec le Père ?', '["Nous sommes proches","Moi et le Père nous sommes un","Nous sommes différents","Nous sommes amis"]', 1, 'Moi et le Père nous sommes un.', 'Jean 10:30'),

-- Lazare et entrée à Jérusalem (Chapitres 11-12)
('joh_088', 'JHN', 'Qui étaient Lazare, Marthe et Marie ?', '["Des inconnus","Des amis de Jésus à Béthanie","Des disciples","Des pharisiens"]', 1, 'Lazare et ses sœurs étaient des amis proches de Jésus.', 'Jean 11:1-5'),
('joh_089', 'JHN', 'Depuis combien de temps Lazare était-il mort quand Jésus arriva ?', '["Un jour","Quatre jours","Une semaine","Deux jours"]', 1, 'Lazare était dans le sépulcre depuis quatre jours.', 'Jean 11:17'),
('joh_090', 'JHN', 'Que déclara Jésus à Marthe ?', '["Désolé pour ta perte","Je suis la résurrection et la vie","Il est en paix","Ne pleure pas"]', 1, 'Je suis la résurrection et la vie; celui qui croit en moi vivra.', 'Jean 11:25'),
('joh_091', 'JHN', 'Quel est le verset le plus court de la Bible (dans certaines versions) ?', '["Dieu est amour","Jésus pleura","Priez sans cesse","Réjouissez-vous"]', 1, 'Jésus pleura devant le tombeau de Lazare.', 'Jean 11:35'),
('joh_092', 'JHN', 'Que cria Jésus devant le tombeau ?', '["Adieu, Lazare","Lazare, sors!","Reviens à la vie","Réveille-toi"]', 1, 'Il cria d''une voix forte: Lazare, sors!', 'Jean 11:43'),
('joh_093', 'JHN', 'Pourquoi les chefs religieux décidèrent-ils de tuer Jésus ?', '["Il blasphémait","De peur que tous croient en lui et que les Romains interviennent","Il était populaire","Il était dangereux"]', 1, 'Ils craignaient que tout le monde croie en lui.', 'Jean 11:48'),
('joh_094', 'JHN', 'Que fit Marie de Béthanie pour Jésus ?', '["Elle cuisina","Elle oignit ses pieds d''un parfum précieux et les essuya avec ses cheveux","Elle chanta","Elle pria"]', 1, 'Marie oignit les pieds de Jésus d''un parfum de nard pur très coûteux.', 'Jean 12:3'),
('joh_095', 'JHN', 'Qui critiqua cette onction ?', '["Pierre","Judas Iscariote","Thomas","Jean"]', 1, 'Judas dit qu''on aurait pu vendre ce parfum pour aider les pauvres.', 'Jean 12:4-5'),
('joh_096', 'JHN', 'Que criait la foule lors de l''entrée triomphale ?', '["Vive le roi!","Hosanna! Béni soit celui qui vient au nom du Seigneur, le roi d''Israël!","Gloire!","Victoire!"]', 1, 'Hosanna! Béni soit le roi d''Israël qui vient au nom du Seigneur!', 'Jean 12:13'),
('joh_097', 'JHN', 'Que doit-il arriver au grain de blé pour porter du fruit ?', '["Être planté","Tomber en terre et mourir","Être arrosé","Être au soleil"]', 1, 'Si le grain de blé ne tombe en terre et ne meurt, il reste seul.', 'Jean 12:24'),
('joh_098', 'JHN', 'Qui sera attiré à Jésus quand il sera élevé ?', '["Les Juifs seulement","Tous les hommes","Les justes seulement","Personne"]', 1, 'Quand j''aurai été élevé de la terre, j''attirerai tous les hommes à moi.', 'Jean 12:32'),

-- Le lavement des pieds et le discours d'adieu (Chapitres 13-14)
('joh_099', 'JHN', 'Que fit Jésus avant le repas de la Pâque ?', '["Il pria","Il lava les pieds de ses disciples","Il bénit le pain","Il chanta"]', 1, 'Jésus se mit à laver les pieds des disciples.', 'Jean 13:5'),
('joh_100', 'JHN', 'Pourquoi Pierre refusa-t-il d''abord ?', '["Il avait honte","Il ne voulait pas que son Seigneur lui lave les pieds","Il était pressé","Il était malade"]', 1, 'Pierre dit: Non, jamais tu ne me laveras les pieds!', 'Jean 13:8'),
('joh_101', 'JHN', 'Quel nouveau commandement Jésus donna-t-il ?', '["Priez toujours","Aimez-vous les uns les autres comme je vous ai aimés","Jeûnez souvent","Donnez aux pauvres"]', 1, 'Je vous donne un commandement nouveau: Aimez-vous les uns les autres.', 'Jean 13:34'),
('joh_102', 'JHN', 'Comment les gens reconnaîtront-ils les disciples de Jésus ?', '["Par leurs vêtements","Par l''amour qu''ils auront les uns pour les autres","Par leurs miracles","Par leur richesse"]', 1, 'À ceci tous connaîtront que vous êtes mes disciples: si vous avez de l''amour.', 'Jean 13:35'),
('joh_103', 'JHN', 'Où Jésus dit-il aller préparer ?', '["Un royaume","Une place dans la maison du Père","Un temple","Une ville"]', 1, 'Je vais vous préparer une place dans la maison de mon Père.', 'Jean 14:2'),
('joh_104', 'JHN', 'Que déclara Jésus être exclusivement ?', '["Un prophète","Le chemin, la vérité et la vie","Un enseignant","Un roi"]', 1, 'Je suis le chemin, la vérité et la vie. Nul ne vient au Père que par moi.', 'Jean 14:6'),
('joh_105', 'JHN', 'Que voit celui qui a vu Jésus ?', '["Un homme","Le Père","Un prophète","Un roi"]', 1, 'Celui qui m''a vu a vu le Père.', 'Jean 14:9'),
('joh_106', 'JHN', 'Qui le Père enverra-t-il au nom de Jésus ?', '["Des anges","Le Consolateur, le Saint-Esprit","Des prophètes","Des signes"]', 1, 'Le Père enverra le Consolateur, le Saint-Esprit, en mon nom.', 'Jean 14:26'),
('joh_107', 'JHN', 'Quelle paix Jésus donne-t-il ?', '["La paix politique","Sa paix, différente de celle du monde","La paix militaire","Aucune"]', 1, 'Je vous laisse la paix, je vous donne ma paix - pas comme le monde la donne.', 'Jean 14:27'),

-- La vigne et le Saint-Esprit (Chapitres 15-16)
('joh_108', 'JHN', 'Que dit Jésus être ?', '["Un arbre fruitier","La vraie vigne, et le Père est le vigneron","Le jardinier","Un fruit"]', 1, 'Je suis la vraie vigne, et mon Père est le vigneron.', 'Jean 15:1'),
('joh_109', 'JHN', 'Que peut faire le sarment sans la vigne ?', '["Peu de fruit","Rien du tout","Quelques fruits","Survivre"]', 1, 'Sans moi vous ne pouvez rien faire.', 'Jean 15:5'),
('joh_110', 'JHN', 'Comment demeurer dans l''amour de Jésus ?', '["Par des rituels","En gardant ses commandements","Par des sacrifices","Par la méditation"]', 1, 'Si vous gardez mes commandements, vous demeurerez dans mon amour.', 'Jean 15:10'),
('joh_111', 'JHN', 'Quel est le plus grand amour ?', '["Aimer ses amis","Donner sa vie pour ses amis","Aimer sa famille","Aimer Dieu"]', 1, 'Il n''y a pas de plus grand amour que de donner sa vie pour ses amis.', 'Jean 15:13'),
('joh_112', 'JHN', 'Pourquoi le monde haïra-t-il les disciples ?', '["Leur faiblesse","Parce qu''ils ne sont pas du monde, comme Jésus n''est pas du monde","Leur pauvreté","Leur ignorance"]', 1, 'Parce que vous n''êtes pas du monde, le monde vous hait.', 'Jean 15:19'),
('joh_113', 'JHN', 'Qui convaincra le monde de péché, de justice et de jugement ?', '["Les disciples","Le Saint-Esprit, l''Esprit de vérité","Les prophètes","Les anges"]', 1, 'Quand il sera venu, il convaincra le monde de péché, de justice et de jugement.', 'Jean 16:8'),
('joh_114', 'JHN', 'Dans quoi l''Esprit de vérité conduira-t-il ?', '["La richesse","Toute la vérité","Le pouvoir","La gloire"]', 1, 'L''Esprit de vérité vous conduira dans toute la vérité.', 'Jean 16:13'),
('joh_115', 'JHN', 'Que dit Jésus sur la tristesse des disciples ?', '["Elle durera toujours","Leur tristesse se changera en joie que personne ne leur ravira","Elle est inutile","Elle est punition"]', 1, 'Votre tristesse se changera en joie... et personne ne vous ravira votre joie.', 'Jean 16:20,22'),

-- La prière sacerdotale (Chapitre 17)
('joh_116', 'JHN', 'Qu''est-ce que la vie éternelle selon Jésus ?', '["Vivre longtemps","Connaître le seul vrai Dieu et Jésus-Christ qu''il a envoyé","Être au ciel","Être riche"]', 1, 'La vie éternelle, c''est qu''ils te connaissent, toi le seul vrai Dieu.', 'Jean 17:3'),
('joh_117', 'JHN', 'Pourquoi Jésus prie-t-il pour ses disciples ?', '["Leur richesse","Qu''ils soient un comme le Père et lui sont un","Leur santé","Leur pouvoir"]', 1, 'Afin que tous soient un, comme toi, Père, tu es en moi, et comme je suis en toi.', 'Jean 17:21'),
('joh_118', 'JHN', 'Jésus prie-t-il seulement pour les disciples présents ?', '["Oui","Non, aussi pour ceux qui croiront par leur parole","Seulement pour Pierre","Seulement pour Jean"]', 1, 'Je ne prie pas pour eux seulement, mais aussi pour ceux qui croiront en moi.', 'Jean 17:20'),

-- L'arrestation, le procès et la crucifixion (Chapitres 18-19)
('joh_119', 'JHN', 'Où Jésus fut-il arrêté ?', '["Au temple","Dans un jardin au-delà du torrent du Cédron","Chez Pierre","À Bethléem"]', 1, 'Jésus sortit avec ses disciples au-delà du torrent du Cédron, où il y avait un jardin.', 'Jean 18:1'),
('joh_120', 'JHN', 'Que se passa-t-il quand Jésus dit "C''est moi" aux soldats ?', '["Ils l''arrêtèrent","Ils reculèrent et tombèrent par terre","Ils s''enfuirent","Ils l''adorèrent"]', 1, 'Quand Jésus dit: C''est moi, ils reculèrent et tombèrent.', 'Jean 18:6'),
('joh_121', 'JHN', 'Qui coupa l''oreille du serviteur du grand prêtre ?', '["Jean","Pierre","Jacques","Thomas"]', 1, 'Simon Pierre avait une épée; il la tira et frappa Malchus.', 'Jean 18:10'),
('joh_122', 'JHN', 'Devant quel gouverneur romain Jésus fut-il amené ?', '["César","Ponce Pilate","Hérode","Agrippa"]', 1, 'Ils conduisirent Jésus au prétoire vers Pilate.', 'Jean 18:28'),
('joh_123', 'JHN', 'Que répondit Jésus quand Pilate demanda s''il était roi ?', '["Non","Mon royaume n''est pas de ce monde","Oui, le roi des Juifs","Je ne sais pas"]', 1, 'Mon royaume n''est pas de ce monde.', 'Jean 18:36'),
('joh_124', 'JHN', 'Que demanda Pilate cyniquement ?', '["Qui es-tu?","Qu''est-ce que la vérité?","Es-tu coupable?","Que veux-tu?"]', 1, 'Pilate lui dit: Qu''est-ce que la vérité?', 'Jean 18:38'),
('joh_125', 'JHN', 'Que cria la foule quand Pilate proposa de relâcher Jésus ?', '["Libère-le!","Crucifie-le! Donne-nous Barabbas!","Épargne-le!","Nous le voulons!"]', 1, 'Ils crièrent: Non pas lui, mais Barabbas!', 'Jean 18:40'),
('joh_126', 'JHN', 'Que dit Pilate en présentant Jésus flagellé ?', '["Voici le roi","Ecce homo - Voici l''homme","Voici le coupable","Voici le prophète"]', 1, 'Pilate dit: Voici l''homme!', 'Jean 19:5'),
('joh_127', 'JHN', 'Quelle inscription fut placée sur la croix ?', '["Roi des Juifs","Jésus de Nazareth, roi des Juifs - en hébreu, latin et grec","Le Messie","Le condamné"]', 1, 'JÉSUS DE NAZARETH, LE ROI DES JUIFS.', 'Jean 19:19'),
('joh_128', 'JHN', 'À qui Jésus confia-t-il sa mère depuis la croix ?', '["À ses frères","Au disciple qu''il aimait (Jean)","À Pierre","À Jacques"]', 1, 'Jésus dit au disciple: Voici ta mère. Et dès ce moment, il la prit chez lui.', 'Jean 19:26-27'),
('joh_129', 'JHN', 'Quelles furent les dernières paroles de Jésus selon Jean ?', '["Mon Dieu, pourquoi?","Tout est accompli","Père, pardonne-leur","Je remets mon esprit"]', 1, 'Jésus dit: Tout est accompli. Et il rendit l''esprit.', 'Jean 19:30'),
('joh_130', 'JHN', 'Qu''est-ce qui sortit du côté de Jésus quand il fut percé ?', '["Rien","Du sang et de l''eau","De l''eau seulement","Du sang seulement"]', 1, 'Un soldat lui perça le côté, et il sortit du sang et de l''eau.', 'Jean 19:34'),

-- La Résurrection et les apparitions (Chapitres 20-21)
('joh_131', 'JHN', 'Qui arriva premier au tombeau le premier jour de la semaine ?', '["Pierre","Marie de Magdala","Jean","Les gardes"]', 1, 'Marie de Magdala se rendit au sépulcre de bon matin.', 'Jean 20:1'),
('joh_132', 'JHN', 'Que vit Pierre dans le tombeau ?', '["Jésus","Les linges posés et le linge de la tête roulé à part","Rien","Des anges"]', 1, 'Il vit les bandes posées et le linge séparé, roulé dans un autre endroit.', 'Jean 20:6-7'),
('joh_133', 'JHN', 'Pour qui Marie prit-elle Jésus ressuscité ?', '["Un disciple","Le jardinier","Un ange","Un garde"]', 1, 'Marie pensait que c''était le jardinier.', 'Jean 20:15'),
('joh_134', 'JHN', 'Comment Jésus se révéla-t-il à Marie ?', '["En montrant ses plaies","En l''appelant par son nom: Marie!","En faisant un miracle","En lui touchant l''épaule"]', 1, 'Jésus lui dit: Marie! Elle se retourna et dit: Rabbouni!', 'Jean 20:16'),
('joh_135', 'JHN', 'Que dit Jésus aux disciples enfermés par peur des Juifs ?', '["N''ayez pas peur","La paix soit avec vous!","Sortez d''ici","Croyez en moi"]', 1, 'Jésus vint et dit: La paix soit avec vous!', 'Jean 20:19'),
('joh_136', 'JHN', 'Que Jésus souffla-t-il sur les disciples ?', '["Du courage","Le Saint-Esprit - Recevez le Saint-Esprit","La force","La sagesse"]', 1, 'Il souffla sur eux et dit: Recevez le Saint-Esprit.', 'Jean 20:22'),
('joh_137', 'JHN', 'Pourquoi Thomas douta-t-il ?', '["Il n''aimait pas Jésus","Il n''était pas présent lors de la première apparition et voulait voir les marques","Il était incrédule par nature","Il avait peur"]', 1, 'Thomas voulait voir et toucher les marques des clous.', 'Jean 20:25'),
('joh_138', 'JHN', 'Que dit Thomas quand il vit Jésus ?', '["Je ne crois pas","Mon Seigneur et mon Dieu!","Pardon de douter","C''est toi!"]', 1, 'Thomas répondit: Mon Seigneur et mon Dieu!', 'Jean 20:28'),
('joh_139', 'JHN', 'Pourquoi l''évangile de Jean fut-il écrit ?', '["Pour raconter une histoire","Afin que vous croyiez que Jésus est le Christ et que vous ayez la vie","Pour enseigner l''histoire","Pour critiquer les Juifs"]', 1, 'Ces choses ont été écrites afin que vous croyiez et que vous ayez la vie en son nom.', 'Jean 20:31'),
('joh_140', 'JHN', 'Combien de poissons furent pris lors de la pêche miraculeuse après la résurrection ?', '["Beaucoup","153 gros poissons","100","200"]', 1, 'Le filet était plein de cent cinquante-trois gros poissons.', 'Jean 21:11'),
('joh_141', 'JHN', 'Combien de fois Jésus demanda-t-il à Pierre s''il l''aimait ?', '["Une fois","Trois fois","Deux fois","Quatre fois"]', 1, 'Jésus demanda trois fois: Simon, m''aimes-tu? - une fois pour chaque reniement.', 'Jean 21:15-17'),
('joh_142', 'JHN', 'Quelle mission Jésus confia-t-il à Pierre ?', '["Pêcher","Pais mes brebis - prendre soin du troupeau de Christ","Prêcher aux païens","Écrire un livre"]', 1, 'Jésus dit: Pais mes brebis.', 'Jean 21:17'),
('joh_143', 'JHN', 'Comment Jean termine-t-il son évangile ?', '["Par une bénédiction","En disant que Jésus a fait tant de choses que le monde ne pourrait contenir les livres","Par une prière","Par un chant"]', 1, 'Si on les écrivait en détail, le monde ne pourrait contenir les livres.', 'Jean 21:25');

