-- Questions pour l'Évangile selon Matthieu
-- La vie et l'enseignement de Jésus-Christ

-- Généalogie et naissance de Jésus (Chapitres 1-2)
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation, verse_ref) VALUES
('mat_001', 'MAT', 'Comment commence l''évangile de Matthieu ?', '["Par la création","Par la généalogie de Jésus-Christ, fils de David, fils d''Abraham","Par le ministère de Jean-Baptiste","Par le baptême de Jésus"]', 1, 'Matthieu commence par établir la lignée royale de Jésus.', 'Matthieu 1:1'),
('mat_002', 'MAT', 'Combien de générations y a-t-il d''Abraham à David, de David à l''exil, et de l''exil à Christ ?', '["10, 10, 10","12, 12, 12","14, 14, 14","7, 7, 7"]', 2, 'Quatorze générations pour chaque période.', 'Matthieu 1:17'),
('mat_003', 'MAT', 'Qui était le fiancé de Marie ?', '["Jean","Joseph, descendant de David","Pierre","Zacharie"]', 1, 'Joseph était fiancé à Marie avant la naissance de Jésus.', 'Matthieu 1:18'),
('mat_004', 'MAT', 'Que voulait faire Joseph quand il apprit que Marie était enceinte ?', '["La dénoncer publiquement","La renvoyer secrètement, ne voulant pas la diffamer","L''épouser immédiatement","Fuir en Égypte"]', 1, 'Joseph, homme juste, voulait la répudier en secret.', 'Matthieu 1:19'),
('mat_005', 'MAT', 'Comment Joseph apprit-il la vérité sur la grossesse de Marie ?', '["Par Marie","Par un ange dans un songe","Par un prophète","Par Elisabeth"]', 1, 'Un ange du Seigneur lui apparut en songe.', 'Matthieu 1:20'),
('mat_006', 'MAT', 'Que signifie le nom "Jésus" ?', '["Roi des Juifs","Il sauvera son peuple de ses péchés","Fils de Dieu","Emmanuel"]', 1, 'Le nom Jésus signifie "l''Éternel sauve".', 'Matthieu 1:21'),
('mat_007', 'MAT', 'Quelle prophétie d''Ésaïe est citée concernant la naissance de Jésus ?', '["Une étoile sortira de Jacob","La vierge concevra et enfantera un fils, Emmanuel","Un rameau sortira d''Isaï","Il sera appelé Nazaréen"]', 1, 'La prophétie d''Emmanuel, Dieu avec nous.', 'Matthieu 1:23'),
('mat_008', 'MAT', 'D''où venaient les mages ?', '["De Rome","De l''Orient","D''Égypte","De Grèce"]', 1, 'Des mages d''Orient vinrent à Jérusalem.', 'Matthieu 2:1'),
('mat_009', 'MAT', 'Que cherchaient les mages ?', '["Un prophète","Le roi des Juifs qui vient de naître","Un grand prêtre","Un guerrier"]', 1, 'Ils cherchaient le roi des Juifs dont ils avaient vu l''étoile.', 'Matthieu 2:2'),
('mat_010', 'MAT', 'Comment Hérode réagit-il à la nouvelle de la naissance du roi des Juifs ?', '["Avec joie","Troublé, ainsi que tout Jérusalem","Avec indifférence","Avec générosité"]', 1, 'Hérode fut troublé et tout Jérusalem avec lui.', 'Matthieu 2:3'),
('mat_011', 'MAT', 'Selon les scribes, où le Christ devait-il naître ?', '["Nazareth","Bethléem de Judée, selon la prophétie de Michée","Jérusalem","Capernaüm"]', 1, 'La prophétie de Michée indiquait Bethléem.', 'Matthieu 2:5-6'),
('mat_012', 'MAT', 'Quels cadeaux les mages offrirent-ils à Jésus ?', '["Argent, vêtements et nourriture","Or, encens et myrrhe","Pierres précieuses","Bétail et récoltes"]', 1, 'Or (royauté), encens (divinité) et myrrhe (mort).', 'Matthieu 2:11'),
('mat_013', 'MAT', 'Comment les mages repartirent-ils ?', '["Par Jérusalem comme Hérode l''avait demandé","Par un autre chemin, avertis en songe","Directement chez eux","Par l''Égypte"]', 1, 'Divinement avertis en songe de ne pas retourner vers Hérode.', 'Matthieu 2:12'),
('mat_014', 'MAT', 'Où Joseph emmena-t-il la famille pour fuir Hérode ?', '["À Nazareth","En Égypte","À Bethléem","En Galilée"]', 1, 'Joseph prit l''enfant et sa mère de nuit et partit pour l''Égypte.', 'Matthieu 2:14'),
('mat_015', 'MAT', 'Quelle prophétie fut accomplie par la fuite en Égypte ?', '["Une étoile sortira de Jacob","J''ai appelé mon fils hors d''Égypte","Il sera appelé Nazaréen","Une voix s''élève à Rama"]', 1, 'D''Égypte j''ai appelé mon fils - prophétie d''Osée.', 'Matthieu 2:15'),
('mat_016', 'MAT', 'Que fit Hérode quand il vit que les mages l''avaient trompé ?', '["Il abandonna","Il fit massacrer tous les enfants de 2 ans et moins à Bethléem","Il envoya des soldats chercher l''enfant","Il pria"]', 1, 'Le massacre des innocents à Bethléem.', 'Matthieu 2:16'),
('mat_017', 'MAT', 'Dans quelle ville Joseph s''établit-il au retour d''Égypte ?', '["Bethléem","Nazareth en Galilée","Jérusalem","Capernaüm"]', 1, 'Il se retira en Galilée et vint habiter à Nazareth.', 'Matthieu 2:22-23'),

-- Jean-Baptiste et le baptême de Jésus (Chapitres 3-4)
('mat_018', 'MAT', 'Quel était le message de Jean-Baptiste ?', '["Suivez-moi","Repentez-vous, car le royaume des cieux est proche","Payez vos impôts","Aimez vos ennemis"]', 1, 'Jean prêchait la repentance et l''arrivée du royaume.', 'Matthieu 3:2'),
('mat_019', 'MAT', 'Comment Jean était-il vêtu ?', '["De lin fin","D''un vêtement de poils de chameau avec une ceinture de cuir","De pourpre","De vêtements blancs"]', 1, 'Il portait un vêtement de poils de chameau et une ceinture de cuir.', 'Matthieu 3:4'),
('mat_020', 'MAT', 'Que mangeait Jean-Baptiste ?', '["Du pain et du vin","Des sauterelles et du miel sauvage","Des fruits","Du poisson"]', 1, 'Sa nourriture était des sauterelles et du miel sauvage.', 'Matthieu 3:4'),
('mat_021', 'MAT', 'Comment Jean appelait-il les pharisiens et sadducéens ?', '["Frères","Race de vipères","Élus de Dieu","Sages"]', 1, 'Race de vipères! Qui vous a appris à fuir la colère à venir?', 'Matthieu 3:7'),
('mat_022', 'MAT', 'Avec quoi Jean disait-il que celui qui venait après lui baptiserait ?', '["D''eau seulement","Du Saint-Esprit et de feu","D''huile","De sang"]', 1, 'Lui vous baptisera du Saint-Esprit et de feu.', 'Matthieu 3:11'),
('mat_023', 'MAT', 'Pourquoi Jésus vint-il vers Jean pour être baptisé ?', '["Pour se repentir","Pour accomplir toute justice","Pour montrer l''exemple","Par obligation"]', 1, 'Jésus dit qu''il convenait d''accomplir ainsi tout ce qui est juste.', 'Matthieu 3:15'),
('mat_024', 'MAT', 'Que se passa-t-il quand Jésus sortit de l''eau après son baptême ?', '["Rien de visible","Les cieux s''ouvrirent, l''Esprit descendit comme une colombe, une voix parla","Un éclair","Une tempête"]', 1, 'L''Esprit descendit comme une colombe et une voix dit: Celui-ci est mon Fils bien-aimé.', 'Matthieu 3:16-17'),
('mat_025', 'MAT', 'Où Jésus fut-il conduit après son baptême ?', '["À Jérusalem","Au désert pour être tenté par le diable","À Capernaüm","À Nazareth"]', 1, 'Jésus fut emmené par l''Esprit dans le désert pour être tenté.', 'Matthieu 4:1'),
('mat_026', 'MAT', 'Combien de temps Jésus jeûna-t-il dans le désert ?', '["7 jours","40 jours et 40 nuits","30 jours","3 jours"]', 1, 'Après avoir jeûné quarante jours et quarante nuits, il eut faim.', 'Matthieu 4:2'),
('mat_027', 'MAT', 'Quelle fut la première tentation de Jésus ?', '["Se prosterner devant Satan","Changer des pierres en pains","Se jeter du temple","Régner sur le monde"]', 1, 'Ordonne que ces pierres deviennent des pains.', 'Matthieu 4:3'),
('mat_028', 'MAT', 'Comment Jésus répondit-il à la première tentation ?', '["Par un miracle","L''homme ne vivra pas de pain seulement mais de toute parole de Dieu","Par le silence","Par la prière"]', 1, 'Il est écrit: L''homme ne vivra pas de pain seulement.', 'Matthieu 4:4'),
('mat_029', 'MAT', 'Où Satan emmena-t-il Jésus pour la deuxième tentation ?', '["Au désert","Sur le pinacle du temple à Jérusalem","Sur une montagne","En Égypte"]', 1, 'Satan le transporta sur le haut du temple.', 'Matthieu 4:5'),
('mat_030', 'MAT', 'Quelle était la troisième tentation de Satan ?', '["Faire des miracles","Offrir tous les royaumes du monde en échange de l''adoration","Commander aux anges","Détruire le temple"]', 1, 'Satan montra tous les royaumes et les offrit en échange d''adoration.', 'Matthieu 4:8-9'),
('mat_031', 'MAT', 'Comment Jésus congédia-t-il Satan ?', '["Par un ange","Retire-toi, Satan! Il est écrit: Tu adoreras le Seigneur ton Dieu seul","Par la force","Par la fuite"]', 1, 'Va-t''en, Satan! Il est écrit: Tu adoreras le Seigneur seul.', 'Matthieu 4:10'),
('mat_032', 'MAT', 'Où Jésus commença-t-il son ministère public ?', '["Jérusalem","Capernaüm, en Galilée","Nazareth","Bethléem"]', 1, 'Jésus vint demeurer à Capernaüm, au bord de la mer.', 'Matthieu 4:13'),
('mat_033', 'MAT', 'Quel était le message de Jésus au début de son ministère ?', '["Suivez la loi","Repentez-vous, car le royaume des cieux est proche","Donnez aux pauvres","Construisez le temple"]', 1, 'Le même message que Jean: Repentez-vous, le royaume est proche.', 'Matthieu 4:17'),
('mat_034', 'MAT', 'Quels furent les premiers disciples appelés ?', '["Jacques et Jean","Simon Pierre et André, pêchant sur la mer","Philippe et Nathanaël","Matthieu et Thomas"]', 1, 'Pierre et André, puis Jacques et Jean, fils de Zébédée.', 'Matthieu 4:18-21'),
('mat_035', 'MAT', 'Que faisaient Pierre et André quand Jésus les appela ?', '["Ils priaient","Ils jetaient un filet dans la mer","Ils réparaient leurs filets","Ils vendaient du poisson"]', 1, 'Ils pêchaient, car ils étaient pêcheurs.', 'Matthieu 4:18'),
('mat_036', 'MAT', 'Que promit Jésus de faire de ces pêcheurs ?', '["Des rois","Des pêcheurs d''hommes","Des prophètes","Des sacrificateurs"]', 1, 'Suivez-moi, et je vous ferai pêcheurs d''hommes.', 'Matthieu 4:19'),

-- Le Sermon sur la Montagne (Chapitres 5-7)
('mat_037', 'MAT', 'Comment s''appellent les déclarations de bénédiction au début du sermon ?', '["Les commandements","Les Béatitudes","Les paraboles","Les lois"]', 1, 'Les Béatitudes commencent par: Heureux les...', 'Matthieu 5:3-12'),
('mat_038', 'MAT', 'Qui sont les premiers bénis dans les Béatitudes ?', '["Les riches","Les pauvres en esprit, car le royaume des cieux est à eux","Les puissants","Les sages"]', 1, 'Heureux les pauvres en esprit, car le royaume des cieux est à eux!', 'Matthieu 5:3'),
('mat_039', 'MAT', 'Que recevront ceux qui ont faim et soif de justice ?', '["De l''or","Ils seront rassasiés","Du pouvoir","De la gloire"]', 1, 'Heureux ceux qui ont faim et soif de la justice, car ils seront rassasiés!', 'Matthieu 5:6'),
('mat_040', 'MAT', 'Que verront les cœurs purs ?', '["La terre promise","Dieu","Les anges","L''avenir"]', 1, 'Heureux ceux qui ont le cœur pur, car ils verront Dieu!', 'Matthieu 5:8'),
('mat_041', 'MAT', 'Comment seront appelés les artisans de paix ?', '["Sages","Fils de Dieu","Prophètes","Rois"]', 1, 'Heureux ceux qui procurent la paix, car ils seront appelés fils de Dieu!', 'Matthieu 5:9'),
('mat_042', 'MAT', 'À quoi Jésus compare-t-il ses disciples ?', '["À des rois","Sel de la terre et lumière du monde","À des soldats","À des prêtres"]', 1, 'Vous êtes le sel de la terre... Vous êtes la lumière du monde.', 'Matthieu 5:13-14'),
('mat_043', 'MAT', 'Jésus est-il venu abolir la loi et les prophètes ?', '["Oui","Non, mais les accomplir","Partiellement","Pour les remplacer"]', 1, 'Je ne suis pas venu pour abolir, mais pour accomplir.', 'Matthieu 5:17'),
('mat_044', 'MAT', 'Quelle justice doit surpasser celle des scribes et pharisiens ?', '["La justice des disciples","Aucune différence","La justice des Romains","La justice des rois"]', 0, 'Si votre justice ne surpasse celle des scribes, vous n''entrerez pas dans le royaume.', 'Matthieu 5:20'),
('mat_045', 'MAT', 'Comment traiter son ennemi selon Jésus ?', '["Le haïr","L''aimer et prier pour lui","L''ignorer","Le fuir"]', 1, 'Aimez vos ennemis, bénissez ceux qui vous maudissent.', 'Matthieu 5:44'),
('mat_046', 'MAT', 'Comment doit-on faire l''aumône ?', '["Publiquement","En secret, que ta main gauche ne sache pas ce que fait ta droite","Devant tous","Avec fanfare"]', 1, 'Que ta main gauche ne sache pas ce que fait ta droite.', 'Matthieu 6:3'),
('mat_047', 'MAT', 'Comment Jésus enseigne-t-il à prier ?', '["Avec beaucoup de mots","Notre Père qui es aux cieux, que ton nom soit sanctifié...","En criant","En public"]', 1, 'Le Notre Père est le modèle de prière enseigné par Jésus.', 'Matthieu 6:9-13'),
('mat_048', 'MAT', 'Où doit-on amasser des trésors ?', '["Sur la terre","Dans le ciel, où ni la teigne ni la rouille ne détruisent","Dans les banques","Nulle part"]', 1, 'Amassez-vous des trésors dans le ciel.', 'Matthieu 6:20'),
('mat_049', 'MAT', 'Peut-on servir deux maîtres ?', '["Oui","Non, on ne peut servir Dieu et Mammon (l''argent)","Parfois","Selon les circonstances"]', 1, 'Vous ne pouvez servir Dieu et Mammon.', 'Matthieu 6:24'),
('mat_050', 'MAT', 'Que doit-on chercher en premier ?', '["La nourriture","Le royaume de Dieu et sa justice","Les vêtements","La sécurité"]', 1, 'Cherchez premièrement le royaume et la justice de Dieu.', 'Matthieu 6:33'),
('mat_051', 'MAT', 'Pourquoi ne pas juger les autres ?', '["C''est interdit","Pour ne pas être jugé de la même manière","C''est impoli","C''est inutile"]', 1, 'Ne jugez point, afin que vous ne soyez point jugés.', 'Matthieu 7:1'),
('mat_052', 'MAT', 'Qu''arrivera-t-il si on demande, cherche et frappe ?', '["Rien","On recevra, on trouvera, on vous ouvrira","On sera déçu","On sera puni"]', 1, 'Demandez et vous recevrez; cherchez et vous trouverez; frappez et on vous ouvrira.', 'Matthieu 7:7-8'),
('mat_053', 'MAT', 'Quel chemin mène à la vie ?', '["Le large","Le chemin étroit et la porte resserrée","Le chemin du milieu","N''importe lequel"]', 1, 'Étroite est la porte et resserré le chemin qui mènent à la vie.', 'Matthieu 7:14'),
('mat_054', 'MAT', 'Comment reconnaît-on les faux prophètes ?', '["Par leurs vêtements","À leurs fruits - un bon arbre ne peut porter de mauvais fruits","Par leurs paroles seulement","Par leur popularité"]', 1, 'Vous les reconnaîtrez à leurs fruits.', 'Matthieu 7:16'),
('mat_055', 'MAT', 'Sur quoi l''homme sage bâtit-il sa maison ?', '["Sur le sable","Sur le roc","Sur la terre","N''importe où"]', 1, 'L''homme prudent bâtit sa maison sur le roc - il écoute et met en pratique.', 'Matthieu 7:24'),
('mat_056', 'MAT', 'Comment la foule réagit-elle à l''enseignement de Jésus ?', '["Avec indifférence","Frappée de son enseignement, car il enseignait avec autorité","Avec colère","Avec mépris"]', 1, 'Les foules furent frappées de sa doctrine car il enseignait avec autorité.', 'Matthieu 7:28-29'),

-- Miracles et enseignements (Chapitres 8-12)
('mat_057', 'MAT', 'Qui fut guéri en premier après le sermon sur la montagne ?', '["Un aveugle","Un lépreux","Un paralytique","Un sourd"]', 1, 'Un lépreux vint se prosterner devant Jésus et fut guéri.', 'Matthieu 8:1-3'),
('mat_058', 'MAT', 'Qui avait une foi que Jésus n''avait pas trouvée en Israël ?', '["Pierre","Un centenier romain","Un pharisien","Un samaritain"]', 1, 'Le centenier croyait qu''une parole de Jésus suffirait à guérir son serviteur.', 'Matthieu 8:10'),
('mat_059', 'MAT', 'Qu''est-ce que Jésus calma dans le bateau ?', '["Les disciples","Une grande tempête sur la mer","Un incendie","Une dispute"]', 1, 'Jésus menaça les vents et la mer, et il y eut un grand calme.', 'Matthieu 8:26'),
('mat_060', 'MAT', 'Où Jésus délivra-t-il deux démoniaques ?', '["En Galilée","Au pays des Gadaréniens","À Jérusalem","À Nazareth"]', 1, 'Dans le pays des Gadaréniens, les démons entrèrent dans des porcs.', 'Matthieu 8:28-32'),
('mat_061', 'MAT', 'Qui Jésus appela-t-il au bureau des péages ?', '["Pierre","Matthieu (Lévi)","Thomas","Jean"]', 1, 'Jésus vit un homme appelé Matthieu et lui dit: Suis-moi.', 'Matthieu 9:9'),
('mat_062', 'MAT', 'Pourquoi Jésus mangeait-il avec les pécheurs ?', '["Par habitude","Parce que ce ne sont pas les bien-portants qui ont besoin de médecin","Par obligation","Pour les convertir seulement"]', 1, 'Ce ne sont pas ceux qui se portent bien qui ont besoin de médecin, mais les malades.', 'Matthieu 9:12'),
('mat_063', 'MAT', 'Que dit Jésus à la femme qui toucha le bord de son vêtement ?', '["Ne me touche pas","Prends courage, ta foi t''a guérie","Va et ne pèche plus","Lève-toi"]', 1, 'Prends courage, ma fille, ta foi t''a guérie.', 'Matthieu 9:22'),
('mat_064', 'MAT', 'Comment Jésus décrit-il la moisson ?', '["Petite","Grande, mais les ouvriers peu nombreux","Terminée","Impossible"]', 1, 'La moisson est grande, mais il y a peu d''ouvriers.', 'Matthieu 9:37'),
('mat_065', 'MAT', 'Combien de disciples Jésus envoya-t-il en mission ?', '["Sept","Douze","Soixante-dix","Trois"]', 1, 'Il appela ses douze disciples et leur donna le pouvoir.', 'Matthieu 10:1'),
('mat_066', 'MAT', 'Comment les disciples devaient-ils être envoyés ?', '["Comme des lions","Comme des brebis au milieu des loups","Comme des aigles","Comme des soldats"]', 1, 'Je vous envoie comme des brebis au milieu des loups.', 'Matthieu 10:16'),
('mat_067', 'MAT', 'Que ne doit-on pas craindre selon Jésus ?', '["La mort","Ceux qui tuent le corps mais ne peuvent tuer l''âme","La maladie","La pauvreté"]', 1, 'Ne craignez pas ceux qui tuent le corps mais ne peuvent tuer l''âme.', 'Matthieu 10:28'),
('mat_068', 'MAT', 'Combien valent deux moineaux ?', '["Beaucoup","Un sou, mais pas un ne tombe sans que le Père le sache","Rien","Cinq pièces"]', 1, 'Ne vend-on pas deux moineaux pour un sou? Pas un ne tombe sans le Père.', 'Matthieu 10:29'),
('mat_069', 'MAT', 'Quel message Jean-Baptiste envoya-t-il à Jésus depuis la prison ?', '["Libère-moi","Es-tu celui qui doit venir, ou devons-nous en attendre un autre?","Je te suis fidèle","Aide-moi"]', 1, 'Jean envoya demander si Jésus était bien le Messie attendu.', 'Matthieu 11:3'),
('mat_070', 'MAT', 'Comment Jésus décrit-il son joug ?', '["Lourd","Doux et léger","Difficile","Impossible"]', 1, 'Mon joug est doux et mon fardeau léger.', 'Matthieu 11:30'),
('mat_071', 'MAT', 'Que dirent les pharisiens quand Jésus guérit le jour du sabbat ?', '["Gloire à Dieu","Cet homme n''est pas de Dieu car il ne garde pas le sabbat","Merci","Nous croyons"]', 1, 'Les pharisiens accusaient Jésus de violer le sabbat.', 'Matthieu 12:10-14'),
('mat_072', 'MAT', 'Quel péché ne sera pas pardonné ?', '["Le meurtre","Le blasphème contre le Saint-Esprit","L''adultère","Le vol"]', 1, 'Le blasphème contre l''Esprit ne sera point pardonné.', 'Matthieu 12:31'),
('mat_073', 'MAT', 'Quel signe Jésus promit-il à cette génération ?', '["Des miracles quotidiens","Le signe de Jonas - trois jours et trois nuits","Des armées célestes","De l''or"]', 1, 'Il ne lui sera donné d''autre signe que celui du prophète Jonas.', 'Matthieu 12:39-40'),

-- Les paraboles du Royaume (Chapitre 13)
('mat_074', 'MAT', 'Où Jésus était-il assis pour enseigner les paraboles ?', '["Dans le temple","Dans un bateau, la foule sur le rivage","Sur une montagne","Dans une maison"]', 1, 'Il monta dans une barque et s''assit, la foule sur le rivage.', 'Matthieu 13:2'),
('mat_075', 'MAT', 'Quelle est la première parabole du chapitre 13 ?', '["Le bon grain et l''ivraie","Le semeur","Le grain de moutarde","Le trésor caché"]', 1, 'La parabole du semeur qui sème la Parole.', 'Matthieu 13:3-9'),
('mat_076', 'MAT', 'Que représente le bon terrain dans la parabole du semeur ?', '["Les pharisiens","Celui qui entend, comprend et porte du fruit","Les disciples seulement","Tout le monde"]', 1, 'Le bon terrain représente celui qui entend et comprend la Parole et porte du fruit.', 'Matthieu 13:23'),
('mat_077', 'MAT', 'Que fit l''ennemi dans la parabole du bon grain et de l''ivraie ?', '["Vola le grain","Sema de l''ivraie parmi le blé pendant que les gens dormaient","Brûla le champ","Coupa les plants"]', 1, 'L''ennemi vint et sema de l''ivraie parmi le blé.', 'Matthieu 13:25'),
('mat_078', 'MAT', 'À quoi ressemble le royaume des cieux selon la parabole du grain de moutarde ?', '["À un grand arbre immédiat","À une graine qui devient un arbre où les oiseaux nichent","À un océan","À une montagne"]', 1, 'Le plus petit des grains devient un arbre où les oiseaux viennent nicher.', 'Matthieu 13:31-32'),
('mat_079', 'MAT', 'À quoi ressemble le royaume dans la parabole du levain ?', '["À du pain","À du levain qui fait lever toute la pâte","À du sel","À de l''eau"]', 1, 'Le royaume est comme du levain qu''une femme mêle à la farine.', 'Matthieu 13:33'),
('mat_080', 'MAT', 'Que fait l''homme qui trouve un trésor caché dans un champ ?', '["Le partage","Il vend tout ce qu''il a et achète le champ","Il le laisse","Il le montre"]', 1, 'Il va, vend tout ce qu''il a et achète ce champ.', 'Matthieu 13:44'),
('mat_081', 'MAT', 'Où Jésus ne put-il faire beaucoup de miracles à cause de l''incrédulité ?', '["Capernaüm","Nazareth, sa patrie","Jérusalem","Bethléem"]', 1, 'Un prophète n''est méprisé que dans sa patrie; il ne fit pas beaucoup de miracles là.', 'Matthieu 13:57-58'),

-- Pierre et la Transfiguration (Chapitres 14-17)
('mat_082', 'MAT', 'Comment Jean-Baptiste mourut-il ?', '["Crucifié","Décapité à cause du serment d''Hérode à la fille d''Hérodias","Lapidé","De vieillesse"]', 1, 'Hérode fit décapiter Jean dans la prison.', 'Matthieu 14:10'),
('mat_083', 'MAT', 'Combien de personnes Jésus nourrit-il avec 5 pains et 2 poissons ?', '["Cent","Environ 5000 hommes, sans compter femmes et enfants","Mille","Les douze disciples"]', 1, 'Ceux qui avaient mangé étaient environ cinq mille hommes, sans les femmes et les enfants.', 'Matthieu 14:21'),
('mat_084', 'MAT', 'Combien de paniers de restes furent ramassés ?', '["Trois","Douze paniers pleins","Sept","Un seul"]', 1, 'On ramassa douze paniers pleins des morceaux qui restaient.', 'Matthieu 14:20'),
('mat_085', 'MAT', 'Comment Pierre marcha-t-il sur l''eau ?', '["Par sa propre force","Par la foi, mais il coula quand il douta","Par magie","Il ne marcha pas"]', 1, 'Pierre marcha sur l''eau mais coula quand il vit le vent et douta.', 'Matthieu 14:29-30'),
('mat_086', 'MAT', 'Que répondit Pierre quand Jésus demanda: Qui dites-vous que je suis ?', '["Un prophète","Tu es le Christ, le Fils du Dieu vivant","Élie","Jean-Baptiste"]', 1, 'Simon Pierre répondit: Tu es le Christ, le Fils du Dieu vivant.', 'Matthieu 16:16'),
('mat_087', 'MAT', 'Sur quelle pierre Jésus dit-il bâtir son Église ?', '["Une pierre littérale","Sur cette pierre (la confession de Pierre), et les portes du séjour des morts ne prévaudront pas","Sur le temple","Sur Jérusalem"]', 1, 'Sur cette pierre je bâtirai mon Église.', 'Matthieu 16:18'),
('mat_088', 'MAT', 'Que doit faire celui qui veut suivre Jésus ?', '["Donner de l''argent","Renoncer à lui-même, prendre sa croix et suivre Jésus","Étudier seulement","Prier beaucoup"]', 1, 'Si quelqu''un veut venir après moi, qu''il renonce à lui-même et prenne sa croix.', 'Matthieu 16:24'),
('mat_089', 'MAT', 'Qui apparut avec Jésus lors de la Transfiguration ?', '["Abraham et David","Moïse et Élie","Les anges","Adam et Ève"]', 1, 'Moïse et Élie apparurent, s''entretenant avec lui.', 'Matthieu 17:3'),
('mat_090', 'MAT', 'Combien de disciples assistèrent à la Transfiguration ?', '["Tous les douze","Trois: Pierre, Jacques et Jean","Deux","Un seul"]', 1, 'Jésus prit avec lui Pierre, Jacques et Jean.', 'Matthieu 17:1'),
('mat_091', 'MAT', 'Que dit la voix du ciel lors de la Transfiguration ?', '["Voici le prophète","Celui-ci est mon Fils bien-aimé, écoutez-le!","Adorez-le","Suivez-le"]', 1, 'Une voix dit: Celui-ci est mon Fils bien-aimé; écoutez-le!', 'Matthieu 17:5'),

-- Enseignements sur le pardon et l'humilité (Chapitres 18-20)
('mat_092', 'MAT', 'Qui est le plus grand dans le royaume des cieux ?', '["Le plus fort","Celui qui s''humilie comme un petit enfant","Le plus riche","Le plus sage"]', 1, 'Celui qui se rendra humble comme ce petit enfant sera le plus grand.', 'Matthieu 18:4'),
('mat_093', 'MAT', 'Combien de fois doit-on pardonner son frère ?', '["Sept fois","Jusqu''à soixante-dix fois sept fois","Une fois","Trois fois"]', 1, 'Je ne te dis pas jusqu''à sept fois, mais jusqu''à soixante-dix fois sept fois.', 'Matthieu 18:22'),
('mat_094', 'MAT', 'Que fit le serviteur impitoyable après avoir été pardonné ?', '["Il pardonna aussi","Il fit jeter en prison son compagnon qui lui devait peu","Il remercia","Il changea"]', 1, 'Il saisit son compagnon qui lui devait peu et le fit jeter en prison.', 'Matthieu 18:28-30'),
('mat_095', 'MAT', 'Que répondit Jésus sur le divorce ?', '["C''est permis","Ce que Dieu a joint, que l''homme ne le sépare pas","C''est toujours interdit","C''est recommandé"]', 1, 'Que l''homme ne sépare pas ce que Dieu a joint.', 'Matthieu 19:6'),
('mat_096', 'MAT', 'Que dit Jésus au jeune homme riche ?', '["Tu es parfait","Va, vends ce que tu possèdes et donne aux pauvres","Continue ainsi","Donne la moitié"]', 1, 'Va, vends ce que tu possèdes, donne-le aux pauvres, et tu auras un trésor dans le ciel.', 'Matthieu 19:21'),
('mat_097', 'MAT', 'Pourquoi est-il difficile pour un riche d''entrer dans le royaume ?', '["C''est impossible","Il est difficile car l''argent peut devenir une idole","C''est facile","C''est égal"]', 1, 'Il est difficile à un riche d''entrer dans le royaume des cieux.', 'Matthieu 19:23'),
('mat_098', 'MAT', 'Quelle récompense auront les disciples qui ont tout quitté ?', '["Rien de plus","Cent fois autant et la vie éternelle","Le double","Une place honorifique"]', 1, 'Quiconque aura quitté maisons, frères, etc. recevra le centuple et la vie éternelle.', 'Matthieu 19:29'),
('mat_099', 'MAT', 'Que demanda la mère de Jacques et Jean pour ses fils ?', '["De l''argent","Qu''ils siègent à la droite et à la gauche de Jésus dans son royaume","Une maison","La guérison"]', 1, 'Elle demanda que ses fils soient assis à la droite et à la gauche de Jésus.', 'Matthieu 20:21'),
('mat_100', 'MAT', 'Pourquoi le Fils de l''homme est-il venu ?', '["Pour être servi","Pour servir et donner sa vie en rançon pour plusieurs","Pour régner","Pour juger"]', 1, 'Le Fils de l''homme est venu pour servir et donner sa vie en rançon.', 'Matthieu 20:28'),

-- Entrée à Jérusalem et controverses (Chapitres 21-23)
('mat_101', 'MAT', 'Comment Jésus entra-t-il à Jérusalem ?', '["À pied","Monté sur un âne et un ânon","Sur un cheval","En char"]', 1, 'Jésus entra monté sur une ânesse et un ânon, accomplissant la prophétie.', 'Matthieu 21:5,7'),
('mat_102', 'MAT', 'Que criait la foule lors de l''entrée triomphale ?', '["Vive le roi!","Hosanna au Fils de David! Béni soit celui qui vient au nom du Seigneur!","Gloire!","Alleluia!"]', 1, 'Hosanna au Fils de David! Béni soit celui qui vient au nom du Seigneur!', 'Matthieu 21:9'),
('mat_103', 'MAT', 'Que fit Jésus dans le temple ?', '["Il pria seulement","Il chassa les vendeurs et renversa les tables des changeurs","Il enseigna seulement","Il offrit un sacrifice"]', 1, 'Jésus chassa les vendeurs car le temple devait être une maison de prière.', 'Matthieu 21:12-13'),
('mat_104', 'MAT', 'Que fit Jésus au figuier sans fruit ?', '["Il l''arrosa","Il le maudit et le figuier sécha","Il le laissa","Il cueillit ses feuilles"]', 1, 'Jésus maudit le figuier qui ne portait que des feuilles, et il sécha.', 'Matthieu 21:19'),
('mat_105', 'MAT', 'Que représentent les vignerons dans la parabole ?', '["Les disciples","Les chefs religieux qui rejettent les prophètes et le Fils","Les Romains","Les païens"]', 1, 'Les vignerons représentent les dirigeants qui rejettent les envoyés de Dieu.', 'Matthieu 21:33-46'),
('mat_106', 'MAT', 'Faut-il payer l''impôt à César ?', '["Non, jamais","Rendez à César ce qui est à César et à Dieu ce qui est à Dieu","Oui, tout","La moitié seulement"]', 1, 'Rendez donc à César ce qui est à César, et à Dieu ce qui est à Dieu.', 'Matthieu 22:21'),
('mat_107', 'MAT', 'Quel est le plus grand commandement ?', '["Ne pas tuer","Tu aimeras le Seigneur ton Dieu de tout ton cœur, de toute ton âme et de toute ta pensée","Ne pas voler","Honorer ses parents"]', 1, 'Tu aimeras le Seigneur ton Dieu de tout ton cœur...', 'Matthieu 22:37'),
('mat_108', 'MAT', 'Quel est le second commandement semblable au premier ?', '["Ne pas mentir","Tu aimeras ton prochain comme toi-même","Sanctifier le sabbat","Ne pas convoiter"]', 1, 'Tu aimeras ton prochain comme toi-même.', 'Matthieu 22:39'),
('mat_109', 'MAT', 'Comment Jésus appelait-il les scribes et pharisiens dans le chapitre 23 ?', '["Frères","Hypocrites, guides aveugles","Amis","Maîtres"]', 1, 'Jésus prononce sept malheurs contre leur hypocrisie.', 'Matthieu 23:13-29'),
('mat_110', 'MAT', 'Que se passe-t-il quand on nettoie l''extérieur de la coupe mais pas l''intérieur ?', '["C''est suffisant","C''est de l''hypocrisie - il faut purifier l''intérieur d''abord","C''est bien","C''est normal"]', 1, 'Pharisien aveugle! Nettoie premièrement l''intérieur de la coupe.', 'Matthieu 23:26'),

-- Discours sur la fin des temps (Chapitres 24-25)
('mat_111', 'MAT', 'Que prédit Jésus concernant le temple ?', '["Sa gloire éternelle","Il n''en restera pas pierre sur pierre - tout sera détruit","Son agrandissement","Sa rénovation"]', 1, 'Il ne restera pas pierre sur pierre qui ne soit renversée.', 'Matthieu 24:2'),
('mat_112', 'MAT', 'Quel signe précédera le retour de Christ ?', '["La paix mondiale","L''Évangile sera prêché dans le monde entier","La fin des guerres","Pas de signe"]', 1, 'Cette bonne nouvelle du royaume sera prêchée dans le monde entier.', 'Matthieu 24:14'),
('mat_113', 'MAT', 'Comment sera le retour du Fils de l''homme ?', '["Secret","Comme l''éclair qui part de l''orient et brille jusqu''à l''occident","Graduel","Invisible"]', 1, 'Le Fils de l''homme viendra comme l''éclair.', 'Matthieu 24:27'),
('mat_114', 'MAT', 'Que savons-nous du jour et de l''heure du retour ?', '["La date exacte","Personne ne le sait, ni les anges, ni le Fils, seulement le Père","C''est bientôt","Dans mille ans"]', 1, 'Pour ce qui est du jour et de l''heure, personne ne le sait.', 'Matthieu 24:36'),
('mat_115', 'MAT', 'Que firent les cinq vierges folles ?', '["Elles préparèrent leur huile","Elles n''emportèrent pas d''huile avec leurs lampes","Elles dormirent bien","Elles arrivèrent tôt"]', 1, 'Les folles prirent leurs lampes mais pas d''huile de réserve.', 'Matthieu 25:3'),
('mat_116', 'MAT', 'Dans la parabole des talents, combien le maître donna-t-il aux serviteurs ?', '["Également à tous","Selon leurs capacités: 5, 2 et 1 talent","Rien","Le même montant"]', 1, 'Il donna à chacun selon sa capacité: 5, 2 et 1 talent.', 'Matthieu 25:15'),
('mat_117', 'MAT', 'Qu''arriva-t-il au serviteur qui enterra son talent ?', '["Il fut félicité","On lui retira son talent et il fut jeté dehors","Rien","Il fut promu"]', 1, 'Le talent lui fut retiré et il fut jeté dans les ténèbres du dehors.', 'Matthieu 25:28-30'),
('mat_118', 'MAT', 'Comment les nations seront-elles jugées ?', '["Par leur richesse","Selon ce qu''elles ont fait aux plus petits des frères de Christ","Par leur pouvoir","Par leur nombre"]', 1, 'Ce que vous avez fait au plus petit de mes frères, c''est à moi que vous l''avez fait.', 'Matthieu 25:40'),

-- La Passion (Chapitres 26-27)
('mat_119', 'MAT', 'Combien Judas reçut-il pour trahir Jésus ?', '["Dix pièces d''argent","Trente pièces d''argent","Cent pièces","Cinquante pièces"]', 1, 'Ils lui comptèrent trente pièces d''argent.', 'Matthieu 26:15'),
('mat_120', 'MAT', 'Que représente le pain dans la Cène ?', '["Seulement du pain","Le corps de Christ","La manne","Le sacrifice"]', 1, 'Jésus dit: Ceci est mon corps.', 'Matthieu 26:26'),
('mat_121', 'MAT', 'Que représente la coupe dans la Cène ?', '["Du vin ordinaire","Le sang de la nouvelle alliance, répandu pour le pardon des péchés","Un symbole de fête","De l''eau"]', 1, 'Ceci est mon sang, le sang de l''alliance, répandu pour plusieurs.', 'Matthieu 26:28'),
('mat_122', 'MAT', 'Où Jésus pria-t-il avant son arrestation ?', '["Au temple","À Gethsémané","Chez Pierre","Sur le mont des Oliviers seulement"]', 1, 'Jésus alla avec eux dans un lieu appelé Gethsémané.', 'Matthieu 26:36'),
('mat_123', 'MAT', 'Quelle fut la prière de Jésus à Gethsémané ?', '["Délivre-moi maintenant","Que cette coupe s''éloigne de moi, toutefois non ma volonté mais la tienne","Que mes disciples soient forts","Pardonne-leur"]', 1, 'Que cette coupe s''éloigne de moi! Toutefois, non pas ce que je veux, mais ce que tu veux.', 'Matthieu 26:39'),
('mat_124', 'MAT', 'Comment Judas identifia-t-il Jésus aux soldats ?', '["En le pointant du doigt","Par un baiser","En criant son nom","Par un signe de la main"]', 1, 'Judas s''approcha de Jésus et lui donna un baiser.', 'Matthieu 26:49'),
('mat_125', 'MAT', 'Combien de fois Pierre renia-t-il Jésus ?', '["Une fois","Trois fois avant le chant du coq","Deux fois","Jamais"]', 1, 'Avant que le coq chante, tu me renieras trois fois.', 'Matthieu 26:34,74'),
('mat_126', 'MAT', 'Que fit Judas après avoir livré Jésus ?', '["Il se réjouit","Il se repentit, rendit l''argent et se pendit","Il s''enfuit","Il demanda pardon"]', 1, 'Pris de remords, Judas rapporta l''argent et alla se pendre.', 'Matthieu 27:3-5'),
('mat_127', 'MAT', 'Qui fut relâché à la place de Jésus ?', '["Pilate","Barabbas, un criminel notoire","Jean-Baptiste","Un innocent"]', 1, 'Pilate leur relâcha Barabbas.', 'Matthieu 27:26'),
('mat_128', 'MAT', 'Qui aida Jésus à porter sa croix ?', '["Pierre","Simon de Cyrène","Jean","Personne"]', 1, 'Ils contraignirent Simon de Cyrène à porter la croix de Jésus.', 'Matthieu 27:32'),
('mat_129', 'MAT', 'Que se passa-t-il quand Jésus mourut ?', '["Rien","Le voile du temple se déchira, la terre trembla, des tombeaux s''ouvrirent","Un arc-en-ciel","Une étoile apparut"]', 1, 'Le voile du temple se déchira, la terre trembla, les rochers se fendirent.', 'Matthieu 27:51'),
('mat_130', 'MAT', 'Que dit le centenier après la mort de Jésus ?', '["C''était un criminel","Assurément, cet homme était Fils de Dieu","Je ne sais pas","Rien"]', 1, 'Le centenier dit: Assurément, cet homme était Fils de Dieu.', 'Matthieu 27:54'),

-- La Résurrection et la Grande Mission (Chapitre 28)
('mat_131', 'MAT', 'Qui vint au tombeau le premier jour de la semaine ?', '["Pierre","Marie de Magdala et l''autre Marie","Jean","Tous les disciples"]', 1, 'Marie de Magdala et l''autre Marie allèrent voir le sépulcre.', 'Matthieu 28:1'),
('mat_132', 'MAT', 'Qui roula la pierre du tombeau ?', '["Les soldats","Un ange du Seigneur, lors d''un tremblement de terre","Les femmes","Pierre"]', 1, 'Un ange du Seigneur descendit du ciel et roula la pierre.', 'Matthieu 28:2'),
('mat_133', 'MAT', 'Quel message l''ange donna-t-il aux femmes ?', '["Pleurez","Il n''est pas ici, il est ressuscité; allez dire aux disciples de le retrouver en Galilée","Attendez ici","Rentrez chez vous"]', 1, 'Il est ressuscité! Allez dire à ses disciples qu''il vous précède en Galilée.', 'Matthieu 28:6-7'),
('mat_134', 'MAT', 'Que firent les gardes après avoir vu l''ange ?', '["Ils adorèrent","Ils tremblèrent de peur et devinrent comme morts","Ils s''enfuirent","Ils attaquèrent"]', 1, 'Les gardes tremblèrent de peur et devinrent comme morts.', 'Matthieu 28:4'),
('mat_135', 'MAT', 'Quelle excuse les chefs religieux inventèrent-ils ?', '["Jésus n''était pas mort","Les disciples ont volé le corps pendant que les gardes dormaient","Le tombeau était vide depuis le début","Ils ne firent rien"]', 1, 'Les soldats furent payés pour dire que les disciples avaient volé le corps.', 'Matthieu 28:12-13'),
('mat_136', 'MAT', 'Où Jésus donna-t-il la Grande Mission ?', '["À Jérusalem","Sur une montagne en Galilée","Au temple","Au bord de la mer"]', 1, 'Les onze disciples allèrent en Galilée, sur la montagne désignée.', 'Matthieu 28:16'),
('mat_137', 'MAT', 'Quelle autorité Jésus revendiqua-t-il ?', '["Sur Israël","Tout pouvoir dans le ciel et sur la terre","Sur les disciples seulement","Sur le temple"]', 1, 'Tout pouvoir m''a été donné dans le ciel et sur la terre.', 'Matthieu 28:18'),
('mat_138', 'MAT', 'Quelle est la Grande Mission ?', '["Restez à Jérusalem","Allez, faites de toutes les nations des disciples, baptisez-les et enseignez-les","Attendez mon retour","Construisez des temples"]', 1, 'Allez, faites de toutes les nations des disciples, les baptisant au nom du Père, du Fils et du Saint-Esprit.', 'Matthieu 28:19'),
('mat_139', 'MAT', 'Au nom de qui les disciples devaient-ils baptiser ?', '["Au nom de Jésus seulement","Au nom du Père, du Fils et du Saint-Esprit","Au nom de Moïse","Au nom des prophètes"]', 1, 'Baptisez-les au nom du Père, du Fils et du Saint-Esprit.', 'Matthieu 28:19'),
('mat_140', 'MAT', 'Quelle promesse Jésus fit-il à la fin de l''évangile ?', '["Vous serez riches","Et voici, je suis avec vous tous les jours, jusqu''à la fin du monde","Vous aurez du pouvoir","Vous serez célèbres"]', 1, 'Je suis avec vous tous les jours, jusqu''à la fin du monde.', 'Matthieu 28:20');

-- Mise à jour du nombre de questions pour Matthieu
UPDATE book_quizzes SET question_count = (SELECT COUNT(*) FROM book_quiz_questions WHERE book_id = 'MAT') WHERE book_id = 'MAT';
