-- Questions pour Apocalypse/Révélation (REV)
-- 120+ questions couvrant les 22 chapitres

-- Chapitre 1: Introduction et vision du Christ glorifié
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation) VALUES
('rev_001', 'REV', 'Qui a écrit le livre de l''Apocalypse?', '["Pierre","Paul","Jean","Jacques"]', 2, 'Apocalypse 1:1,4 - La révélation de Jésus-Christ donnée à son serviteur Jean.'),
('rev_002', 'REV', 'Où Jean était-il quand il a reçu cette révélation?', '["À Jérusalem","Sur l''île de Patmos","À Rome","À Éphèse"]', 1, 'Apocalypse 1:9 - J''étais dans l''île appelée Patmos.'),
('rev_003', 'REV', 'Pourquoi Jean était-il à Patmos?', '["En vacances","À cause de la parole de Dieu et du témoignage de Jésus","Pour pêcher","Pour évangéliser"]', 1, 'Apocalypse 1:9 - À cause de la parole de Dieu et du témoignage de Jésus.'),
('rev_004', 'REV', 'À combien d''églises Jean doit-il envoyer ce livre?', '["Trois","Cinq","Sept","Douze"]', 2, 'Apocalypse 1:4,11 - Aux sept Églises qui sont en Asie.'),
('rev_005', 'REV', 'Comment Jésus est-il décrit dans la vision de Jean?', '["Comme un agneau","Comme un fils d''homme avec des cheveux blancs et des yeux comme une flamme de feu","Comme un lion","Comme une colombe"]', 1, 'Apocalypse 1:13-14 - Un fils d''homme, sa tête et ses cheveux étaient blancs, et ses yeux comme une flamme de feu.'),
('rev_006', 'REV', 'Que tenait Jésus dans sa main droite?', '["Une épée","Un livre","Sept étoiles","Une clé"]', 2, 'Apocalypse 1:16 - Il avait dans sa main droite sept étoiles.'),
('rev_007', 'REV', 'Qu''est-ce qui sortait de la bouche de Jésus?', '["Du feu","Une épée aiguë à deux tranchants","De l''eau","De la lumière"]', 1, 'Apocalypse 1:16 - De sa bouche sortait une épée aiguë, à deux tranchants.'),
('rev_008', 'REV', 'Que représentent les sept étoiles?', '["Les sept jours","Les anges des sept Églises","Les sept esprits","Les sept sceaux"]', 1, 'Apocalypse 1:20 - Les sept étoiles sont les anges des sept Églises.'),
('rev_009', 'REV', 'Que représentent les sept chandeliers d''or?', '["Les sept jours","Les sept anges","Les sept Églises","Les sept coupes"]', 2, 'Apocalypse 1:20 - Les sept chandeliers sont les sept Églises.'),
('rev_010', 'REV', 'Jésus dit: "Je suis le premier et le dernier, et le..."', '["Saint","Vivant","Juste","Puissant"]', 1, 'Apocalypse 1:17-18 - Je suis le premier et le dernier, et le vivant.'),

-- Chapitres 2-3: Lettres aux sept églises
('rev_011', 'REV', 'Quelle église a abandonné son premier amour?', '["Smyrne","Pergame","Éphèse","Thyatire"]', 2, 'Apocalypse 2:4 - J''ai contre toi que tu as abandonné ton premier amour (Éphèse).'),
('rev_012', 'REV', 'Quelle église va souffrir une tribulation de dix jours?', '["Éphèse","Smyrne","Pergame","Sardes"]', 1, 'Apocalypse 2:10 - Vous aurez une tribulation de dix jours (Smyrne).'),
('rev_013', 'REV', 'Quelle église habite là où est le trône de Satan?', '["Thyatire","Philadelphie","Pergame","Laodicée"]', 2, 'Apocalypse 2:13 - Tu habites là où est le trône de Satan (Pergame).'),
('rev_014', 'REV', 'Quelle église tolérait la femme Jézabel qui se disait prophétesse?', '["Éphèse","Smyrne","Thyatire","Sardes"]', 2, 'Apocalypse 2:20 - Tu laisses la femme Jézabel enseigner (Thyatire).'),
('rev_015', 'REV', 'Quelle église est décrite comme morte bien qu''elle ait la réputation d''être vivante?', '["Pergame","Sardes","Philadelphie","Laodicée"]', 1, 'Apocalypse 3:1 - Tu passes pour être vivant, et tu es mort (Sardes).'),
('rev_016', 'REV', 'Quelle église a devant elle une porte ouverte que personne ne peut fermer?', '["Smyrne","Thyatire","Philadelphie","Laodicée"]', 2, 'Apocalypse 3:8 - J''ai mis devant toi une porte ouverte (Philadelphie).'),
('rev_017', 'REV', 'Quelle église est décrite comme tiède, ni froide ni bouillante?', '["Éphèse","Pergame","Sardes","Laodicée"]', 3, 'Apocalypse 3:15-16 - Tu es tiède, ni froid ni bouillant (Laodicée).'),
('rev_018', 'REV', 'À l''église de Laodicée, Jésus dit qu''il se tient à la porte et:', '["Attend","Frappe","Pleure","Juge"]', 1, 'Apocalypse 3:20 - Voici, je me tiens à la porte, et je frappe.'),
('rev_019', 'REV', 'Que promet Jésus à celui qui vaincra à Éphèse?', '["La couronne de vie","L''arbre de vie","La manne cachée","L''étoile du matin"]', 1, 'Apocalypse 2:7 - Je donnerai à manger de l''arbre de vie.'),
('rev_020', 'REV', 'Que promet Jésus à celui qui vaincra à Smyrne?', '["L''arbre de vie","La couronne de vie","La manne cachée","Le pouvoir sur les nations"]', 1, 'Apocalypse 2:10 - Je te donnerai la couronne de vie.'),

-- Chapitres 4-5: Le trône céleste et l'Agneau
('rev_021', 'REV', 'Qu''a vu Jean quand une porte s''est ouverte dans le ciel?', '["Des anges","Un trône","Une armée","Un temple"]', 1, 'Apocalypse 4:2 - Un trône était dressé dans le ciel.'),
('rev_022', 'REV', 'Combien de vieillards entourent le trône?', '["Douze","Sept","Vingt-quatre","Cent"]', 2, 'Apocalypse 4:4 - Vingt-quatre trônes, et sur ces trônes vingt-quatre vieillards assis.'),
('rev_023', 'REV', 'Combien de créatures vivantes sont autour du trône?', '["Deux","Quatre","Sept","Douze"]', 1, 'Apocalypse 4:6 - Quatre êtres vivants remplis d''yeux.'),
('rev_024', 'REV', 'À quoi ressemble le premier être vivant?', '["Un aigle","Un lion","Un homme","Un veau"]', 1, 'Apocalypse 4:7 - Le premier être vivant est semblable à un lion.'),
('rev_025', 'REV', 'Que disent les quatre êtres vivants jour et nuit?', '["Alléluia","Saint, saint, saint est le Seigneur Dieu","Amen","Gloire à Dieu"]', 1, 'Apocalypse 4:8 - Saint, saint, saint est le Seigneur Dieu, le Tout-Puissant.'),
('rev_026', 'REV', 'Qui est digne d''ouvrir le livre scellé de sept sceaux?', '["Un ange","Un vieillard","L''Agneau","Moïse"]', 2, 'Apocalypse 5:5-6 - Le Lion de la tribu de Juda, l''Agneau immolé.'),
('rev_027', 'REV', 'Comment l''Agneau est-il décrit dans le chapitre 5?', '["Comme un lion","Comme immolé, avec sept cornes et sept yeux","Comme un homme","Comme une colombe"]', 1, 'Apocalypse 5:6 - Un agneau qui était là comme immolé, ayant sept cornes et sept yeux.'),
('rev_028', 'REV', 'Combien de sceaux avait le livre?', '["Trois","Cinq","Sept","Douze"]', 2, 'Apocalypse 5:1 - Un livre scellé de sept sceaux.'),
('rev_029', 'REV', 'Que tiennent les vieillards avec leurs harpes?', '["Des épées","Des coupes d''or pleines de parfums","Des trompettes","Des couronnes"]', 1, 'Apocalypse 5:8 - Des coupes d''or remplies de parfums, qui sont les prières des saints.'),
('rev_030', 'REV', 'De combien d''anges se compose la grande multitude qui loue l''Agneau?', '["Mille","Dix mille fois dix mille","Un million","Cent mille"]', 1, 'Apocalypse 5:11 - Des myriades de myriades et des milliers de milliers.'),

-- Chapitre 6: Les six premiers sceaux
('rev_031', 'REV', 'Quelle est la couleur du premier cheval quand le premier sceau est ouvert?', '["Rouge","Noir","Pâle","Blanc"]', 3, 'Apocalypse 6:2 - Un cheval blanc.'),
('rev_032', 'REV', 'Que représente le cavalier sur le cheval blanc du premier sceau?', '["La mort","La guerre","La conquête","La famine"]', 2, 'Apocalypse 6:2 - Il avait un arc, et une couronne lui fut donnée, et il partit en vainqueur.'),
('rev_033', 'REV', 'Quelle est la couleur du deuxième cheval?', '["Blanc","Rouge feu","Noir","Pâle"]', 1, 'Apocalypse 6:4 - Un autre cheval, rouge feu.'),
('rev_034', 'REV', 'Que symbolise le cheval rouge?', '["La paix","La guerre","La famine","La mort"]', 1, 'Apocalypse 6:4 - Le pouvoir d''enlever la paix de la terre, afin que les hommes s''égorgent.'),
('rev_035', 'REV', 'Quelle est la couleur du troisième cheval?', '["Blanc","Rouge","Noir","Pâle"]', 2, 'Apocalypse 6:5 - Un cheval noir.'),
('rev_036', 'REV', 'Que symbolise le cheval noir?', '["La guerre","La famine","La peste","La conquête"]', 1, 'Apocalypse 6:5-6 - La balance dans sa main représente la famine et les prix élevés.'),
('rev_037', 'REV', 'Quelle est la couleur du quatrième cheval?', '["Blanc","Rouge","Noir","Pâle (verdâtre)"]', 3, 'Apocalypse 6:8 - Un cheval d''une couleur pâle (verdâtre).'),
('rev_038', 'REV', 'Comment s''appelle le cavalier du cheval pâle?', '["Guerre","Famine","La Mort","Conquête"]', 2, 'Apocalypse 6:8 - Celui qui le montait se nommait la mort.'),
('rev_039', 'REV', 'Qu''est-ce que Jean voit sous l''autel au cinquième sceau?', '["De l''or","Les âmes de ceux qui avaient été immolés","Des anges","Des couronnes"]', 1, 'Apocalypse 6:9 - Les âmes de ceux qui avaient été immolés à cause de la parole de Dieu.'),
('rev_040', 'REV', 'Que se passe-t-il à l''ouverture du sixième sceau?', '["Un grand tremblement de terre","Une grande guerre","Une famine","Une pluie de feu"]', 0, 'Apocalypse 6:12 - Il y eut un grand tremblement de terre.'),

-- Chapitre 7: Les 144 000 et la grande multitude
('rev_041', 'REV', 'Combien de personnes sont scellées des tribus d''Israël?', '["12 000","24 000","144 000","1 000 000"]', 2, 'Apocalypse 7:4 - J''entendis le nombre de ceux qui avaient été marqués: 144 000.'),
('rev_042', 'REV', 'De combien de tribus d''Israël viennent les 144 000?', '["Dix","Douze","Sept","Vingt-quatre"]', 1, 'Apocalypse 7:5-8 - 12 000 de chaque tribu, donc 12 tribus.'),
('rev_043', 'REV', 'De quoi est vêtue la grande multitude que personne ne peut compter?', '["D''or","De robes blanches","De pourpre","De lin"]', 1, 'Apocalypse 7:9 - Vêtus de robes blanches.'),
('rev_044', 'REV', 'Que tient la grande multitude dans ses mains?', '["Des épées","Des harpes","Des palmes","Des couronnes"]', 2, 'Apocalypse 7:9 - Des palmes dans leurs mains.'),
('rev_045', 'REV', 'D''où vient la grande multitude?', '["De toute nation, tribu, peuple et langue","D''Israël seulement","De Rome","D''Asie"]', 0, 'Apocalypse 7:9 - De toute nation, de toute tribu, de tout peuple, et de toute langue.'),
('rev_046', 'REV', 'Comment les robes blanches ont-elles été blanchies?', '["Par l''eau","Dans le sang de l''Agneau","Par le feu","Par le soleil"]', 1, 'Apocalypse 7:14 - Ils ont lavé leurs robes et les ont blanchies dans le sang de l''Agneau.'),

-- Chapitre 8-9: Le septième sceau et les trompettes
('rev_047', 'REV', 'Que se passe-t-il quand le septième sceau est ouvert?', '["Un silence d''environ une demi-heure","Un grand bruit","Une tempête","Une lumière"]', 0, 'Apocalypse 8:1 - Il y eut dans le ciel un silence d''environ une demi-heure.'),
('rev_048', 'REV', 'Combien de trompettes sont données aux anges?', '["Trois","Cinq","Sept","Douze"]', 2, 'Apocalypse 8:2 - Sept trompettes leur furent données.'),
('rev_049', 'REV', 'Que se passe-t-il à la première trompette?', '["La mer devient du sang","De la grêle et du feu mêlés de sang","Les étoiles tombent","Le soleil s''obscurcit"]', 1, 'Apocalypse 8:7 - De la grêle et du feu mêlés de sang.'),
('rev_050', 'REV', 'À la deuxième trompette, que devient le tiers de la mer?', '["Du feu","Du sang","De l''huile","De la glace"]', 1, 'Apocalypse 8:8-9 - Le tiers de la mer devint du sang.'),
('rev_051', 'REV', 'Comment s''appelle l''étoile qui tombe à la troisième trompette?', '["Lucifer","Absinthe","Étoile du matin","Bethléhem"]', 1, 'Apocalypse 8:11 - L''étoile s''appelle Absinthe.'),
('rev_052', 'REV', 'Qu''est-ce qui sort du puits de l''abîme à la cinquième trompette?', '["De l''eau","Du feu","Des sauterelles","Des serpents"]', 2, 'Apocalypse 9:3 - Des sauterelles vinrent sur la terre.'),
('rev_053', 'REV', 'Comment s''appelle le roi des sauterelles?', '["Satan","Abaddon/Apollyon","Belzébuth","Lucifer"]', 1, 'Apocalypse 9:11 - Elles avaient sur elles comme roi l''ange de l''abîme, nommé en hébreu Abaddon, et en grec Apollyon.'),
('rev_054', 'REV', 'Combien d''anges sont déliés à la sixième trompette?', '["Un","Quatre","Sept","Douze"]', 1, 'Apocalypse 9:14 - Délie les quatre anges qui sont liés sur le grand fleuve d''Euphrate.'),
('rev_055', 'REV', 'Combien de cavaliers Jean voit-il à la sixième trompette?', '["Mille","Dix mille","Deux cents millions","Un milliard"]', 2, 'Apocalypse 9:16 - Le nombre des cavaliers de l''armée était de deux cents millions.'),

-- Chapitre 10-11: L'ange et le petit livre, les deux témoins
('rev_056', 'REV', 'Que tient l''ange puissant qui descend du ciel au chapitre 10?', '["Une épée","Un petit livre ouvert","Une trompette","Une couronne"]', 1, 'Apocalypse 10:2 - Il tenait dans sa main un petit livre ouvert.'),
('rev_057', 'REV', 'Quel goût avait le petit livre quand Jean l''a mangé?', '["Amer dans la bouche, doux dans le ventre","Doux dans la bouche, amer dans le ventre","Toujours doux","Toujours amer"]', 1, 'Apocalypse 10:9-10 - Doux comme du miel dans ma bouche, mais amer dans mes entrailles.'),
('rev_058', 'REV', 'Combien de témoins prophétisent pendant 1260 jours?', '["Un","Deux","Trois","Sept"]', 1, 'Apocalypse 11:3 - Je donnerai à mes deux témoins le pouvoir de prophétiser.'),
('rev_059', 'REV', 'Les deux témoins sont comparés à quoi?', '["Deux étoiles et deux lunes","Deux oliviers et deux chandeliers","Deux lions et deux aigles","Deux anges et deux prophètes"]', 1, 'Apocalypse 11:4 - Ce sont les deux oliviers et les deux chandeliers.'),
('rev_060', 'REV', 'Qui tue les deux témoins?', '["Satan","La bête qui monte de l''abîme","Les rois de la terre","Babylone"]', 1, 'Apocalypse 11:7 - La bête qui monte de l''abîme leur fera la guerre et les tuera.'),
('rev_061', 'REV', 'Après combien de jours les deux témoins ressuscitent-ils?', '["Un jour","Trois jours et demi","Sept jours","Quarante jours"]', 1, 'Apocalypse 11:11 - Après les trois jours et demi, un esprit de vie entra en eux.'),
('rev_062', 'REV', 'Que se passe-t-il à la septième trompette?', '["Le silence","Des voix dans le ciel proclamant le règne de Dieu","La fin du monde","Un tremblement de terre"]', 1, 'Apocalypse 11:15 - Le royaume du monde est remis à notre Seigneur et à son Christ.'),

-- Chapitre 12: La femme et le dragon
('rev_063', 'REV', 'Comment est décrite la femme dans le ciel au chapitre 12?', '["Vêtue du soleil, la lune sous ses pieds, couronnée de douze étoiles","Vêtue de blanc","Vêtue de pourpre","Vêtue d''or"]', 0, 'Apocalypse 12:1 - Une femme enveloppée du soleil, la lune sous ses pieds, et une couronne de douze étoiles.'),
('rev_064', 'REV', 'De quelle couleur est le dragon?', '["Noir","Vert","Rouge","Blanc"]', 2, 'Apocalypse 12:3 - Un grand dragon rouge feu.'),
('rev_065', 'REV', 'Combien de têtes et de cornes a le dragon?', '["Sept têtes et dix cornes","Dix têtes et sept cornes","Une tête et dix cornes","Sept têtes et sept cornes"]', 0, 'Apocalypse 12:3 - Sept têtes et dix cornes.'),
('rev_066', 'REV', 'Que veut faire le dragon à l''enfant de la femme?', '["Le couronner","Le dévorer","Le bénir","L''adorer"]', 1, 'Apocalypse 12:4 - Le dragon se tint devant la femme pour dévorer son enfant.'),
('rev_067', 'REV', 'Qui est le dragon selon Apocalypse 12?', '["Un démon","Le serpent ancien, le diable, Satan","Un roi terrestre","Un faux prophète"]', 1, 'Apocalypse 12:9 - Le serpent ancien, appelé le diable et Satan.'),
('rev_068', 'REV', 'Qui combat contre le dragon dans le ciel?', '["Gabriel","Michel et ses anges","Les 24 vieillards","L''Agneau"]', 1, 'Apocalypse 12:7 - Michel et ses anges combattirent contre le dragon.'),
('rev_069', 'REV', 'Combien de temps la femme est-elle nourrie dans le désert?', '["40 jours","1260 jours","7 ans","3 ans"]', 1, 'Apocalypse 12:6 - Elle fut nourrie pendant 1260 jours.'),

-- Chapitre 13: Les deux bêtes
('rev_070', 'REV', 'D''où monte la première bête?', '["Du ciel","De la mer","De la terre","De l''abîme"]', 1, 'Apocalypse 13:1 - Je vis monter de la mer une bête.'),
('rev_071', 'REV', 'Combien de têtes et de cornes a la bête de la mer?', '["Sept têtes et dix cornes","Dix têtes et sept cornes","Une tête","Trois têtes"]', 0, 'Apocalypse 13:1 - Ayant dix cornes et sept têtes.'),
('rev_072', 'REV', 'Qui donne sa puissance à la bête?', '["Les rois de la terre","Le dragon","Les anges","Babylone"]', 1, 'Apocalypse 13:2 - Le dragon lui donna sa puissance.'),
('rev_073', 'REV', 'Combien de temps la bête peut-elle agir?', '["1260 jours","3 ans","42 mois","7 ans"]', 2, 'Apocalypse 13:5 - Il lui fut donné le pouvoir d''agir pendant quarante-deux mois.'),
('rev_074', 'REV', 'D''où monte la deuxième bête?', '["De la mer","De la terre","Du ciel","De l''abîme"]', 1, 'Apocalypse 13:11 - Je vis monter de la terre une autre bête.'),
('rev_075', 'REV', 'Que fait la deuxième bête pour tromper les habitants de la terre?', '["Elle fait descendre le feu du ciel","Elle guérit les malades","Elle ressuscite les morts","Elle nourrit les foules"]', 0, 'Apocalypse 13:13 - Elle fait descendre du feu du ciel sur la terre.'),
('rev_076', 'REV', 'Quel est le nombre de la bête?', '["777","666","999","333"]', 1, 'Apocalypse 13:18 - C''est un nombre d''homme, et son nombre est six cent soixante-six.'),
('rev_077', 'REV', 'Où la marque de la bête est-elle placée?', '["Sur le front ou la main droite","Sur le cœur","Sur le pied","Sur l''épaule"]', 0, 'Apocalypse 13:16 - Une marque sur leur main droite ou sur leur front.'),

-- Chapitre 14: L'Agneau et les 144 000, les trois anges
('rev_078', 'REV', 'Où se tient l''Agneau avec les 144 000?', '["Sur le mont des Oliviers","Sur la montagne de Sion","À Jérusalem","Dans le temple"]', 1, 'Apocalypse 14:1 - L''agneau se tenait sur la montagne de Sion.'),
('rev_079', 'REV', 'Qu''ont les 144 000 écrit sur leur front?', '["Le nom de l''Agneau et de son Père","Le nombre 144000","Leur propre nom","Saint"]', 0, 'Apocalypse 14:1 - Son nom et le nom de son Père écrits sur leurs fronts.'),
('rev_080', 'REV', 'Que proclame le premier ange volant au milieu du ciel?', '["L''Évangile éternel","La destruction de Babylone","La colère de Dieu","Le règne de l''Agneau"]', 0, 'Apocalypse 14:6 - Il avait un Évangile éternel à annoncer.'),
('rev_081', 'REV', 'Que proclame le deuxième ange?', '["Babylone est tombée","L''heure du jugement est venue","Craignez Dieu","Adorez l''Agneau"]', 0, 'Apocalypse 14:8 - Elle est tombée, elle est tombée, Babylone la grande.'),
('rev_082', 'REV', 'Que dit le troisième ange concernant ceux qui adorent la bête?', '["Ils seront bénis","Ils boiront du vin de la colère de Dieu","Ils seront pardonnés","Ils régneront"]', 1, 'Apocalypse 14:9-10 - Il boira du vin de la fureur de Dieu.'),
('rev_083', 'REV', 'Heureux sont les morts qui meurent dans le Seigneur car ils se reposent de leurs:', '["Péchés","Travaux","Souffrances","Larmes"]', 1, 'Apocalypse 14:13 - Ils se reposent de leurs travaux, car leurs œuvres les suivent.'),

-- Chapitre 15-16: Les sept coupes de la colère de Dieu
('rev_084', 'REV', 'Combien de coupes contiennent les derniers fléaux?', '["Trois","Cinq","Sept","Dix"]', 2, 'Apocalypse 15:1 - Sept anges qui tenaient sept fléaux, les derniers.'),
('rev_085', 'REV', 'Où les sept anges reçoivent-ils les coupes?', '["Du trône","Du temple","De l''autel","De l''Agneau"]', 1, 'Apocalypse 15:6 - Les sept anges sortirent du temple.'),
('rev_086', 'REV', 'Que contiennent les coupes d''or?', '["De l''eau","Du sang","La colère de Dieu","Du parfum"]', 2, 'Apocalypse 15:7 - Sept coupes d''or, pleines de la colère de Dieu.'),
('rev_087', 'REV', 'Que produit la première coupe?', '["La mer devient du sang","Un ulcère malin sur ceux qui ont la marque","Les eaux deviennent amères","Le soleil brûle"]', 1, 'Apocalypse 16:2 - Un ulcère malin et douloureux frappa les hommes qui avaient la marque de la bête.'),
('rev_088', 'REV', 'Que devient la mer à la deuxième coupe?', '["Du feu","Du sang comme celui d''un mort","De la glace","De l''huile"]', 1, 'Apocalypse 16:3 - La mer devint du sang, comme celui d''un mort.'),
('rev_089', 'REV', 'Sur quoi la sixième coupe est-elle versée?', '["La mer","La terre","Le fleuve Euphrate","Le soleil"]', 2, 'Apocalypse 16:12 - Le sixième ange versa sa coupe sur le grand fleuve, l''Euphrate.'),
('rev_090', 'REV', 'Quel est le nom du lieu où les rois sont rassemblés pour la bataille?', '["Babylone","Jérusalem","Harmaguédon (Armageddon)","Rome"]', 2, 'Apocalypse 16:16 - Ils les rassemblèrent dans le lieu appelé en hébreu Harmaguédon.'),
('rev_091', 'REV', 'Que dit une voix du trône à la septième coupe?', '["C''est fait","Encore un peu","Attendez","Commencez"]', 0, 'Apocalypse 16:17 - Une voix forte sortit du temple: C''en est fait!'),

-- Chapitre 17-18: La chute de Babylone
('rev_092', 'REV', 'Comment est décrite Babylone?', '["La grande prostituée assise sur les grandes eaux","La ville sainte","La nouvelle Jérusalem","La cité de Dieu"]', 0, 'Apocalypse 17:1 - La grande prostituée qui est assise sur les grandes eaux.'),
('rev_093', 'REV', 'De quelle couleur est vêtue la femme (Babylone)?', '["Blanc","Pourpre et écarlate","Noir","Or"]', 1, 'Apocalypse 17:4 - Vêtue de pourpre et d''écarlate.'),
('rev_094', 'REV', 'Qu''est-il écrit sur le front de la femme?', '["Sainte","Mystère, Babylone la grande","Jérusalem","Reine du ciel"]', 1, 'Apocalypse 17:5 - Mystère, Babylone la grande, la mère des impudiques.'),
('rev_095', 'REV', 'Combien de rois sont représentés par les dix cornes?', '["Sept","Dix","Douze","Vingt-quatre"]', 1, 'Apocalypse 17:12 - Les dix cornes sont dix rois.'),
('rev_096', 'REV', 'L''Agneau vaincra les rois car il est:', '["Fort","Le Seigneur des seigneurs et le Roi des rois","Sage","Juste"]', 1, 'Apocalypse 17:14 - L''agneau les vaincra, parce qu''il est le Seigneur des seigneurs et le Roi des rois.'),
('rev_097', 'REV', 'Que dit la voix du ciel au peuple de Dieu concernant Babylone?', '["Restez-y","Sortez du milieu d''elle","Combattez-la","Ignorez-la"]', 1, 'Apocalypse 18:4 - Sortez du milieu d''elle, mon peuple.'),
('rev_098', 'REV', 'En combien de temps Babylone est-elle jugée?', '["Un jour","Une heure","Un an","Sept jours"]', 1, 'Apocalypse 18:10 - En une seule heure est venu ton jugement!'),

-- Chapitre 19: Les noces de l'Agneau et le retour de Christ
('rev_099', 'REV', 'Que crient les grandes foules dans le ciel après la chute de Babylone?', '["Amen","Alléluia","Hosanna","Gloire"]', 1, 'Apocalypse 19:1 - J''entendis comme une voix forte d''une foule nombreuse: Alléluia!'),
('rev_100', 'REV', 'Qu''annonce la grande voix comme une foule nombreuse?', '["Les noces de l''Agneau sont venues","La fin du monde","Le jugement dernier","La nouvelle création"]', 0, 'Apocalypse 19:7 - Les noces de l''agneau sont venues.'),
('rev_101', 'REV', 'De quoi est vêtue l''épouse de l''Agneau?', '["D''or","De fin lin, éclatant et pur","De pourpre","De soie"]', 1, 'Apocalypse 19:8 - Il lui a été donné de se revêtir d''un fin lin, éclatant, pur.'),
('rev_102', 'REV', 'Quel est le nom de celui qui monte le cheval blanc au chapitre 19?', '["Michel","La Parole de Dieu","L''Ange","Le Fils"]', 1, 'Apocalypse 19:13 - Son nom est la Parole de Dieu.'),
('rev_103', 'REV', 'Qu''est-il écrit sur le vêtement et la cuisse du cavalier?', '["Saint des saints","Roi des rois et Seigneur des seigneurs","L''Éternel","Alpha et Oméga"]', 1, 'Apocalypse 19:16 - Roi des rois et Seigneur des seigneurs.'),
('rev_104', 'REV', 'Où sont jetés la bête et le faux prophète?', '["En prison","Dans l''abîme","Dans l''étang de feu","Dans la mer"]', 2, 'Apocalypse 19:20 - Ils furent tous les deux jetés vivants dans l''étang ardent de feu et de soufre.'),

-- Chapitre 20: Le millénium et le jugement final
('rev_105', 'REV', 'Pour combien de temps Satan est-il lié?', '["100 ans","500 ans","1000 ans","Éternellement"]', 2, 'Apocalypse 20:2 - Il le lia pour mille ans.'),
('rev_106', 'REV', 'Où Satan est-il jeté pendant les mille ans?', '["Dans le feu","Dans l''abîme","Dans la mer","Sur la terre"]', 1, 'Apocalypse 20:3 - Il le jeta dans l''abîme.'),
('rev_107', 'REV', 'Qu''est-ce que la première résurrection?', '["La résurrection de tous","La résurrection des martyrs qui règnent avec Christ","La résurrection des méchants","La résurrection des anges"]', 1, 'Apocalypse 20:4-6 - Ils revinrent à la vie et régnèrent avec Christ pendant mille ans. C''est la première résurrection.'),
('rev_108', 'REV', 'Que se passe-t-il après les mille ans?', '["Satan est relâché pour un peu de temps","La nouvelle terre apparaît","Le jugement final commence immédiatement","Christ retourne au ciel"]', 0, 'Apocalypse 20:7 - Quand les mille ans seront accomplis, Satan sera relâché de sa prison.'),
('rev_109', 'REV', 'Qui sont Gog et Magog?', '["Des anges","Des nations séduites par Satan","Des prophètes","Des rois"]', 1, 'Apocalypse 20:8 - Il sortira pour séduire les nations, Gog et Magog.'),
('rev_110', 'REV', 'Où Satan est-il finalement jeté?', '["Dans l''abîme","Dans l''étang de feu et de soufre","Dans la mer","Sur la terre"]', 1, 'Apocalypse 20:10 - Le diable fut jeté dans l''étang de feu et de soufre.'),
('rev_111', 'REV', 'Devant quel trône les morts sont-ils jugés?', '["Un trône d''or","Un grand trône blanc","Un trône de feu","Un trône de cristal"]', 1, 'Apocalypse 20:11 - Je vis un grand trône blanc.'),
('rev_112', 'REV', 'Selon quoi les morts sont-ils jugés?', '["Leurs paroles","Leurs œuvres écrites dans les livres","Leur foi","Leur richesse"]', 1, 'Apocalypse 20:12 - Les morts furent jugés selon leurs œuvres.'),
('rev_113', 'REV', 'Qu''est-ce que la seconde mort?', '["La mort physique","L''étang de feu","La séparation de Dieu","Le sommeil éternel"]', 1, 'Apocalypse 20:14 - L''étang de feu, c''est la seconde mort.'),

-- Chapitre 21-22: La nouvelle Jérusalem et la conclusion
('rev_114', 'REV', 'Que voit Jean descendre du ciel?', '["Un ange","La nouvelle Jérusalem","Un livre","Une couronne"]', 1, 'Apocalypse 21:2 - Je vis descendre du ciel la ville sainte, la nouvelle Jérusalem.'),
('rev_115', 'REV', 'Dieu essuiera toute larme et il n''y aura plus de:', '["Travail","Mort, deuil, cri, ni douleur","Joie","Nuit"]', 1, 'Apocalypse 21:4 - Il n''y aura plus ni mort, ni deuil, ni cri, ni douleur.'),
('rev_116', 'REV', 'Combien de portes a la nouvelle Jérusalem?', '["Quatre","Sept","Douze","Vingt-quatre"]', 2, 'Apocalypse 21:12 - Elle avait douze portes.'),
('rev_117', 'REV', 'De quoi sont faites les portes de la ville?', '["D''or","D''une seule perle chacune","De diamant","D''argent"]', 1, 'Apocalypse 21:21 - Les douze portes étaient douze perles; chaque porte était d''une seule perle.'),
('rev_118', 'REV', 'Pourquoi n''y a-t-il pas de temple dans la nouvelle Jérusalem?', '["Il a été détruit","Le Seigneur Dieu et l''Agneau sont son temple","Il est caché","Il est spirituel"]', 1, 'Apocalypse 21:22 - Le Seigneur Dieu tout-puissant est son temple, ainsi que l''agneau.'),
('rev_119', 'REV', 'Qu''est-ce qui éclaire la ville?', '["Le soleil","La lune","La gloire de Dieu et l''Agneau","Des lampes"]', 2, 'Apocalypse 21:23 - La gloire de Dieu l''éclaire, et l''agneau est son flambeau.'),
('rev_120', 'REV', 'Quel fleuve coule du trône de Dieu et de l''Agneau?', '["Le Jourdain","L''Euphrate","Le fleuve d''eau de la vie","Le Nil"]', 2, 'Apocalypse 22:1 - Un fleuve d''eau de la vie, limpide comme du cristal.'),
('rev_121', 'REV', 'Que produit l''arbre de vie chaque mois?', '["Des fleurs","Des fruits","Des feuilles","Des graines"]', 1, 'Apocalypse 22:2 - Il produit douze fois des fruits, rendant son fruit chaque mois.'),
('rev_122', 'REV', 'À quoi servent les feuilles de l''arbre de vie?', '["À la décoration","À la guérison des nations","À la nourriture","À l''ombre"]', 1, 'Apocalypse 22:2 - Les feuilles de l''arbre servent à la guérison des nations.'),
('rev_123', 'REV', 'Que dit Jésus trois fois dans le chapitre 22?', '["Je t''aime","Je suis le Seigneur","Voici, je viens bientôt","Soyez saints"]', 2, 'Apocalypse 22:7,12,20 - Voici, je viens bientôt.'),
('rev_124', 'REV', 'Comment Jésus se décrit-il à la fin du livre?', '["Le Fils de Dieu","L''Alpha et l''Oméga, le premier et le dernier","Le Roi des rois","Le Bon Berger"]', 1, 'Apocalypse 22:13 - Je suis l''alpha et l''oméga, le premier et le dernier.'),
('rev_125', 'REV', 'Que dit l''Esprit et l''Épouse?', '["Amen","Alléluia","Viens","Attends"]', 2, 'Apocalypse 22:17 - L''Esprit et l''épouse disent: Viens!'),
('rev_126', 'REV', 'Quelle est la dernière prière du livre?', '["Amen","Viens, Seigneur Jésus","Gloire à Dieu","Alléluia"]', 1, 'Apocalypse 22:20 - Amen! Viens, Seigneur Jésus!'),
('rev_127', 'REV', 'Quel avertissement est donné concernant ce livre?', '["Ne pas le lire","Ne rien ajouter ni retrancher","Le cacher","Le brûler"]', 1, 'Apocalypse 22:18-19 - Si quelqu''un y ajoute ou en retranche, Dieu lui retranchera sa part.');
