-- Questions pour l'Épître aux Romains
-- L'évangile de la justification par la foi

-- Introduction et thème (Chapitre 1)
INSERT OR IGNORE INTO book_quiz_questions (id, book_id, question, options, correct_answer, explanation, verse_ref) VALUES
('rom_001', 'ROM', 'Qui a écrit l''épître aux Romains ?', '["Pierre","Paul, serviteur de Jésus-Christ, appelé apôtre","Jean","Jacques"]', 1, 'Paul se présente comme serviteur de Christ et apôtre.', 'Romains 1:1'),
('rom_002', 'ROM', 'À qui cette lettre est-elle adressée ?', '["Aux Juifs","À tous ceux qui sont à Rome, bien-aimés de Dieu, appelés saints","Aux Grecs","Aux Galates"]', 1, 'La lettre est adressée aux chrétiens de Rome.', 'Romains 1:7'),
('rom_003', 'ROM', 'De quoi Paul n''a-t-il pas honte ?', '["De sa faiblesse","De l''Évangile, car c''est la puissance de Dieu pour le salut","De son passé","De ses souffrances"]', 1, 'Je n''ai pas honte de l''Évangile car c''est la puissance de Dieu pour le salut.', 'Romains 1:16'),
('rom_004', 'ROM', 'Comment le juste vivra-t-il ?', '["Par la loi","Par la foi","Par les œuvres","Par la richesse"]', 1, 'Le juste vivra par la foi - citation d''Habakuk.', 'Romains 1:17'),
('rom_005', 'ROM', 'Qu''est-ce qui est révélé du ciel contre l''impiété ?', '["La bénédiction","La colère de Dieu","La grâce seulement","Rien"]', 1, 'La colère de Dieu se révèle contre toute impiété et injustice.', 'Romains 1:18'),
('rom_006', 'ROM', 'Comment Dieu s''est-il manifesté aux hommes ?', '["Par la loi seulement","Par ses œuvres - la création révèle sa puissance éternelle","Par des anges","Par des rêves uniquement"]', 1, 'Depuis la création, les attributs invisibles de Dieu sont visibles dans ses œuvres.', 'Romains 1:20'),
('rom_007', 'ROM', 'Pourquoi les hommes sont-ils inexcusables ?', '["Ils n''ont pas eu accès à la vérité","Ayant connu Dieu, ils ne l''ont pas glorifié comme Dieu","Ils étaient ignorants","Ils étaient faibles"]', 1, 'Ils ont connu Dieu mais ne l''ont pas glorifié ni remercié.', 'Romains 1:21'),
('rom_008', 'ROM', 'Que fit Dieu face au rejet des hommes ?', '["Il les détruisit immédiatement","Il les livra à leurs passions dégradantes","Il envoya un déluge","Il se cacha"]', 1, 'Dieu les livra à l''impureté et aux passions déshonorantes.', 'Romains 1:24,26'),

-- Le jugement de Dieu (Chapitre 2)
('rom_009', 'ROM', 'Celui qui juge les autres se condamne lui-même pourquoi ?', '["C''est interdit de juger","Il fait les mêmes choses qu''il condamne","Il n''a pas le droit","Il est trop faible"]', 1, 'En jugeant les autres, tu te condamnes car tu fais les mêmes choses.', 'Romains 2:1'),
('rom_010', 'ROM', 'Vers quoi la bonté de Dieu devrait-elle nous conduire ?', '["À la paresse","À la repentance","À l''orgueil","À la peur"]', 1, 'La bonté de Dieu te pousse à la repentance.', 'Romains 2:4'),
('rom_011', 'ROM', 'Comment Dieu jugera-t-il ?', '["Selon la richesse","Selon les œuvres de chacun, sans partialité","Selon la nationalité","Selon l''apparence"]', 1, 'Dieu rendra à chacun selon ses œuvres, sans favoritisme.', 'Romains 2:6,11'),
('rom_012', 'ROM', 'Qui sera justifié devant Dieu ?', '["Ceux qui écoutent la loi","Ceux qui pratiquent la loi","Ceux qui l''enseignent","Les riches"]', 1, 'Ce ne sont pas ceux qui écoutent qui sont justes, mais ceux qui pratiquent.', 'Romains 2:13'),
('rom_013', 'ROM', 'Qu''ont les païens qui n''ont pas la loi ?', '["Rien","La loi écrite dans leurs cœurs, leur conscience","La loi de Moïse","Des tablettes"]', 1, 'Les païens qui font naturellement ce que la loi ordonne sont une loi pour eux-mêmes.', 'Romains 2:14-15'),
('rom_014', 'ROM', 'Qu''est-ce que la vraie circoncision selon Paul ?', '["Celle de la chair seulement","Celle du cœur, par l''Esprit","Celle des mains","Celle visible"]', 1, 'La circoncision est celle du cœur, par l''Esprit, non par la lettre.', 'Romains 2:29'),

-- Tous sont pécheurs (Chapitre 3)
('rom_015', 'ROM', 'Quel avantage ont les Juifs ?', '["Aucun avantage","Les oracles de Dieu leur ont été confiés","Ils sont meilleurs","Ils sont plus riches"]', 1, 'Les Juifs ont l''avantage d''avoir reçu les oracles de Dieu.', 'Romains 3:1-2'),
('rom_016', 'ROM', 'Combien de justes y a-t-il selon l''Écriture ?', '["Beaucoup","Il n''y en a aucun, pas même un seul","Quelques-uns","Les prophètes seulement"]', 1, 'Il n''y a point de juste, pas même un seul.', 'Romains 3:10'),
('rom_017', 'ROM', 'Que fait la loi concernant le péché ?', '["Elle le pardonne","Elle donne la connaissance du péché","Elle l''ignore","Elle le justifie"]', 1, 'Par la loi vient la connaissance du péché.', 'Romains 3:20'),
('rom_018', 'ROM', 'Comment tous ont-ils péché ?', '["Seulement quelques-uns","Tous ont péché et sont privés de la gloire de Dieu","Les païens seulement","Les Juifs seulement"]', 1, 'Tous ont péché et sont privés de la gloire de Dieu.', 'Romains 3:23'),
('rom_019', 'ROM', 'Comment sommes-nous justifiés ?', '["Par nos œuvres","Gratuitement par sa grâce, par la rédemption en Christ","Par la loi","Par nos efforts"]', 1, 'Nous sommes justifiés gratuitement par sa grâce, par la rédemption en Jésus-Christ.', 'Romains 3:24'),
('rom_020', 'ROM', 'Par quoi Abraham et nous sommes-nous justifiés ?', '["Par les œuvres","Par la foi, indépendamment des œuvres de la loi","Par la circoncision","Par les sacrifices"]', 1, 'L''homme est justifié par la foi, sans les œuvres de la loi.', 'Romains 3:28'),
('rom_021', 'ROM', 'La foi annule-t-elle la loi ?', '["Oui, complètement","Non, au contraire nous confirmons la loi","Partiellement","La loi n''existe plus"]', 1, 'Nous n''annulons pas la loi par la foi, nous la confirmons.', 'Romains 3:31'),

-- Abraham justifié par la foi (Chapitre 4)
('rom_022', 'ROM', 'Comment Abraham fut-il justifié ?', '["Par ses œuvres","Il crut Dieu et cela lui fut compté comme justice","Par la circoncision","Par des sacrifices"]', 1, 'Abraham crut Dieu, et cela lui fut imputé à justice.', 'Romains 4:3'),
('rom_023', 'ROM', 'Qu''est-ce qui est imputé à celui qui ne travaille pas mais croit ?', '["Rien","Sa foi lui est comptée comme justice","Ses œuvres","Son argent"]', 1, 'À celui qui ne fait pas d''œuvres mais croit, sa foi est comptée comme justice.', 'Romains 4:5'),
('rom_024', 'ROM', 'Quand Abraham fut-il déclaré juste ?', '["Après la circoncision","Avant la circoncision, alors qu''il était incirconcis","Au moment de la circoncision","À sa mort"]', 1, 'Abraham fut justifié avant d''être circoncis.', 'Romains 4:10'),
('rom_025', 'ROM', 'De qui Abraham est-il le père ?', '["Des Juifs seulement","De tous ceux qui croient, circoncis ou non","Des incirconcis seulement","Des sacrificateurs"]', 1, 'Abraham est le père de tous les croyants.', 'Romains 4:11-12'),
('rom_026', 'ROM', 'Comment Abraham reçut-il la promesse de l''héritage ?', '["Par la loi","Par la justice de la foi","Par ses œuvres","Par ses richesses"]', 1, 'La promesse vint par la justice de la foi, non par la loi.', 'Romains 4:13'),
('rom_027', 'ROM', 'Contre quoi Abraham espéra-t-il ?', '["Contre la logique","Contre toute espérance, il crut avec espérance","Contre sa famille","Contre Dieu"]', 1, 'Espérant contre toute espérance, il crut.', 'Romains 4:18'),
('rom_028', 'ROM', 'Pourquoi Jésus fut-il livré et ressuscité ?', '["Pour nous punir","Livré pour nos offenses, ressuscité pour notre justification","Pour montrer sa puissance","Pour les Juifs seulement"]', 1, 'Il a été livré pour nos offenses et est ressuscité pour notre justification.', 'Romains 4:25'),

-- La justification et ses fruits (Chapitre 5)
('rom_029', 'ROM', 'Qu''avons-nous étant justifiés par la foi ?', '["Des richesses","La paix avec Dieu par notre Seigneur Jésus-Christ","Le pouvoir","La gloire terrestre"]', 1, 'Étant justifiés par la foi, nous avons la paix avec Dieu.', 'Romains 5:1'),
('rom_030', 'ROM', 'De quoi nous glorifions-nous même dans les tribulations ?', '["De notre force","La tribulation produit la persévérance, la persévérance l''épreuve, l''épreuve l''espérance","De notre sagesse","De notre richesse"]', 1, 'La tribulation produit la persévérance, qui produit l''épreuve, puis l''espérance.', 'Romains 5:3-4'),
('rom_031', 'ROM', 'Comment l''amour de Dieu nous est-il démontré ?', '["Par la création","Christ est mort pour nous alors que nous étions pécheurs","Par la loi","Par les miracles"]', 1, 'Dieu prouve son amour: Christ est mort pour nous alors que nous étions pécheurs.', 'Romains 5:8'),
('rom_032', 'ROM', 'Si nous avons été réconciliés par la mort de Christ, par quoi serons-nous sauvés ?', '["Par nos œuvres","Par sa vie","Par la loi","Par les sacrifices"]', 1, 'Réconciliés par sa mort, nous serons sauvés par sa vie.', 'Romains 5:10'),
('rom_033', 'ROM', 'Par qui le péché est-il entré dans le monde ?', '["Par Ève seulement","Par un seul homme, Adam","Par le serpent","Par les anges"]', 1, 'Par un seul homme le péché est entré dans le monde.', 'Romains 5:12'),
('rom_034', 'ROM', 'Comment la mort s''est-elle étendue à tous ?', '["Par hérédité physique","Parce que tous ont péché","Par malédiction","Par hasard"]', 1, 'La mort s''est étendue à tous parce que tous ont péché.', 'Romains 5:12'),
('rom_035', 'ROM', 'Comment le don gratuit diffère-t-il de l''offense ?', '["Ils sont égaux","Le don abonde beaucoup plus que l''offense","L''offense est plus grande","Ils n''ont pas de rapport"]', 1, 'Là où le péché a abondé, la grâce a surabondé.', 'Romains 5:15,20'),
('rom_036', 'ROM', 'Par l''obéissance d''un seul, que deviendront beaucoup ?', '["Rien","Beaucoup seront rendus justes","Quelques-uns seulement","Les Juifs seulement"]', 1, 'Par l''obéissance d''un seul, beaucoup seront rendus justes.', 'Romains 5:19'),

-- Morts au péché, vivants en Christ (Chapitre 6)
('rom_037', 'ROM', 'Devons-nous demeurer dans le péché pour que la grâce abonde ?', '["Oui","Loin de là! Nous qui sommes morts au péché, comment vivrions-nous encore?","Parfois","Peut-être"]', 1, 'Loin de là! Comment vivrions-nous encore dans le péché?', 'Romains 6:1-2'),
('rom_038', 'ROM', 'Que symbolise le baptême ?', '["Un lavage","Nous avons été ensevelis avec Christ pour marcher en nouveauté de vie","Une purification rituelle","Un symbole externe seulement"]', 1, 'Nous avons été ensevelis avec lui par le baptême pour marcher en nouveauté de vie.', 'Romains 6:4'),
('rom_039', 'ROM', 'Que savons-nous de notre vieil homme ?', '["Il est fort","Il a été crucifié avec Christ pour que le corps du péché soit détruit","Il vit encore","Il est bon"]', 1, 'Notre vieil homme a été crucifié avec lui pour que le corps du péché soit réduit à l''impuissance.', 'Romains 6:6'),
('rom_040', 'ROM', 'Que devons-nous nous considérer comme étant ?', '["Forts par nous-mêmes","Morts au péché et vivants pour Dieu en Jésus-Christ","Sans péché","Parfaits"]', 1, 'Regardez-vous comme morts au péché et vivants pour Dieu en Christ.', 'Romains 6:11'),
('rom_041', 'ROM', 'Que ne doit pas régner dans notre corps mortel ?', '["La fatigue","Le péché","La maladie","La faiblesse"]', 1, 'Que le péché ne règne donc pas dans votre corps mortel.', 'Romains 6:12'),
('rom_042', 'ROM', 'Le péché n''aura pas de pouvoir sur nous pourquoi ?', '["Nous sommes forts","Nous ne sommes pas sous la loi mais sous la grâce","Nous sommes parfaits","Nous ne péchons jamais"]', 1, 'Le péché n''aura pas de pouvoir sur vous car vous êtes sous la grâce.', 'Romains 6:14'),
('rom_043', 'ROM', 'De quoi sommes-nous devenus esclaves ?', '["Du péché","De la justice, ayant été libérés du péché","De la loi","De nous-mêmes"]', 1, 'Libérés du péché, vous êtes devenus esclaves de la justice.', 'Romains 6:18'),
('rom_044', 'ROM', 'Quel est le salaire du péché ?', '["La richesse","La mort","Le bonheur","Le pouvoir"]', 1, 'Le salaire du péché, c''est la mort.', 'Romains 6:23'),
('rom_045', 'ROM', 'Quel est le don gratuit de Dieu ?', '["La richesse","La vie éternelle en Jésus-Christ notre Seigneur","La santé","Le pouvoir"]', 1, 'Le don gratuit de Dieu, c''est la vie éternelle en Jésus-Christ.', 'Romains 6:23'),

-- La loi et le péché (Chapitre 7)
('rom_046', 'ROM', 'Combien de temps la loi a-t-elle pouvoir sur un homme ?', '["Toujours","Aussi longtemps qu''il vit","Quelques années","Jusqu''à la conversion"]', 1, 'La loi a pouvoir sur l''homme aussi longtemps qu''il vit.', 'Romains 7:1'),
('rom_047', 'ROM', 'Comment sommes-nous morts à la loi ?', '["Par nos efforts","Par le corps de Christ, pour appartenir à un autre","Par la circoncision","Par les œuvres"]', 1, 'Vous êtes morts à la loi par le corps de Christ pour appartenir à un autre.', 'Romains 7:4'),
('rom_048', 'ROM', 'La loi est-elle péché ?', '["Oui","Non, mais elle m''a fait connaître le péché","Partiellement","Elle cause le péché"]', 1, 'La loi n''est pas péché, mais elle m''a fait connaître le péché.', 'Romains 7:7'),
('rom_049', 'ROM', 'Quel conflit Paul décrit-il en lui ?', '["Un conflit physique","Je ne fais pas le bien que je veux, mais le mal que je ne veux pas","Un conflit avec les autres","Un conflit financier"]', 1, 'Je ne fais pas le bien que je veux, et je fais le mal que je ne veux pas.', 'Romains 7:19'),
('rom_050', 'ROM', 'Qui délivrera Paul de ce corps de mort ?', '["Lui-même","Grâces soient rendues à Dieu par Jésus-Christ","La loi","Les œuvres"]', 1, 'Grâces soient rendues à Dieu par Jésus-Christ notre Seigneur!', 'Romains 7:25'),

-- La vie selon l'Esprit (Chapitre 8)
('rom_051', 'ROM', 'Y a-t-il de la condamnation pour ceux qui sont en Christ ?', '["Oui, un peu","Il n''y a maintenant aucune condamnation","Parfois","Pour certains"]', 1, 'Il n''y a donc maintenant aucune condamnation pour ceux qui sont en Jésus-Christ.', 'Romains 8:1'),
('rom_052', 'ROM', 'De quoi la loi de l''Esprit de vie nous a-t-elle affranchis ?', '["De la fatigue","De la loi du péché et de la mort","De la pauvreté","De la maladie"]', 1, 'La loi de l''Esprit de vie en Christ nous a affranchis de la loi du péché et de la mort.', 'Romains 8:2'),
('rom_053', 'ROM', 'À quoi les affections de la chair mènent-elles ?', '["Au bonheur","À la mort","À la richesse","Au pouvoir"]', 1, 'L''affection de la chair, c''est la mort; l''affection de l''Esprit, c''est la vie et la paix.', 'Romains 8:6'),
('rom_054', 'ROM', 'Si l''Esprit de Christ n''habite pas en quelqu''un, que se passe-t-il ?', '["Rien","Il ne lui appartient pas","Il est quand même sauvé","Il est béni"]', 1, 'Si quelqu''un n''a pas l''Esprit de Christ, il ne lui appartient pas.', 'Romains 8:9'),
('rom_055', 'ROM', 'Que fait l''Esprit par rapport à notre adoption ?', '["Rien","Il rend témoignage avec notre esprit que nous sommes enfants de Dieu","Il nous condamne","Il nous éloigne"]', 1, 'L''Esprit lui-même rend témoignage que nous sommes enfants de Dieu.', 'Romains 8:16'),
('rom_056', 'ROM', 'Si nous sommes enfants, que sommes-nous aussi ?', '["Esclaves","Héritiers de Dieu et cohéritiers de Christ","Serviteurs","Étrangers"]', 1, 'Si nous sommes enfants, nous sommes aussi héritiers de Dieu et cohéritiers de Christ.', 'Romains 8:17'),
('rom_057', 'ROM', 'Comment les souffrances présentes se comparent-elles à la gloire à venir ?', '["Elles sont égales","Elles ne sont pas comparables - la gloire les surpasse","Elles sont plus grandes","Elles sont permanentes"]', 1, 'Les souffrances présentes ne sont pas comparables à la gloire à venir.', 'Romains 8:18'),
('rom_058', 'ROM', 'Qu''attend la création ?', '["Sa destruction","La révélation des fils de Dieu","Rien","Le jugement"]', 1, 'La création attend avec un ardent désir la révélation des fils de Dieu.', 'Romains 8:19'),
('rom_059', 'ROM', 'Comment l''Esprit nous aide-t-il dans notre faiblesse ?', '["Il ne nous aide pas","Il intercède pour nous par des soupirs inexprimables","Il nous abandonne","Il nous critique"]', 1, 'L''Esprit nous aide dans notre faiblesse et intercède pour nous.', 'Romains 8:26'),
('rom_060', 'ROM', 'Toutes choses concourent au bien de qui ?', '["De tous","De ceux qui aiment Dieu, appelés selon son dessein","Des riches","Des forts"]', 1, 'Toutes choses concourent au bien de ceux qui aiment Dieu.', 'Romains 8:28'),
('rom_061', 'ROM', 'Si Dieu est pour nous, qui sera contre nous ?', '["Beaucoup","Personne ne peut réussir contre nous","Quelques-uns","Satan"]', 1, 'Si Dieu est pour nous, qui sera contre nous?', 'Romains 8:31'),
('rom_062', 'ROM', 'Qui peut nous séparer de l''amour de Christ ?', '["La tribulation","Rien ne peut nous séparer - ni mort, ni vie, ni anges, ni rien","La faiblesse","Le péché"]', 1, 'Rien ne pourra nous séparer de l''amour de Dieu en Jésus-Christ.', 'Romains 8:35-39'),

-- Israël et l'élection (Chapitres 9-11)
('rom_063', 'ROM', 'Quelle tristesse Paul a-t-il pour Israël ?', '["Aucune","Une grande tristesse et une douleur continuelle pour ses frères selon la chair","De la colère","De l''indifférence"]', 1, 'Paul a une grande tristesse pour ses frères israélites.', 'Romains 9:2-3'),
('rom_064', 'ROM', 'Tous les descendants d''Israël sont-ils vraiment Israël ?', '["Oui","Non, ce ne sont pas tous ceux qui descendent d''Israël qui sont Israël","Parfois","Les circoncis seulement"]', 1, 'Ce ne sont pas tous ceux qui descendent d''Israël qui sont Israël.', 'Romains 9:6'),
('rom_065', 'ROM', 'Sur quel principe Dieu a-t-il choisi Jacob plutôt qu''Ésaü ?', '["Les œuvres","Avant leur naissance, selon son libre choix","La force","La sagesse"]', 1, 'L''aîné sera assujetti au plus jeune - avant qu''ils aient fait ni bien ni mal.', 'Romains 9:11-12'),
('rom_066', 'ROM', 'Dieu est-il injuste dans son choix ?', '["Oui","Loin de là! Il fait miséricorde à qui il veut","Parfois","C''est discutable"]', 1, 'Dieu n''est pas injuste; il fait miséricorde à qui il veut.', 'Romains 9:14-15'),
('rom_067', 'ROM', 'La parole de Dieu est-elle tombée à terre concernant Israël ?', '["Oui","Non, car un reste selon l''élection de la grâce existe","Partiellement","C''est fini"]', 1, 'Il y a un reste selon l''élection de la grâce.', 'Romains 11:5'),
('rom_068', 'ROM', 'Pourquoi Israël n''a-t-il pas atteint la justice ?', '["Ils étaient faibles","Ils l''ont cherchée par les œuvres, non par la foi","Ils étaient ignorants","Dieu les a rejetés"]', 1, 'Israël a cherché la justice par les œuvres et non par la foi.', 'Romains 9:31-32'),
('rom_069', 'ROM', 'Que confesse-t-on pour être sauvé ?', '["Ses péchés seulement","Que Jésus est Seigneur et qu''on croit en sa résurrection","Sa faiblesse","Son appartenance"]', 1, 'Si tu confesses de ta bouche le Seigneur Jésus et crois qu''il est ressuscité.', 'Romains 10:9'),
('rom_070', 'ROM', 'Comment la foi vient-elle ?', '["Par les œuvres","Par ce qu''on entend, et ce qu''on entend par la parole de Christ","Par la vue","Par les miracles"]', 1, 'La foi vient de ce qu''on entend, et ce qu''on entend vient de la parole de Christ.', 'Romains 10:17'),
('rom_071', 'ROM', 'Dieu a-t-il rejeté définitivement Israël ?', '["Oui","Loin de là! Il y a un reste, et tout Israël sera sauvé","Partiellement","Pour toujours"]', 1, 'Dieu n''a pas rejeté son peuple; tout Israël sera sauvé.', 'Romains 11:1,26'),
('rom_072', 'ROM', 'Pourquoi les païens ont-ils été greffés ?', '["Par mérite","Par la foi, à cause de l''endurcissement partiel d''Israël","Par leurs œuvres","Par leur nombre"]', 1, 'Un endurcissement partiel est arrivé à Israël, et les païens sont entrés par la foi.', 'Romains 11:20,25'),
('rom_073', 'ROM', 'Les dons et l''appel de Dieu sont-ils irrévocables ?', '["Non","Oui, Dieu ne se repent pas de ses dons","Parfois","Pour certains seulement"]', 1, 'Les dons et l''appel de Dieu sont irrévocables.', 'Romains 11:29'),
('rom_074', 'ROM', 'Comment Paul termine-t-il cette section sur Israël ?', '["Par une plainte","Par une doxologie: Ô profondeur des richesses de Dieu!","Par une prière","Par un ordre"]', 1, 'Ô profondeur des richesses, de la sagesse et de la science de Dieu!', 'Romains 11:33'),

-- La vie chrétienne pratique (Chapitres 12-15)
('rom_075', 'ROM', 'Qu''est-ce que le culte raisonnable ?', '["Les cérémonies","Offrir nos corps comme un sacrifice vivant, saint et agréable","Les prières seulement","Les dîmes"]', 1, 'Offrez vos corps comme un sacrifice vivant - c''est votre culte raisonnable.', 'Romains 12:1'),
('rom_076', 'ROM', 'Que ne devons-nous pas faire avec ce monde ?', '["L''aimer","Nous conformer au siècle présent, mais être transformés","Le fuir complètement","Le dominer"]', 1, 'Ne vous conformez pas au siècle présent, mais soyez transformés.', 'Romains 12:2'),
('rom_077', 'ROM', 'Comment devons-nous nous estimer ?', '["Très haut","Avec modestie, selon la mesure de foi que Dieu a donnée","Très bas","Sans importance"]', 1, 'Ne pas avoir de soi une trop haute opinion, mais une sage modestie.', 'Romains 12:3'),
('rom_078', 'ROM', 'Comment le corps de Christ fonctionne-t-il ?', '["Individuellement","Plusieurs membres en un seul corps, chacun avec des dons différents","Tous pareils","Sans organisation"]', 1, 'Nous formons un seul corps en Christ avec des dons différents.', 'Romains 12:4-6'),
('rom_079', 'ROM', 'Que faire envers celui qui nous fait du mal ?', '["Se venger","Ne rendez à personne le mal pour le mal; vainquez le mal par le bien","L''ignorer","Le fuir"]', 1, 'Ne rendez à personne le mal pour le mal; soyez vainqueurs du mal par le bien.', 'Romains 12:17,21'),
('rom_080', 'ROM', 'Comment traiter les ennemis affamés ou assoiffés ?', '["Les ignorer","Leur donner à manger et à boire - des charbons ardents sur leur tête","Les punir","Les éviter"]', 1, 'Si ton ennemi a faim, donne-lui à manger; s''il a soif, donne-lui à boire.', 'Romains 12:20'),
('rom_081', 'ROM', 'Quelle attitude avoir envers les autorités ?', '["La rébellion","La soumission car elles sont établies par Dieu","L''indifférence","Le mépris"]', 1, 'Que toute personne soit soumise aux autorités car elles sont établies par Dieu.', 'Romains 13:1'),
('rom_082', 'ROM', 'Quelle dette devons-nous toujours avoir ?', '["Aucune dette","La dette de l''amour les uns envers les autres","Une dette financière","Une dette sociale"]', 1, 'Ne devez rien à personne, sinon de vous aimer les uns les autres.', 'Romains 13:8'),
('rom_083', 'ROM', 'Quel commandement résume tous les autres ?', '["Ne pas voler","Tu aimeras ton prochain comme toi-même","Ne pas mentir","Honorer Dieu"]', 1, 'L''amour du prochain accomplit toute la loi.', 'Romains 13:9-10'),
('rom_084', 'ROM', 'De quoi devons-nous nous revêtir ?', '["De richesses","Du Seigneur Jésus-Christ, sans penser à la chair","De beaux vêtements","De pouvoir"]', 1, 'Revêtez-vous du Seigneur Jésus-Christ et ne vous préoccupez pas de la chair.', 'Romains 13:14'),
('rom_085', 'ROM', 'Comment traiter celui qui est faible dans la foi ?', '["Le rejeter","L''accueillir sans disputes sur les opinions","Le critiquer","L''ignorer"]', 1, 'Accueillez celui qui est faible sans discuter les opinions.', 'Romains 14:1'),
('rom_086', 'ROM', 'Pourquoi ne devons-nous pas juger notre frère ?', '["C''est interdit","Nous comparaîtrons tous devant le tribunal de Dieu","C''est impoli","C''est inutile"]', 1, 'Nous comparaîtrons tous devant le tribunal de Dieu.', 'Romains 14:10'),
('rom_087', 'ROM', 'Qu''est-ce que le royaume de Dieu ?', '["Le manger et le boire","La justice, la paix et la joie par le Saint-Esprit","Le pouvoir","La richesse"]', 1, 'Le royaume de Dieu n''est pas le manger et le boire, mais justice, paix et joie.', 'Romains 14:17'),
('rom_088', 'ROM', 'Que devons-nous rechercher ?', '["Notre propre intérêt","Ce qui contribue à la paix et à l''édification mutuelle","La richesse","Le pouvoir"]', 1, 'Recherchons ce qui contribue à la paix et à l''édification mutuelle.', 'Romains 14:19'),
('rom_089', 'ROM', 'Les forts doivent-ils plaire à eux-mêmes ?', '["Oui","Non, nous devons supporter les faiblesses des faibles","Parfois","S''ils le méritent"]', 1, 'Nous qui sommes forts, devons supporter les faiblesses des faibles.', 'Romains 15:1'),
('rom_090', 'ROM', 'Pourquoi Christ ne s''est-il pas plu à lui-même ?', '["Par faiblesse","Pour nous donner l''exemple de supporter les reproches","Par obligation","Sans raison"]', 1, 'Christ n''a pas cherché à se plaire à lui-même.', 'Romains 15:3'),
('rom_091', 'ROM', 'Que Dieu de persévérance et de consolation donne-t-il ?', '["La richesse","D''avoir les mêmes sentiments selon Jésus-Christ","Le pouvoir","La gloire terrestre"]', 1, 'Que Dieu vous donne d''être unanimes pour glorifier Dieu.', 'Romains 15:5-6'),
('rom_092', 'ROM', 'Pourquoi devons-nous nous accueillir les uns les autres ?', '["Par politesse","Comme Christ nous a accueillis, pour la gloire de Dieu","Par obligation","Par habitude"]', 1, 'Accueillez-vous les uns les autres comme Christ vous a accueillis.', 'Romains 15:7'),

-- Salutations finales (Chapitre 16)
('rom_093', 'ROM', 'Qui Paul recommande-t-il comme diaconesse ?', '["Marie","Phœbé, notre sœur, diaconesse de l''église de Cenchrées","Priscille","Junias"]', 1, 'Je vous recommande Phœbé, diaconesse de l''église de Cenchrées.', 'Romains 16:1'),
('rom_094', 'ROM', 'Qui étaient Priscille et Aquilas ?', '["Des inconnus","Des collaborateurs de Paul qui ont risqué leur vie pour lui","Des ennemis","Des Juifs incroyants"]', 1, 'Priscille et Aquilas, mes compagnons d''œuvre, ont exposé leur tête pour ma vie.', 'Romains 16:3-4'),
('rom_095', 'ROM', 'De quoi Paul met-il en garde ?', '["De la pauvreté","De ceux qui causent des divisions contrairement à l''enseignement reçu","De la maladie","Des Romains"]', 1, 'Prenez garde à ceux qui causent des divisions et des scandales.', 'Romains 16:17'),
('rom_096', 'ROM', 'Que Dieu de paix fera-t-il à Satan ?', '["Le pardonner","L''écrasera bientôt sous vos pieds","L''ignorer","Le convertir"]', 1, 'Le Dieu de paix écrasera bientôt Satan sous vos pieds.', 'Romains 16:20'),
('rom_097', 'ROM', 'Comment l''épître se termine-t-elle ?', '["Par des plaintes","Par une doxologie: À Dieu seul sage soit la gloire par Jésus-Christ","Par des ordres","Par des questions"]', 1, 'À Dieu seul sage soit la gloire, aux siècles des siècles! Amen.', 'Romains 16:27');

