INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('mat_q1', 'MAT', 'Dans quelle ville Jésus est-il né ?', 'Nazareth', 'Jérusalem', 'Bethléem', 'Capernaüm', 2, 'Jésus est né à Bethléem de Judée, comme prophétisé par Michée.', 'Matthieu 2:1'),
('mat_q2', 'MAT', 'Qui vinrent adorer Jésus à sa naissance ?', 'Les bergers', 'Les mages', 'Les prêtres', 'Les anges', 1, 'Des mages d''Orient vinrent adorer l''enfant Jésus.', 'Matthieu 2:1-2'),
('mat_q3', 'MAT', 'Où la famille de Jésus s''enfuit-elle ?', 'En Syrie', 'En Égypte', 'En Grèce', 'En Perse', 1, 'Joseph emmena l''enfant et sa mère en Égypte pour fuir Hérode.', 'Matthieu 2:14'),
('mat_q4', 'MAT', 'Qui a baptisé Jésus ?', 'Pierre', 'Jean-Baptiste', 'André', 'Paul', 1, 'Jean-Baptiste baptisa Jésus dans le Jourdain.', 'Matthieu 3:13'),
('mat_q5', 'MAT', 'Combien de jours Jésus a-t-il jeûné dans le désert ?', '7 jours', '21 jours', '40 jours', '70 jours', 2, 'Jésus jeûna 40 jours et 40 nuits dans le désert.', 'Matthieu 4:2'),
('mat_q6', 'MAT', 'Combien de béatitudes y a-t-il ?', '5', '7', '8', '10', 2, 'Il y a 8 béatitudes dans le Sermon sur la montagne.', 'Matthieu 5:3-12'),
('mat_q7', 'MAT', 'Comment commence la prière du Notre Père ?', 'Notre Père qui es aux cieux', 'Seigneur, écoute-nous', 'Dieu tout-puissant', 'Éternel, aie pitié', 0, 'Jésus enseigna à prier : Notre Père qui es aux cieux...', 'Matthieu 6:9'),
('mat_q8', 'MAT', 'Combien de disciples Jésus a-t-il choisis ?', '7', '10', '12', '70', 2, 'Jésus choisit 12 apôtres.', 'Matthieu 10:1-4'),
('mat_q9', 'MAT', 'Qui a trahi Jésus ?', 'Pierre', 'Thomas', 'Judas', 'Jean', 2, 'Judas Iscariot trahit Jésus pour 30 pièces d''argent.', 'Matthieu 26:14-16'),
('mat_q10', 'MAT', 'Combien de fois Pierre a-t-il renié Jésus ?', '1 fois', '2 fois', '3 fois', '7 fois', 2, 'Pierre renia Jésus trois fois avant le chant du coq.', 'Matthieu 26:69-75'),
('mat_q11', 'MAT', 'Qui demanda le corps de Jésus après la crucifixion ?', 'Pierre', 'Joseph d''Arimathée', 'Nicodème', 'Jean', 1, 'Joseph d''Arimathée demanda à Pilate le corps de Jésus.', 'Matthieu 27:57-58'),
('mat_q12', 'MAT', 'Que dit Jésus à la fin de Matthieu ?', 'Restez à Jérusalem', 'Allez, faites de toutes les nations des disciples', 'Priez sans cesse', 'Aimez-vous les uns les autres', 1, 'C''est le Grand Commandement missionnaire.', 'Matthieu 28:19'),
('mat_q13', 'MAT', 'Quel disciple marcha sur l''eau avec Jésus ?', 'Jean', 'Jacques', 'Pierre', 'André', 2, 'Pierre marcha sur l''eau vers Jésus.', 'Matthieu 14:29'),
('mat_q14', 'MAT', 'Comment Jésus multiplia-t-il les pains ?', 'Avec 5 pains et 2 poissons', 'Avec 7 pains et 3 poissons', 'Avec 3 pains et 5 poissons', 'Avec 2 pains et 2 poissons', 0, 'Jésus nourrit 5000 hommes avec 5 pains et 2 poissons.', 'Matthieu 14:17-21'),
('mat_q15', 'MAT', 'Que dit Pierre à Jésus à Césarée de Philippe ?', 'Tu es un prophète', 'Tu es le Christ, le Fils du Dieu vivant', 'Tu es Élie', 'Tu es Jean-Baptiste', 1, 'Pierre confessa que Jésus est le Christ.', 'Matthieu 16:16');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('mrk_q1', 'MRK', 'Par quoi commence l''évangile de Marc ?', 'La naissance de Jésus', 'Le ministère de Jean-Baptiste', 'La généalogie', 'L''enfance de Jésus', 1, 'Marc commence directement avec Jean-Baptiste.', 'Marc 1:1-4'),
('mrk_q2', 'MRK', 'Quel est le thème principal de Marc ?', 'Jésus comme roi', 'Jésus comme serviteur', 'Jésus comme prophète', 'Jésus comme prêtre', 1, 'Marc présente Jésus comme le Serviteur souffrant.', 'Marc 10:45'),
('mrk_q3', 'MRK', 'Qui était Lévi dans Marc ?', 'Un pécheur', 'Un collecteur d''impôts', 'Un prêtre', 'Un pharisien', 1, 'Lévi (Matthieu) était collecteur d''impôts.', 'Marc 2:14'),
('mrk_q4', 'MRK', 'Qui Jésus ressuscita-t-il dans Marc 5 ?', 'Lazare', 'La fille de Jaïrus', 'Le fils de la veuve', 'Un jeune homme', 1, 'Jésus ressuscita la fille de Jaïrus, chef de synagogue.', 'Marc 5:41-42'),
('mrk_q5', 'MRK', 'Où Jésus fut-il transfiguré ?', 'Mont Sinaï', 'Mont des Oliviers', 'Une haute montagne', 'Mont Hermon', 2, 'Jésus fut transfiguré sur une haute montagne.', 'Marc 9:2'),
('mrk_q6', 'MRK', 'Qui apparut avec Jésus lors de la transfiguration ?', 'Abraham et Isaac', 'Moïse et Élie', 'David et Salomon', 'Pierre et Jean', 1, 'Moïse et Élie apparurent avec Jésus.', 'Marc 9:4'),
('mrk_q7', 'MRK', 'Que fit Jésus dans le temple ?', 'Il enseigna', 'Il chassa les marchands', 'Il pria', 'Il guérit des malades', 1, 'Jésus chassa ceux qui vendaient et achetaient.', 'Marc 11:15'),
('mrk_q8', 'MRK', 'Quel est le plus grand commandement selon Marc ?', 'Tu aimeras ton prochain', 'Tu aimeras le Seigneur ton Dieu', 'Tu honoreras ton père', 'Tu ne voleras pas', 1, 'Tu aimeras le Seigneur ton Dieu de tout ton cœur.', 'Marc 12:30'),
('mrk_q9', 'MRK', 'Qui porta la croix de Jésus ?', 'Pierre', 'Simon de Cyrène', 'Jean', 'Un soldat', 1, 'Simon de Cyrène fut contraint de porter la croix.', 'Marc 15:21'),
('mrk_q10', 'MRK', 'Qui fut le premier à voir le tombeau vide ?', 'Pierre', 'Jean', 'Marie de Magdala et deux autres', 'Les soldats', 2, 'Les femmes allèrent au tombeau de bon matin.', 'Marc 16:1-4'),
('mrk_q11', 'MRK', 'Comment Jésus appela-t-il les 4 premiers disciples ?', 'Venez et voyez', 'Suivez-moi', 'Croyez en moi', 'Écoutez-moi', 1, 'Jésus dit : Suivez-moi et je vous ferai pêcheurs d''hommes.', 'Marc 1:17'),
('mrk_q12', 'MRK', 'Qui était aveugle à Jéricho ?', 'Bartimée', 'Zachée', 'Lazare', 'Siméon', 0, 'Bartimée cria à Jésus : Fils de David, aie pitié de moi !', 'Marc 10:46-52');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('luk_q1', 'LUK', 'À qui Luc adresse-t-il son évangile ?', 'À Timothée', 'À Théophile', 'À Pierre', 'À Paul', 1, 'Luc écrit à Théophile.', 'Luc 1:3'),
('luk_q2', 'LUK', 'Quel ange annonça la naissance de Jean-Baptiste ?', 'Michel', 'Gabriel', 'Raphaël', 'Uriel', 1, 'L''ange Gabriel apparut à Zacharie.', 'Luc 1:19'),
('luk_q3', 'LUK', 'Quel cantique Marie a-t-elle chanté ?', 'Le Benedictus', 'Le Magnificat', 'Le Nunc Dimittis', 'Le Gloria', 1, 'Marie chanta le Magnificat : Mon âme exalte le Seigneur.', 'Luc 1:46-55'),
('luk_q4', 'LUK', 'Qui annonça la naissance de Jésus aux bergers ?', 'Une étoile', 'Un ange', 'Jean-Baptiste', 'Un prophète', 1, 'Un ange du Seigneur leur apparut.', 'Luc 2:9-10'),
('luk_q5', 'LUK', 'Où Jésus fut-il présenté au temple ?', 'À Nazareth', 'À Bethléem', 'À Jérusalem', 'À Capernaüm', 2, 'Marie et Joseph amenèrent Jésus au temple de Jérusalem.', 'Luc 2:22'),
('luk_q6', 'LUK', 'Quel âge avait Jésus quand il enseigna au temple ?', '8 ans', '10 ans', '12 ans', '15 ans', 2, 'À 12 ans, Jésus enseignait les docteurs au temple.', 'Luc 2:42-46'),
('luk_q7', 'LUK', 'Quelle parabole parle d''un fils qui gaspille ?', 'Le bon Samaritain', 'Le fils prodigue', 'Le semeur', 'Les talents', 1, 'La parabole du fils prodigue est unique à Luc.', 'Luc 15:11-32'),
('luk_q8', 'LUK', 'Qui était Zachée ?', 'Un pécheur', 'Un chef des publicains', 'Un pharisien', 'Un prêtre', 1, 'Zachée était un chef des publicains et il était riche.', 'Luc 19:2'),
('luk_q9', 'LUK', 'Où Zachée monta-t-il pour voir Jésus ?', 'Sur un mur', 'Sur un toit', 'Dans un sycomore', 'Sur une colline', 2, 'Zachée monta sur un sycomore à cause de sa petite taille.', 'Luc 19:4'),
('luk_q10', 'LUK', 'Que dit Jésus au brigand sur la croix ?', 'Tu seras pardonné', 'Aujourd''hui tu seras avec moi au paradis', 'Repens-toi', 'Tu seras jugé', 1, 'Jésus promit le paradis au brigand repentant.', 'Luc 23:43'),
('luk_q11', 'LUK', 'Où Jésus apparut-il à deux disciples après sa résurrection ?', 'À Jérusalem', 'Sur le chemin d''Emmaüs', 'En Galilée', 'À Béthanie', 1, 'Jésus apparut aux disciples d''Emmaüs.', 'Luc 24:13-15'),
('luk_q12', 'LUK', 'Qui était le publicain dans la parabole ?', 'Un homme humble', 'Un homme orgueilleux', 'Un prêtre', 'Un pharisien', 0, 'Le publicain se tenait humble, demandant pardon.', 'Luc 18:13'),
('luk_q13', 'LUK', 'Combien de lépreux Jésus guérit-il ?', '1', '5', '10', '12', 2, 'Jésus guérit 10 lépreux, mais un seul revint remercier.', 'Luc 17:12-17'),
('luk_q14', 'LUK', 'Dans la parabole, qui aida l''homme blessé ?', 'Un prêtre', 'Un lévite', 'Un Samaritain', 'Un Pharisien', 2, 'Le bon Samaritain secourut l''homme blessé.', 'Luc 10:33-35'),
('luk_q15', 'LUK', 'Où Jésus fut-il élevé au ciel ?', 'Au mont des Oliviers', 'À Béthanie', 'À Jérusalem', 'En Galilée', 1, 'Jésus les conduisit vers Béthanie et fut élevé au ciel.', 'Luc 24:50-51');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('jhn_q1', 'JHN', 'Comment Jean décrit-il Jésus au début ?', 'Le Fils de David', 'La Parole', 'Le Messie', 'Le Prophète', 1, 'Au commencement était la Parole (le Logos).', 'Jean 1:1'),
('jhn_q2', 'JHN', 'Quel fut le premier miracle de Jésus ?', 'Guérir un aveugle', 'Changer l''eau en vin', 'Multiplier les pains', 'Marcher sur l''eau', 1, 'Jésus changea l''eau en vin aux noces de Cana.', 'Jean 2:1-11'),
('jhn_q3', 'JHN', 'Qui vint voir Jésus de nuit ?', 'Joseph d''Arimathée', 'Nicodème', 'Gamaliel', 'Un centurion', 1, 'Nicodème vint trouver Jésus de nuit.', 'Jean 3:1-2'),
('jhn_q4', 'JHN', 'Quel verset parle de l''amour de Dieu pour le monde ?', 'Jean 1:1', 'Jean 3:16', 'Jean 11:35', 'Jean 14:6', 1, 'Car Dieu a tant aimé le monde...', 'Jean 3:16'),
('jhn_q5', 'JHN', 'À quel puits Jésus rencontra-t-il une femme ?', 'Le puits de Beer-Shéba', 'Le puits de Jacob', 'Le puits de Moïse', 'Le puits d''Abraham', 1, 'Jésus rencontra la Samaritaine au puits de Jacob.', 'Jean 4:6'),
('jhn_q6', 'JHN', 'Que dit Jésus de lui-même en Jean 14:6 ?', 'Je suis le bon berger', 'Je suis la porte', 'Je suis le chemin, la vérité et la vie', 'Je suis la lumière du monde', 2, 'Jésus dit : Je suis le chemin, la vérité et la vie.', 'Jean 14:6'),
('jhn_q7', 'JHN', 'Qui Jésus ressuscita-t-il à Béthanie ?', 'Le fils de la veuve', 'Lazare', 'La fille de Jaïrus', 'Un jeune homme', 1, 'Jésus ressuscita son ami Lazare après 4 jours.', 'Jean 11:43-44'),
('jhn_q8', 'JHN', 'Quel est le verset le plus court de la Bible ?', 'Jean 11:35', 'Jean 3:16', 'Jean 1:1', 'Jean 14:6', 0, 'Jésus pleura (Jean 11:35 en grec).', 'Jean 11:35'),
('jhn_q9', 'JHN', 'Qui lava les pieds des disciples ?', 'Pierre', 'Jean', 'Jésus', 'Un serviteur', 2, 'Jésus se ceignit et lava les pieds de ses disciples.', 'Jean 13:4-5'),
('jhn_q10', 'JHN', 'Quel disciple douta de la résurrection ?', 'Pierre', 'Jean', 'Thomas', 'Philippe', 2, 'Thomas dit qu''il ne croirait que s''il voyait les marques.', 'Jean 20:25'),
('jhn_q11', 'JHN', 'Combien de "Je suis" y a-t-il dans Jean ?', '5', '6', '7', '8', 2, 'Il y a 7 déclarations "Je suis" dans l''évangile de Jean.', 'Jean'),
('jhn_q12', 'JHN', 'Qui renia Jésus dans la cour du grand prêtre ?', 'Jean', 'Pierre', 'Thomas', 'Jacques', 1, 'Pierre renia Jésus trois fois.', 'Jean 18:15-27'),
('jhn_q13', 'JHN', 'À qui Jésus confia-t-il sa mère à la croix ?', 'À Pierre', 'Au disciple qu''il aimait', 'À Jacques', 'À Joseph', 1, 'Jésus confia Marie à Jean, le disciple qu''il aimait.', 'Jean 19:26-27'),
('jhn_q14', 'JHN', 'Que dit Jésus à Thomas ?', 'Tu as douté', 'Heureux ceux qui croient sans avoir vu', 'Va et ne pèche plus', 'Suis-moi', 1, 'Jésus dit que ceux qui croient sans voir sont bénis.', 'Jean 20:29'),
('jhn_q15', 'JHN', 'Combien de poissons les disciples prirent-ils ?', '100', '120', '153', '200', 2, 'Le filet contenait 153 gros poissons.', 'Jean 21:11');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('act_q1', 'ACT', 'Qui a écrit le livre des Actes ?', 'Paul', 'Pierre', 'Luc', 'Jean', 2, 'Luc, l''auteur de l''évangile, a aussi écrit les Actes.', 'Actes 1:1'),
('act_q2', 'ACT', 'Quel événement est célébré à la Pentecôte ?', 'La résurrection', 'La venue du Saint-Esprit', 'L''ascension', 'La naissance de Jésus', 1, 'Le Saint-Esprit descendit sur les disciples à la Pentecôte.', 'Actes 2:1-4'),
('act_q3', 'ACT', 'Combien furent baptisés le jour de la Pentecôte ?', '1000', '2000', '3000', '5000', 2, 'Environ 3000 personnes furent baptisées ce jour-là.', 'Actes 2:41'),
('act_q4', 'ACT', 'Qui fut le premier martyr chrétien ?', 'Pierre', 'Étienne', 'Jacques', 'Paul', 1, 'Étienne fut lapidé pour son témoignage.', 'Actes 7:59-60'),
('act_q5', 'ACT', 'Comment s''appelait Saul après sa conversion ?', 'Pierre', 'Jean', 'Paul', 'Silas', 2, 'Saul devint Paul après sa conversion.', 'Actes 13:9'),
('act_q6', 'ACT', 'Sur quel chemin Saul rencontra-t-il Jésus ?', 'Le chemin de Jérusalem', 'Le chemin de Damas', 'Le chemin de Rome', 'Le chemin d''Antioche', 1, 'Saul rencontra Jésus sur le chemin de Damas.', 'Actes 9:3'),
('act_q7', 'ACT', 'Qui était le premier païen converti ?', 'Lydie', 'Corneille', 'Le geôlier', 'Sergius Paulus', 1, 'Corneille, centurion romain, fut le premier païen converti.', 'Actes 10:44-48'),
('act_q8', 'ACT', 'Où les disciples furent-ils appelés chrétiens pour la première fois ?', 'Jérusalem', 'Antioche', 'Rome', 'Corinthe', 1, 'C''est à Antioche qu''on les appela chrétiens.', 'Actes 11:26'),
('act_q9', 'ACT', 'Qui accompagna Paul lors de son premier voyage ?', 'Silas', 'Timothée', 'Barnabas', 'Luc', 2, 'Barnabas accompagna Paul lors du premier voyage.', 'Actes 13:2'),
('act_q10', 'ACT', 'Qui fut libéré de prison par un tremblement de terre ?', 'Pierre', 'Paul et Silas', 'Jean', 'Étienne', 1, 'Paul et Silas furent libérés miraculeusement.', 'Actes 16:25-26'),
('act_q11', 'ACT', 'Quelle femme vendeuse de pourpre fut convertie ?', 'Marie', 'Lydie', 'Priscille', 'Dorcas', 1, 'Lydie, de Thyatire, fut convertie à Philippes.', 'Actes 16:14'),
('act_q12', 'ACT', 'Où Paul prêcha-t-il sur le Dieu inconnu ?', 'À Rome', 'À Corinthe', 'À Athènes', 'À Éphèse', 2, 'Paul prêcha sur l''Aréopage d''Athènes.', 'Actes 17:22-23'),
('act_q13', 'ACT', 'Combien de voyages missionnaires Paul fit-il ?', '2', '3', '4', '5', 1, 'Paul fit 3 voyages missionnaires.', 'Actes'),
('act_q14', 'ACT', 'Où Paul fut-il emprisonné à la fin des Actes ?', 'Jérusalem', 'Césarée', 'Rome', 'Athènes', 2, 'Paul fut emprisonné à Rome en attente de son procès.', 'Actes 28:16'),
('act_q15', 'ACT', 'Qui fabriquait des tentes avec Paul ?', 'Barnabas', 'Silas', 'Aquilas et Priscille', 'Timothée', 2, 'Paul travaillait avec Aquilas et Priscille.', 'Actes 18:2-3');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('rom_q1', 'ROM', 'Qui a écrit l''épître aux Romains ?', 'Pierre', 'Jean', 'Paul', 'Jacques', 2, 'Paul a écrit Romains depuis Corinthe.', 'Romains 1:1'),
('rom_q2', 'ROM', 'Selon Romains 3:23, qui a péché ?', 'Les Juifs seulement', 'Les païens seulement', 'Tous', 'Personne', 2, 'Tous ont péché et sont privés de la gloire de Dieu.', 'Romains 3:23'),
('rom_q3', 'ROM', 'Comment sommes-nous justifiés selon Romains ?', 'Par les œuvres', 'Par la foi', 'Par la loi', 'Par les sacrifices', 1, 'Nous sommes justifiés par la foi en Jésus-Christ.', 'Romains 5:1'),
('rom_q4', 'ROM', 'Quel chapitre parle de la vie selon l''Esprit ?', 'Romains 5', 'Romains 6', 'Romains 8', 'Romains 12', 2, 'Romains 8 parle de la vie selon l''Esprit.', 'Romains 8'),
('rom_q5', 'ROM', 'Que dit Romains 8:28 ?', 'Tout est permis', 'Toutes choses concourent au bien', 'La loi est bonne', 'L''amour est patient', 1, 'Toutes choses concourent au bien de ceux qui aiment Dieu.', 'Romains 8:28'),
('rom_q6', 'ROM', 'Qu''est-ce qui ne peut nous séparer de l''amour de Christ ?', 'Rien', 'Le péché', 'La mort', 'Les anges', 0, 'Rien ne peut nous séparer de l''amour de Dieu.', 'Romains 8:38-39'),
('rom_q7', 'ROM', 'Que devons-nous présenter à Dieu selon Romains 12 ?', 'Nos offrandes', 'Nos corps comme sacrifice vivant', 'Nos prières', 'Nos dîmes', 1, 'Présentez vos corps comme un sacrifice vivant.', 'Romains 12:1'),
('rom_q8', 'ROM', 'Comment obtient-on la foi selon Romains ?', 'Par les œuvres', 'Par la prédication de la Parole', 'Par les miracles', 'Par la méditation', 1, 'La foi vient de ce qu''on entend, et on entend par la Parole.', 'Romains 10:17'),
('rom_q9', 'ROM', 'Que dit Romains sur le salaire du péché ?', 'C''est la souffrance', 'C''est la mort', 'C''est la pauvreté', 'C''est la maladie', 1, 'Le salaire du péché, c''est la mort.', 'Romains 6:23'),
('rom_q10', 'ROM', 'Quel don gratuit Dieu donne-t-il ?', 'La richesse', 'La vie éternelle', 'La santé', 'La sagesse', 1, 'Le don gratuit de Dieu, c''est la vie éternelle.', 'Romains 6:23'),
('rom_q11', 'ROM', 'Que dit Romains 10:9 pour être sauvé ?', 'Faire de bonnes œuvres', 'Confesser Jésus comme Seigneur', 'Être baptisé', 'Donner aux pauvres', 1, 'Si tu confesses Jésus comme Seigneur et crois, tu seras sauvé.', 'Romains 10:9'),
('rom_q12', 'ROM', 'Comment devons-nous traiter les autorités selon Romains 13 ?', 'Les ignorer', 'Nous soumettre à elles', 'Les combattre', 'Les critiquer', 1, 'Que toute personne soit soumise aux autorités supérieures.', 'Romains 13:1');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('1co_q1', '1CO', 'Quel problème l''église de Corinthe avait-elle ?', 'La persécution', 'Les divisions', 'La pauvreté', 'L''isolement', 1, 'L''église était divisée entre différents leaders.', '1 Corinthiens 1:10-12'),
('1co_q2', '1CO', 'Quel chapitre parle de l''amour ?', '1 Corinthiens 11', '1 Corinthiens 12', '1 Corinthiens 13', '1 Corinthiens 15', 2, '1 Corinthiens 13 est le chapitre de l''amour.', '1 Corinthiens 13'),
('1co_q3', '1CO', 'Selon 1 Corinthiens 13, qu''est-ce qui demeure ?', 'Les prophéties', 'Les langues', 'La foi, l''espérance et l''amour', 'Les miracles', 2, 'Ces trois choses demeurent, mais la plus grande c''est l''amour.', '1 Corinthiens 13:13'),
('1co_q4', '1CO', 'Quel chapitre parle de la résurrection ?', '1 Corinthiens 12', '1 Corinthiens 13', '1 Corinthiens 14', '1 Corinthiens 15', 3, '1 Corinthiens 15 traite de la résurrection des morts.', '1 Corinthiens 15'),
('1co_q5', '1CO', 'Que dit Paul sur les dons spirituels ?', 'Ils sont inutiles', 'Ils sont divers mais viennent du même Esprit', 'Ils sont pour les apôtres seulement', 'Ils ont cessé', 1, 'Il y a diversité de dons, mais le même Esprit.', '1 Corinthiens 12:4'),
('1co_q6', '1CO', 'Comment Paul décrit-il l''église ?', 'Comme un bâtiment', 'Comme un corps', 'Comme une armée', 'Comme une famille', 1, 'L''église est le corps de Christ avec plusieurs membres.', '1 Corinthiens 12:12-27'),
('1co_q7', '1CO', 'Que dit Paul sur la Cène ?', 'C''est une fête', 'C''est en mémoire du Seigneur', 'C''est optionnel', 'C''est pour les leaders', 1, 'Faites ceci en mémoire de moi.', '1 Corinthiens 11:24-25'),
('1co_q8', '1CO', 'Sans quoi les dons n''ont pas de valeur ?', 'Sans la foi', 'Sans l''amour', 'Sans les œuvres', 'Sans l''église', 1, 'Si je n''ai pas l''amour, je ne suis rien.', '1 Corinthiens 13:2'),
('1co_q9', '1CO', 'Que dit Paul sur la sagesse du monde ?', 'Elle est excellente', 'Elle est folie devant Dieu', 'Elle est nécessaire', 'Elle est divine', 1, 'La sagesse de ce monde est folie devant Dieu.', '1 Corinthiens 3:19'),
('1co_q10', '1CO', 'Qu''est-ce que nos corps selon Paul ?', 'Des instruments', 'Des temples du Saint-Esprit', 'Des prisons', 'Des outils', 1, 'Votre corps est le temple du Saint-Esprit.', '1 Corinthiens 6:19');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('eph_q1', 'EPH', 'D''où Paul écrit-il Éphésiens ?', 'De Corinthe', 'De Rome (prison)', 'D''Athènes', 'D''Antioche', 1, 'Éphésiens est une épître de la captivité.', 'Éphésiens 3:1'),
('eph_q2', 'EPH', 'Comment sommes-nous sauvés selon Éphésiens 2:8 ?', 'Par les œuvres', 'Par la grâce, par la foi', 'Par la loi', 'Par les sacrifices', 1, 'C''est par la grâce que vous êtes sauvés, par le moyen de la foi.', 'Éphésiens 2:8'),
('eph_q3', 'EPH', 'Quel est le thème principal d''Éphésiens ?', 'La loi', 'L''église comme corps de Christ', 'Les prophéties', 'La fin des temps', 1, 'Paul présente l''église comme le corps de Christ.', 'Éphésiens'),
('eph_q4', 'EPH', 'Que devons-nous revêtir selon Éphésiens 6 ?', 'Des vêtements blancs', 'L''armure de Dieu', 'Des habits de deuil', 'La robe de justice', 1, 'Revêtez-vous de toutes les armes de Dieu.', 'Éphésiens 6:11'),
('eph_q5', 'EPH', 'Quelles armes font partie de l''armure de Dieu ?', 'L''épée de l''Esprit', 'Le bouclier de la foi', 'Le casque du salut', 'Toutes ces réponses', 3, 'L''armure comprend plusieurs pièces spirituelles.', 'Éphésiens 6:14-17'),
('eph_q6', 'EPH', 'Comment les maris doivent-ils aimer leurs femmes ?', 'Comme eux-mêmes', 'Comme Christ a aimé l''Église', 'Comme leurs parents', 'Comme leurs amis', 1, 'Maris, aimez vos femmes comme Christ a aimé l''Église.', 'Éphésiens 5:25'),
('eph_q7', 'EPH', 'Que dit Éphésiens 4:26 sur la colère ?', 'Elle est interdite', 'Si vous vous mettez en colère, ne péchez pas', 'Elle est permise', 'Elle est nécessaire', 1, 'Si vous vous mettez en colère, ne péchez pas.', 'Éphésiens 4:26'),
('eph_q8', 'EPH', 'Combien y a-t-il d''éléments dans l''unité de l''Esprit ?', '5', '6', '7', '8', 2, 'Un seul corps, un seul Esprit, un seul Seigneur, une seule foi...', 'Éphésiens 4:4-6'),
('eph_q9', 'EPH', 'Quel commandement avec promesse Paul cite-t-il ?', 'Tu ne tueras point', 'Honore ton père et ta mère', 'Tu ne voleras point', 'Tu aimeras ton prochain', 1, 'Honore ton père et ta mère, c''est le premier commandement avec promesse.', 'Éphésiens 6:2'),
('eph_q10', 'EPH', 'Comment devons-nous marcher selon Éphésiens ?', 'Dans les ténèbres', 'Dans la lumière', 'Dans la peur', 'Dans le doute', 1, 'Marchez comme des enfants de lumière.', 'Éphésiens 5:8');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('php_q1', 'PHP', 'Quel est le thème principal de Philippiens ?', 'La souffrance', 'La joie', 'La loi', 'Les prophéties', 1, 'La joie est le thème central de cette épître.', 'Philippiens'),
('php_q2', 'PHP', 'Où Paul écrit-il Philippiens ?', 'De Corinthe', 'De Rome (prison)', 'D''Athènes', 'D''Éphèse', 1, 'Paul écrit depuis sa captivité à Rome.', 'Philippiens 1:13'),
('php_q3', 'PHP', 'Que dit Philippiens 4:13 ?', 'Je peux tout', 'Je peux tout par celui qui me fortifie', 'Je suis faible', 'Je n''ai besoin de rien', 1, 'Je peux tout par celui qui me fortifie.', 'Philippiens 4:13'),
('php_q4', 'PHP', 'Comment Jésus s''est-il dépouillé selon Philippiens 2 ?', 'De sa gloire', 'De lui-même, prenant forme de serviteur', 'De ses richesses', 'De son pouvoir', 1, 'Il s''est dépouillé lui-même, prenant une forme de serviteur.', 'Philippiens 2:7'),
('php_q5', 'PHP', 'Que devons-nous faire au lieu de nous inquiéter ?', 'Travailler plus', 'Exposer nos demandes à Dieu avec action de grâce', 'Ignorer nos problèmes', 'Demander aux autres', 1, 'Faites connaître vos besoins à Dieu par la prière.', 'Philippiens 4:6'),
('php_q6', 'PHP', 'Qu''est-ce que Paul considérait comme une perte ?', 'Son argent', 'Toutes choses à cause de Christ', 'Ses amis', 'Sa santé', 1, 'Je regarde toutes choses comme une perte à cause de Christ.', 'Philippiens 3:8'),
('php_q7', 'PHP', 'Quel sentiment Paul dit-il d''avoir selon Philippiens 2:5 ?', 'L''orgueil', 'Les mêmes sentiments qui étaient en Christ', 'La jalousie', 'L''ambition', 1, 'Ayez en vous les sentiments qui étaient en Jésus-Christ.', 'Philippiens 2:5'),
('php_q8', 'PHP', 'Que gardera nos cœurs et pensées ?', 'Notre volonté', 'La paix de Dieu', 'Notre intelligence', 'Nos amis', 1, 'La paix de Dieu gardera vos cœurs et vos pensées.', 'Philippiens 4:7');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('rev_q1', 'REV', 'Qui a écrit l''Apocalypse ?', 'Paul', 'Pierre', 'Jean', 'Jacques', 2, 'L''apôtre Jean a écrit l''Apocalypse sur l''île de Patmos.', 'Apocalypse 1:1'),
('rev_q2', 'REV', 'À combien d''églises Jean écrit-il ?', '5', '7', '10', '12', 1, 'Jean écrit aux 7 églises d''Asie.', 'Apocalypse 1:11'),
('rev_q3', 'REV', 'Qui est l''Alpha et l''Oméga ?', 'Un ange', 'Jésus-Christ', 'Jean', 'Moïse', 1, 'Je suis l''Alpha et l''Oméga, dit le Seigneur.', 'Apocalypse 1:8'),
('rev_q4', 'REV', 'Combien de sceaux sont ouverts ?', '5', '6', '7', '10', 2, 'L''Agneau ouvre les 7 sceaux.', 'Apocalypse 6-8'),
('rev_q5', 'REV', 'Combien de trompettes sonnent ?', '5', '6', '7', '10', 2, '7 anges sonnent 7 trompettes.', 'Apocalypse 8-11'),
('rev_q6', 'REV', 'Quel est le nombre de la bête ?', '777', '666', '333', '999', 1, 'Le nombre de la bête est 666.', 'Apocalypse 13:18'),
('rev_q7', 'REV', 'Qui combat le dragon ?', 'Pierre', 'Michel et ses anges', 'Jean', 'Les 24 anciens', 1, 'Michel et ses anges combattirent le dragon.', 'Apocalypse 12:7'),
('rev_q8', 'REV', 'Quelle ville descend du ciel ?', 'Babylone', 'Rome', 'La nouvelle Jérusalem', 'Athènes', 2, 'La sainte cité, la nouvelle Jérusalem, descendit du ciel.', 'Apocalypse 21:2'),
('rev_q9', 'REV', 'Combien de portes a la nouvelle Jérusalem ?', '4', '7', '10', '12', 3, 'Elle avait 12 portes, 3 de chaque côté.', 'Apocalypse 21:12-13'),
('rev_q10', 'REV', 'Qu''y a-t-il dans la nouvelle Jérusalem ?', 'Un temple', 'Un arbre de vie', 'L''arche de l''alliance', 'Le tabernacle', 1, 'L''arbre de vie est au milieu de la place.', 'Apocalypse 22:2'),
('rev_q11', 'REV', 'Quelle est la dernière prière de la Bible ?', 'Amen', 'Viens, Seigneur Jésus', 'Alléluia', 'Gloire à Dieu', 1, 'Viens, Seigneur Jésus !', 'Apocalypse 22:20'),
('rev_q12', 'REV', 'Qu''est-ce qui n''existera plus dans la nouvelle Jérusalem ?', 'La joie', 'La mort, le deuil, les pleurs', 'L''amour', 'La vie', 1, 'Il n''y aura plus de mort, ni de deuil, ni de cri.', 'Apocalypse 21:4'),
('rev_q13', 'REV', 'Combien d''anciens entourent le trône ?', '12', '24', '7', '144', 1, '24 anciens sont assis autour du trône.', 'Apocalypse 4:4'),
('rev_q14', 'REV', 'Qui sont les 144 000 ?', 'Des anges', 'Des serviteurs de Dieu scellés', 'Des prophètes', 'Des rois', 1, '144 000 serviteurs de Dieu sont scellés.', 'Apocalypse 7:4'),
('rev_q15', 'REV', 'Comment est décrit Jésus au chapitre 1 ?', 'Comme un agneau', 'Comme un lion', 'Avec des cheveux blancs et des yeux de feu', 'Comme un berger', 2, 'Sa tête et ses cheveux étaient blancs, ses yeux comme du feu.', 'Apocalypse 1:14');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('1jn_q1', '1JN', 'Quel est le thème principal de 1 Jean ?', 'La loi', 'L''amour et la communion', 'Les prophéties', 'La fin des temps', 1, '1 Jean parle de l''amour de Dieu et de la communion fraternelle.', '1 Jean'),
('1jn_q2', '1JN', 'Que dit 1 Jean 1:9 si nous confessons nos péchés ?', 'Nous serons punis', 'Dieu est fidèle pour nous pardonner', 'Nous devons jeûner', 'Nous devons sacrifier', 1, 'Si nous confessons, il est fidèle et juste pour nous pardonner.', '1 Jean 1:9'),
('1jn_q3', '1JN', 'Comment Jean définit-il Dieu ?', 'Dieu est justice', 'Dieu est amour', 'Dieu est colère', 'Dieu est loi', 1, 'Dieu est amour.', '1 Jean 4:8'),
('1jn_q4', '1JN', 'Que dit Jean sur celui qui prétend aimer Dieu mais hait son frère ?', 'Il est sage', 'Il est menteur', 'Il est fidèle', 'Il est pardonné', 1, 'Si quelqu''un dit : J''aime Dieu, et qu''il haïsse son frère, c''est un menteur.', '1 Jean 4:20'),
('1jn_q5', '1JN', 'Comment savons-nous que nous sommes passés de la mort à la vie ?', 'Par nos œuvres', 'Parce que nous aimons les frères', 'Par nos dons', 'Par notre connaissance', 1, 'Nous savons que nous sommes passés de la mort à la vie parce que nous aimons.', '1 Jean 3:14'),
('1jn_q6', '1JN', 'Qu''est-ce que l''amour parfait chasse ?', 'La joie', 'La crainte', 'L''espérance', 'La foi', 1, 'L''amour parfait bannit la crainte.', '1 Jean 4:18'),
('1jn_q7', '1JN', 'Qui est notre avocat selon 1 Jean ?', 'L''Esprit Saint', 'Jésus-Christ le juste', 'Les anges', 'Les prophètes', 1, 'Nous avons un avocat auprès du Père, Jésus-Christ le juste.', '1 Jean 2:1'),
('1jn_q8', '1JN', 'Comment devons-nous aimer selon 1 Jean ?', 'En paroles seulement', 'En actions et en vérité', 'En pensées', 'En prières', 1, 'N''aimons pas en paroles seulement, mais en actions et en vérité.', '1 Jean 3:18');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('heb_q1', 'HEB', 'Comment Dieu a-t-il parlé autrefois ?', 'Par les rois', 'Par les prophètes', 'Par les anges', 'Par les prêtres', 1, 'Dieu a parlé autrefois à nos pères par les prophètes.', 'Hébreux 1:1'),
('heb_q2', 'HEB', 'Comment Dieu nous parle-t-il maintenant ?', 'Par les anges', 'Par son Fils', 'Par les prophètes', 'Par les rêves', 1, 'En ces derniers temps, il nous a parlé par le Fils.', 'Hébreux 1:2'),
('heb_q3', 'HEB', 'Quel est le thème d''Hébreux 11 ?', 'L''amour', 'La foi', 'L''espérance', 'La loi', 1, 'Hébreux 11 est le chapitre de la foi et de ses héros.', 'Hébreux 11'),
('heb_q4', 'HEB', 'Qu''est-ce que la foi selon Hébreux 11:1 ?', 'Une certitude visible', 'Une ferme assurance des choses qu''on espère', 'Un doute raisonnable', 'Une tradition', 1, 'La foi est une ferme assurance des choses qu''on espère.', 'Hébreux 11:1'),
('heb_q5', 'HEB', 'Quel personnage de l''AT est mentionné comme prêtre selon l''ordre de Melchisédek ?', 'Aaron', 'Moïse', 'Jésus', 'Abraham', 2, 'Jésus est prêtre pour toujours selon l''ordre de Melchisédek.', 'Hébreux 7:17'),
('heb_q6', 'HEB', 'Que dit Hébreux sur la Parole de Dieu ?', 'Elle est ancienne', 'Elle est vivante et efficace', 'Elle est compliquée', 'Elle est cachée', 1, 'La Parole de Dieu est vivante et efficace.', 'Hébreux 4:12'),
('heb_q7', 'HEB', 'Comment devons-nous approcher du trône de la grâce ?', 'Avec crainte', 'Avec assurance', 'Avec tremblement', 'Avec hésitation', 1, 'Approchons-nous avec assurance du trône de la grâce.', 'Hébreux 4:16'),
('heb_q8', 'HEB', 'Qu''est-ce qui est impossible à plaire à Dieu ?', 'Le péché', 'L''absence de foi', 'La pauvreté', 'La maladie', 1, 'Sans la foi, il est impossible de lui être agréable.', 'Hébreux 11:6'),
('heb_q9', 'HEB', 'Qui est entouré d''une nuée de témoins ?', 'Les anges', 'Nous', 'Les prophètes', 'Les prêtres', 1, 'Nous sommes environnés d''une grande nuée de témoins.', 'Hébreux 12:1'),
('heb_q10', 'HEB', 'Comment Jésus est-il décrit dans Hébreux ?', 'Comme un prophète', 'Comme souverain sacrificateur', 'Comme un roi', 'Comme un ange', 1, 'Jésus est notre grand souverain sacrificateur.', 'Hébreux 4:14'),
('heb_q11', 'HEB', 'Que dit Hébreux sur Jésus ?', 'Il change', 'Il est le même hier, aujourd''hui et éternellement', 'Il évolue', 'Il grandit', 1, 'Jésus-Christ est le même hier, aujourd''hui et éternellement.', 'Hébreux 13:8'),
('heb_q12', 'HEB', 'Vers qui devons-nous fixer nos regards selon Hébreux 12 ?', 'Vers les anges', 'Vers Jésus', 'Vers les prophètes', 'Vers les ancêtres', 1, 'Ayant les regards sur Jésus, le chef et le consommateur de la foi.', 'Hébreux 12:2');
INSERT INTO book_quiz_questions (id, book_id, question, option_a, option_b, option_c, option_d, correct_answer, explanation, verse_ref) VALUES
('jas_q1', 'JAS', 'Qui a écrit l''épître de Jacques ?', 'Jacques, fils de Zébédée', 'Jacques, frère du Seigneur', 'Jacques, fils d''Alphée', 'Un Jacques inconnu', 1, 'Jacques, le frère du Seigneur, a écrit cette épître.', 'Jacques 1:1'),
('jas_q2', 'JAS', 'Que produit l''épreuve de la foi ?', 'Le découragement', 'La patience', 'La colère', 'La tristesse', 1, 'L''épreuve de votre foi produit la patience.', 'Jacques 1:3'),
('jas_q3', 'JAS', 'Que doit demander celui qui manque de sagesse ?', 'Aux anciens', 'À Dieu qui donne à tous', 'Aux prophètes', 'Aux anges', 1, 'Si quelqu''un manque de sagesse, qu''il la demande à Dieu.', 'Jacques 1:5'),
('jas_q4', 'JAS', 'Comment Jacques décrit-il la foi sans les œuvres ?', 'Parfaite', 'Morte', 'Suffisante', 'Excellente', 1, 'La foi sans les œuvres est morte.', 'Jacques 2:26'),
('jas_q5', 'JAS', 'Quel membre du corps est difficile à dompter ?', 'La main', 'Le pied', 'La langue', 'L''œil', 2, 'La langue est un petit membre, mais nul ne peut la dompter.', 'Jacques 3:8'),
('jas_q6', 'JAS', 'Que dit Jacques sur les riches qui oppriment ?', 'Ils sont bénis', 'Ils seront jugés', 'Ils sont sages', 'Ils sont pardonnés', 1, 'Vos richesses sont pourries, votre or et argent sont rouillés.', 'Jacques 5:1-3'),
('jas_q7', 'JAS', 'Que doivent faire les malades selon Jacques ?', 'Jeûner', 'Appeler les anciens pour la prière et l''onction', 'Offrir des sacrifices', 'Partir en pèlerinage', 1, 'Qu''il appelle les anciens de l''église pour prier et oindre d''huile.', 'Jacques 5:14'),
('jas_q8', 'JAS', 'Qu''est-ce que la prière du juste ?', 'Faible', 'Grande en son efficacité', 'Sans effet', 'Seulement pour lui-même', 1, 'La prière fervente du juste a une grande efficacité.', 'Jacques 5:16');
