-- Méditations bibliques - Partie 3
-- Catégories: Service, Sainteté, Obéissance, Louange, Grâce, Humilité

-- ==========================================
-- CATÉGORIE: SERVICE (service)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('se-001', 'Servir comme Jésus', 'Car le Fils de l''homme est venu, non pour être servi, mais pour servir, et donner sa vie comme la rançon de plusieurs.', 'Marc 10:45', 'Jésus, le Roi des rois, est venu pour servir. Son exemple renverse nos notions de grandeur. La vraie grandeur dans le Royaume se mesure au service, non au pouvoir. Suivre Jésus, c''est adopter son attitude de serviteur.', 'Comment le modèle de Jésus-serviteur transforme-t-il votre vision du leadership et du succès?', 'Seigneur Jésus, tu as servi jusqu''à donner ta vie. Donne-moi ce cœur de serviteur. Amen.', NULL, NULL, 'service'),

('se-002', 'Servir les uns les autres', 'Par amour, servez-vous les uns les autres.', 'Galates 5:13', 'Notre liberté en Christ n''est pas pour notre propre confort mais pour servir les autres par amour. Le service est l''expression pratique de l''amour fraternel. Dans le corps de Christ, nous sommes appelés à nous servir mutuellement.', 'Comment servez-vous vos frères et sœurs en Christ? Votre service est-il motivé par l''amour?', 'Seigneur, que ma liberté me conduise à servir les autres par amour. Montre-moi comment servir aujourd''hui. Amen.', NULL, NULL, 'service'),

('se-003', 'Les dons pour servir', 'Comme de bons dispensateurs des diverses grâces de Dieu, que chacun de vous mette au service des autres le don qu''il a reçu.', '1 Pierre 4:10', 'Chaque croyant a reçu des dons de Dieu - non pour les garder mais pour les mettre au service des autres. Nous sommes des dispensateurs, pas des propriétaires. Nos dons trouvent leur plénitude quand ils bénissent à d''autres.', 'Quels dons Dieu vous a-t-il donnés? Comment les mettez-vous au service des autres?', 'Père, révèle-moi mes dons et aide-moi à les utiliser pour servir les autres fidèlement. Amen.', NULL, NULL, 'service'),

('se-004', 'Servir dans l''humilité', 'Revêtez-vous d''humilité; car Dieu résiste aux orgueilleux, mais il fait grâce aux humbles.', '1 Pierre 5:5', 'Le vrai service est empreint d''humilité. Servir avec orgueil - pour être vu ou applaudi - n''honore pas Dieu. L''humilité reconnaît que tout ce que nous avons et sommes vient de Dieu. C''est dans cette attitude que le service est béni.', 'Votre service est-il humble ou recherchez-vous la reconnaissance? Comment cultiver plus d''humilité?', 'Seigneur, revêts-moi d''humilité dans mon service. Que je serve pour ta gloire, pas pour la mienne. Amen.', NULL, NULL, 'service'),

('se-005', 'Laver les pieds', 'Si donc je vous ai lavé les pieds, moi, le Seigneur et le Maître, vous devez aussi vous laver les pieds les uns aux autres.', 'Jean 13:14', 'Jésus, le Maître, a lavé les pieds de ses disciples - la tâche du plus humble serviteur. Ce geste nous enseigne que aucun service n''est trop bas pour nous. Servir les autres dans les tâches humbles est suivre l''exemple de Christ.', 'Y a-t-il des services que vous considérez indignes de vous? Comment pouvez-vous "laver les pieds" des autres?', 'Jésus, donne-moi l''humilité de servir dans les tâches les plus humbles, comme tu l''as fait. Amen.', NULL, NULL, 'service'),

('se-006', 'Servir le Seigneur', 'Tout ce que vous faites, faites-le de bon cœur, comme pour le Seigneur et non pour des hommes.', 'Colossiens 3:23', 'Notre service ultime est pour le Seigneur, même quand nous servons les hommes. Cette perspective transforme chaque tâche ordinaire en acte d''adoration. Servir "de bon cœur" signifie avec joie et excellence, car nous servons Christ.', 'Voyez-vous votre travail quotidien comme un service pour le Seigneur? Comment cette perspective change-t-elle votre attitude?', 'Seigneur, tout ce que je fais, je veux le faire pour toi. Transforme mon travail en acte d''adoration. Amen.', NULL, NULL, 'service'),

('se-007', 'Servir les pauvres', 'Je vous le dis en vérité, toutes les fois que vous avez fait ces choses à l''un de ces plus petits de mes frères, c''est à moi que vous les avez faites.', 'Matthieu 25:40', 'Quand nous servons les plus vulnérables - les pauvres, les malades, les étrangers - nous servons Jésus lui-même. Il s''identifie aux "plus petits". Notre compassion envers eux est notre réponse à l''amour du Christ.', 'Comment servez-vous les plus vulnérables de votre communauté? Voyez-vous Jésus en eux?', 'Jésus, ouvre mes yeux pour te voir dans les plus petits. Aide-moi à les servir comme je te servirais. Amen.', NULL, NULL, 'service'),

('se-008', 'L''appel au ministère', 'Comme tu m''as envoyé dans le monde, je les ai aussi envoyés dans le monde.', 'Jean 17:18', 'Nous sommes envoyés dans le monde comme Jésus a été envoyé. Chaque croyant est un ministre de la réconciliation, appelé à servir dans sa sphère d''influence. Le ministère n''est pas réservé aux professionnels - c''est l''appel de tous.', 'Comment vivez-vous votre appel missionnaire dans votre quotidien? Où Dieu vous a-t-il envoyé?', 'Père, merci de m''envoyer comme tu as envoyé Jésus. Aide-moi à accomplir mon ministère là où je suis. Amen.', NULL, NULL, 'service'),

('se-009', 'Servir avec fidélité', 'C''est bien, bon et fidèle serviteur; tu as été fidèle en peu de chose, je te confierai beaucoup.', 'Matthieu 25:21', 'La fidélité dans les petites choses ouvre la porte à de plus grandes responsabilités. Dieu teste notre fiabilité dans le peu avant de nous confier davantage. Un serviteur fidèle est reconnu et récompensé par son Maître.', 'Êtes-vous fidèle dans les petites responsabilités que Dieu vous a confiées? Comment pouvez-vous être plus fidèle?', 'Maître, aide-moi à être fidèle en peu de chose. Que tu puisses me confier beaucoup. Amen.', NULL, NULL, 'service'),

('se-010', 'La récompense du service', 'Et quoi que vous fassiez, faites-le de bon cœur... sachant que vous recevrez du Seigneur l''héritage pour récompense. Servez Christ, le Seigneur.', 'Colossiens 3:23-24', 'Notre service n''est pas vain. Le Seigneur voit et récompense chaque acte de service fait en son nom. Même si les hommes ne reconnaissent pas notre travail, nous avons un héritage éternel qui nous attend. C''est le Seigneur que nous servons.', 'Servez-vous en attendant une récompense humaine ou êtes-vous motivé par l''héritage éternel?', 'Christ, je te sers toi. Je sais que tu récompenseras mon service fidèle par ton héritage glorieux. Amen.', NULL, NULL, 'service');

-- ==========================================
-- CATÉGORIE: SAINTETÉ (saintete)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('st-001', 'Appelés à la sainteté', 'Car Dieu ne nous a pas appelés à l''impureté, mais à la sanctification.', '1 Thessaloniciens 4:7', 'La sainteté n''est pas optionnelle - c''est notre vocation. Dieu nous appelle à être mis à part pour lui, séparés du péché et consacrés à sa gloire. Cet appel est un privilège et une responsabilité.', 'Répondez-vous à l''appel de Dieu à la sainteté? Quels domaines de votre vie ont besoin de sanctification?', 'Père saint, merci de m''appeler à la sanctification. Aide-moi à répondre à cet appel avec tout mon cœur. Amen.', NULL, NULL, 'saintete'),

('st-002', 'Saints comme Dieu est saint', 'Vous serez saints, car je suis saint.', '1 Pierre 1:16', 'Notre modèle de sainteté est Dieu lui-même. Cette norme peut sembler impossible, mais avec l''aide du Saint-Esprit, nous pouvons grandir dans la ressemblance à Dieu. Sa sainteté devient progressivement notre caractère.', 'La sainteté de Dieu vous inspire-t-elle ou vous intimide-t-elle? Comment pouvez-vous refléter davantage sa sainteté?', 'Seigneur saint, je veux être saint comme tu es saint. Transforme-moi par ton Esprit à ton image. Amen.', NULL, NULL, 'saintete'),

('st-003', 'La sanctification progressive', 'Que le Dieu de paix vous sanctifie lui-même tout entiers, et que tout votre être, l''esprit, l''âme et le corps, soit conservé irrépréhensible, lors de l''avènement de notre Seigneur Jésus-Christ!', '1 Thessaloniciens 5:23', 'La sanctification touche notre être entier - esprit, âme et corps. C''est un processus que Dieu accomplit en nous, pas quelque chose que nous produisons par nos efforts. Lui seul peut nous garder irréprochables jusqu''à son retour.', 'Permettez-vous à Dieu de sanctifier tout votre être? Y a-t-il des zones interdites?', 'Dieu de paix, sanctifie-moi tout entier - esprit, âme et corps. Garde-moi irréprochable jusqu''à ton retour. Amen.', NULL, NULL, 'saintete'),

('st-004', 'Fuir le péché', 'Fuyez l''impudicité. Quelque autre péché qu''un homme commette, ce péché est hors du corps; mais celui qui se livre à l''impudicité pèche contre son propre corps.', '1 Corinthiens 6:18', 'Parfois, la sainteté demande la fuite. Face à certaines tentations, la meilleure stratégie n''est pas de combattre mais de fuir. Joseph a fui devant la femme de Potiphar. La sagesse est de connaître nos faiblesses et d''éviter les occasions de péché.', 'De quoi devez-vous fuir pour préserver votre sainteté? Quelles précautions devez-vous prendre?', 'Seigneur, donne-moi la sagesse de fuir les tentations et le courage de m''éloigner de ce qui compromet ma sainteté. Amen.', NULL, NULL, 'saintete'),

('st-005', 'La pureté du cœur', 'Heureux ceux qui ont le cœur pur, car ils verront Dieu!', 'Matthieu 5:8', 'La pureté du cœur est plus que l''absence de péché extérieur - c''est un cœur entièrement dévoué à Dieu. Un cœur pur n''a pas de motifs cachés, pas de double allégeance. Et la récompense est extraordinaire: voir Dieu!', 'Votre cœur est-il pur? Quelles impuretés avez-vous besoin de confesser et d''abandonner?', 'Père, purifie mon cœur de toute impureté. Je veux te voir et cela demande un cœur pur. Amen.', NULL, NULL, 'saintete'),

('st-006', 'Séparés pour Dieu', 'Ne vous mettez pas avec les infidèles sous un joug étranger. Car quel rapport y a-t-il entre la justice et l''iniquité? Ou qu''y a-t-il de commun entre la lumière et les ténèbres?', '2 Corinthiens 6:14', 'La sainteté implique une séparation. Non pas un isolement du monde, mais une distinction dans nos alliances et nos engagements. La lumière et les ténèbres ne peuvent cohabiter. Nous sommes dans le monde mais pas du monde.', 'Y a-t-il des alliances ou des associations qui compromettent votre sainteté? Comment vous en séparer?', 'Seigneur, aide-moi à vivre séparé du monde tout en restant une lumière pour lui. Amen.', NULL, NULL, 'saintete'),

('st-007', 'Le temple saint', 'Ne savez-vous pas que vous êtes le temple de Dieu, et que l''Esprit de Dieu habite en vous? Si quelqu''un détruit le temple de Dieu, Dieu le détruira; car le temple de Dieu est saint, et c''est ce que vous êtes.', '1 Corinthiens 3:16-17', 'Nous sommes le temple du Saint-Esprit. Cette réalité élève notre corps et notre vie à un statut sacré. Ce qui entre dans notre temple - nos pensées, nos paroles, nos actions - doit être digne de la présence de Dieu.', 'Traitez-vous votre corps et votre vie comme un temple saint? Qu''avez-vous besoin de changer?', 'Saint-Esprit, tu habites en moi. Aide-moi à garder ton temple pur et saint. Amen.', NULL, NULL, 'saintete'),

('st-008', 'La sanctification par la Parole', 'Sanctifie-les par ta vérité: ta parole est la vérité.', 'Jean 17:17', 'La Parole de Dieu est l''agent de notre sanctification. Elle nous révèle le péché, nous montre le chemin de la pureté et nous transforme progressivement. Plus nous nous exposons à la Parole, plus nous sommes sanctifiés.', 'Combien de temps passez-vous dans la Parole de Dieu? Comment la Parole vous sanctifie-t-elle?', 'Père, sanctifie-moi par ta Parole de vérité. Que la lecture quotidienne me transforme. Amen.', NULL, NULL, 'saintete'),

('st-009', 'La poursuite de la sainteté', 'Recherchez la paix avec tous, et la sanctification, sans laquelle personne ne verra le Seigneur.', 'Hébreux 12:14', 'La sainteté doit être recherchée, poursuivie activement. Ce n''est pas automatique. Et l''enjeu est élevé: sans la sanctification, personne ne verra le Seigneur. Cette poursuite est essentielle pour notre vie éternelle avec Dieu.', 'Poursuivez-vous activement la sainteté? Quelle est l''urgence de cette quête pour vous?', 'Seigneur, je veux te voir. Aide-moi à poursuivre la sainteté avec urgence et détermination. Amen.', NULL, NULL, 'saintete'),

('st-010', 'La beauté de la sainteté', 'Prosternez-vous devant l''Éternel avec des ornements sacrés.', 'Psaume 29:2', 'La sainteté est belle aux yeux de Dieu. Elle n''est pas austère ou triste - elle est un ornement sacré qui nous embellit. Quand nous nous approchons de Dieu dans la sainteté, nous reflétons sa gloire et sa beauté.', 'Voyez-vous la sainteté comme un fardeau ou comme un ornement qui vous embellit?', 'Éternel, que la sainteté soit mon ornement. Que je m''approche de toi dans la beauté de la pureté. Amen.', NULL, NULL, 'saintete');

-- ==========================================
-- CATÉGORIE: OBÉISSANCE (obeissance)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('ob-001', 'Obéir vaut mieux', 'Voici, l''obéissance vaut mieux que les sacrifices, et l''observation de sa parole vaut mieux que la graisse des béliers.', '1 Samuel 15:22', 'Dieu préfère notre obéissance à nos offrandes extérieures. Les rituels religieux sans obéissance du cœur ne l''impressionnent pas. Ce qu''il désire, c''est un cœur qui écoute sa voix et agit selon sa parole.', 'Vos actes religieux sont-ils accompagnés d''obéissance? Ou compensez-vous la désobéissance par des rituels?', 'Père, je ne veux pas te donner des sacrifices sans obéissance. Donne-moi un cœur obéissant. Amen.', NULL, NULL, 'obeissance'),

('ob-002', 'L''obéissance de la foi', 'C''est par lui que nous avons reçu la grâce et l''apostolat, pour amener en son nom à l''obéissance de la foi tous les païens.', 'Romains 1:5', 'La foi véritable produit l''obéissance. Ce ne sont pas deux choses séparées - la foi qui n''obéit pas n''est pas une vraie foi. L''obéissance est la réponse naturelle d''un cœur qui croit vraiment en Dieu et en ses promesses.', 'Votre foi produit-elle l''obéissance? Y a-t-il des domaines où vous croyez mais n''obéissez pas?', 'Seigneur, que ma foi soit une foi obéissante. Que je ne sois pas seulement un auditeur mais un faiseur. Amen.', NULL, NULL, 'obeissance'),

('ob-003', 'Obéir à Dieu plutôt qu''aux hommes', 'Il faut obéir à Dieu plutôt qu''aux hommes.', 'Actes 5:29', 'Quand les commandements humains contredisent les commandements de Dieu, notre allégeance première est à Dieu. Cette obéissance peut coûter cher, mais elle est non négociable. Les apôtres l''ont démontré même face à la prison.', 'Avez-vous déjà dû choisir entre obéir à Dieu et obéir aux hommes? Comment avez-vous réagi?', 'Seigneur, donne-moi le courage d''obéir à toi même quand cela me coûte. Tu es mon autorité suprême. Amen.', NULL, NULL, 'obeissance'),

('ob-004', 'L''obéissance de Jésus', 'Après s''être mis en prière, il s''humilia lui-même, se rendant obéissant jusqu''à la mort, même jusqu''à la mort de la croix.', 'Philippiens 2:8', 'Jésus est notre modèle d''obéissance parfaite. Son obéissance au Père l''a conduit jusqu''à la croix. Il n''a pas cherché sa propre volonté mais celle de celui qui l''avait envoyé. Cette obéissance a accompli notre salut.', 'L''obéissance de Jésus vous inspire-t-elle? Êtes-vous prêt à obéir jusqu''au bout?', 'Jésus, ton obéissance m''a sauvé. Aide-moi à t''imiter et à obéir au Père en toutes choses. Amen.', NULL, NULL, 'obeissance'),

('ob-005', 'L''obéissance immédiate', 'Mon fils, va travailler aujourd''hui dans ma vigne. Il répondit: Je ne veux pas. Ensuite, il se repentit, et il alla.', 'Matthieu 21:28-29', 'L''obéissance différée vaut mieux que la désobéissance, mais l''obéissance immédiate honore davantage Dieu. Quand Dieu parle, la meilleure réponse est d''agir sans délai. L''hésitation peut devenir désobéissance.', 'Obéissez-vous immédiatement à la voix de Dieu ou tardez-vous à agir? Qu''est-ce qui cause vos délais?', 'Père, apprends-moi l''obéissance immédiate. Quand tu parles, je veux agir sans tarder. Amen.', NULL, NULL, 'obeissance'),

('ob-006', 'Garder ses commandements', 'Si vous m''aimez, gardez mes commandements.', 'Jean 14:15', 'L''amour pour Jésus se prouve par l''obéissance à ses commandements. Les paroles d''amour sans les actes d''obéissance sont vides. L''obéissance est la langue de l''amour que Dieu comprend et apprécie le plus.', 'Votre amour pour Jésus se traduit-il en obéissance? Quels commandements avez-vous du mal à garder?', 'Jésus, je t''aime et je veux te le prouver en gardant tes commandements. Aide-moi. Amen.', NULL, NULL, 'obeissance'),

('ob-007', 'Les bénédictions de l''obéissance', 'Heureux ceux qui écoutent la parole de Dieu, et qui la gardent!', 'Luc 11:28', 'L''obéissance apporte la bénédiction. Ce n''est pas une transaction, mais un principe du Royaume. Ceux qui écoutent et gardent la Parole de Dieu expérimentent le bonheur véritable - la paix avec Dieu et la joie de lui plaire.', 'Avez-vous expérimenté la bénédiction de l''obéissance? Comment l''obéissance vous a-t-elle rendu heureux?', 'Père, je veux être de ceux qui écoutent et gardent ta Parole. Accorde-moi ce bonheur. Amen.', NULL, NULL, 'obeissance'),

('ob-008', 'L''obéissance dans les petites choses', 'Celui qui est fidèle dans les moindres choses l''est aussi dans les grandes, et celui qui est injuste dans les moindres choses l''est aussi dans les grandes.', 'Luc 16:10', 'La fidélité dans les petites choses révèle notre cœur. Dieu observe notre obéissance dans le quotidien, pas seulement dans les grandes décisions. Les petites obéissances préparent aux grandes missions.', 'Êtes-vous fidèle dans les petites obéissances quotidiennes? Quelles "moindres choses" négligez-vous?', 'Seigneur, aide-moi à être fidèle dans les moindres choses. Que ma petite obéissance te plaise. Amen.', NULL, NULL, 'obeissance'),

('ob-009', 'L''obéissance malgré l''incompréhension', 'Bien qu''il fût Fils, il a appris l''obéissance par les choses qu''il a souffertes.', 'Hébreux 5:8', 'Parfois Dieu nous demande d''obéir sans comprendre pourquoi. L''obéissance dans l''obscurité est la plus pure. Même Jésus a appris l''obéissance à travers la souffrance. L''incompréhension n''est pas une excuse pour la désobéissance.', 'Avez-vous du mal à obéir quand vous ne comprenez pas? Comment pouvez-vous faire confiance à Dieu malgré l''incertitude?', 'Père, même quand je ne comprends pas, je choisis d''obéir. Ta sagesse dépasse la mienne. Amen.', NULL, NULL, 'obeissance'),

('ob-010', 'Écouter et faire', 'Mettez en pratique la parole, et ne vous bornez pas à l''écouter, en vous trompant vous-mêmes par de faux raisonnements.', 'Jacques 1:22', 'L''écoute sans action est une illusion de spiritualité. Nous pouvons nous tromper nous-mêmes en pensant qu''entendre la Parole suffit. Mais la vraie obéissance demande la mise en pratique. L''action complète l''écoute.', 'Êtes-vous un auditeur ou un faiseur de la Parole? Comment pouvez-vous mettre en pratique ce que vous avez entendu récemment?', 'Seigneur, je ne veux pas seulement écouter mais faire. Transforme-moi en faiseur de ta Parole. Amen.', NULL, NULL, 'obeissance');

-- ==========================================
-- CATÉGORIE: LOUANGE (louange)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('lo-001', 'Créés pour la louange', 'Ce peuple, je l''ai formé pour moi; il publiera ma louange.', 'Ésaïe 43:21', 'Nous existons pour louer Dieu. C''est notre raison d''être la plus fondamentale. Quand nous louons, nous accomplissons le but même de notre création. La louange n''est pas une activité parmi d''autres - c''est le centre de notre existence.', 'Vivez-vous comme quelqu''un créé pour la louange? La louange occupe-t-elle la place centrale dans votre vie?', 'Père, tu m''as formé pour ta louange. Accomplis en moi ce dessein. Que ma vie soit un hymne à ta gloire. Amen.', NULL, NULL, 'louange'),

('lo-002', 'Louange continuelle', 'Par lui, offrons sans cesse à Dieu un sacrifice de louange, c''est-à-dire le fruit de lèvres qui confessent son nom.', 'Hébreux 13:15', 'La louange n''est pas réservée au dimanche ou aux moments de joie. C''est un sacrifice continu que nous offrons à Dieu. Même quand nous n''en avons pas envie, nous pouvons choisir de louer. C''est un acte de volonté et de foi.', 'Offrez-vous un sacrifice de louange même quand c''est difficile? Comment pouvez-vous louer plus continuellement?', 'Seigneur, je t''offre le sacrifice de la louange. Que mes lèvres confessent ton nom sans cesse. Amen.', NULL, NULL, 'louange'),

('lo-003', 'Louange dans l''épreuve', 'Quoique le figuier ne fleurisse pas, quoiqu''il n''y ait point de produit dans les vignes... toutefois, je veux me réjouir en l''Éternel, je veux me réjouir dans le Dieu de mon salut.', 'Habacuc 3:17-18', 'La louange la plus puissante vient au milieu de l''épreuve. Quand tout va mal, choisir de se réjouir en l''Éternel est un acte de foi héroïque. Cette louange déclare que Dieu est digne, quelles que soient les circonstances.', 'Pouvez-vous louer Dieu même quand les circonstances sont contre vous? Comment développer cette louange résiliente?', 'Éternel, même si tout semble aller mal, je veux me réjouir en toi. Tu es mon salut et ma joie. Amen.', NULL, NULL, 'louange'),

('lo-004', 'Le pouvoir de la louange', 'Vers le milieu de la nuit, Paul et Silas priaient et chantaient les louanges de Dieu... tout à coup il se fit un grand tremblement de terre.', 'Actes 16:25-26', 'La louange a le pouvoir de briser les chaînes! Paul et Silas, enchaînés et battus, ont loué Dieu - et les portes de la prison se sont ouvertes. La louange libère la puissance de Dieu et change les situations les plus désespérées.', 'Utilisez-vous la louange comme arme spirituelle? Quelle prison avez-vous besoin de voir s''ouvrir par la louange?', 'Seigneur, je te loue au milieu de ma nuit. Que ta puissance se manifeste et brise mes chaînes. Amen.', NULL, NULL, 'louange'),

('lo-005', 'Louer Dieu pour qui il est', 'L''Éternel règne: que la terre soit dans l''allégresse, que les îles nombreuses se réjouissent!', 'Psaume 97:1', 'La louange la plus pure se concentre sur qui Dieu est, pas sur ce qu''il fait pour nous. Louer sa souveraineté, sa sainteté, sa bonté, sa puissance - c''est lui offrir une adoration désintéressée qui le glorifie vraiment.', 'Votre louange est-elle centrée sur Dieu ou sur vos bénédictions? Comment pouvez-vous mieux le louer pour qui il est?', 'Éternel, tu règnes! Je te loue pour ta majesté, ta sainteté, ta grandeur incomparable. Amen.', NULL, NULL, 'louange'),

('lo-006', 'L''entrée par la louange', 'Entrez dans ses portes avec des louanges, dans ses parvis avec des cantiques! Célébrez-le, bénissez son nom!', 'Psaume 100:4', 'La louange est la porte d''entrée dans la présence de Dieu. Quand nous commençons par la louange, nous nous préparons à rencontrer Dieu. La louange ouvre notre cœur et crée l''atmosphère propice à l''intimité avec lui.', 'Commencez-vous vos temps de prière par la louange? Comment cela change-t-il votre expérience de la présence de Dieu?', 'Seigneur, j''entre dans ta présence avec des louanges. Je célèbre ton nom glorieux. Amen.', NULL, NULL, 'louange'),

('lo-007', 'Louange corporelle', 'Que tout ce qui respire loue l''Éternel! Louez l''Éternel!', 'Psaume 150:6', 'La louange peut impliquer tout notre être - voix, mains, corps entier. Danser, lever les mains, s''incliner - sont des expressions bibliques de la louange. Nous ne sommes pas seulement des esprits - notre corps aussi peut glorifier Dieu.', 'Êtes-vous libre d''exprimer votre louange physiquement? Quelles barrières vous retiennent?', 'Seigneur, je te loue de tout mon être - esprit, âme et corps. Que chaque souffle soit une louange. Amen.', NULL, NULL, 'louange'),

('lo-008', 'La louange qui précède la victoire', 'Quand ils commencèrent les chants et les louanges, l''Éternel plaça une embuscade contre les fils d''Ammon, de Moab et de la montagne de Séir.', '2 Chroniques 20:22', 'Josaphat a placé les chanteurs devant l''armée! Quand ils ont commencé à louer, Dieu a combattu pour eux. La louange précède souvent la victoire. C''est une arme spirituelle qui met Dieu au combat à notre place.', 'Faites-vous précéder vos batailles par la louange? Comment pouvez-vous utiliser cette arme plus efficacement?', 'Éternel, je te loue avant même de voir la victoire. Je sais que tu combats pour moi. Amen.', NULL, NULL, 'louange'),

('lo-009', 'Louange en esprit et en vérité', 'Mais l''heure vient, et elle est déjà venue, où les vrais adorateurs adoreront le Père en esprit et en vérité.', 'Jean 4:23', 'Dieu cherche des adorateurs qui l''adorent en esprit - avec sincérité et profondeur - et en vérité - selon sa révélation. La vraie louange n''est pas superficielle ou ignorante. Elle vient d''un cœur touché par l''Esprit et éclairé par la Parole.', 'Votre louange est-elle en esprit et en vérité? Comment pouvez-vous approfondir votre adoration?', 'Père, fais de moi un vrai adorateur. Que mon esprit et ta vérité se rencontrent dans la louange. Amen.', NULL, NULL, 'louange'),

('lo-010', 'Louange éternelle', 'Après cela, je regardai, et voici, il y avait une grande foule... Ils criaient d''une voix forte, en disant: Le salut est à notre Dieu.', 'Apocalypse 7:9-10', 'La louange sera notre activité éternelle. Au ciel, une multitude de toutes nations adorera l''Agneau. En apprenant à louer maintenant, nous nous préparons pour l''éternité. La louange sur terre est un avant-goût du ciel.', 'Anticipez-vous la louange éternelle? Comment cette perspective colore-t-elle votre louange présente?', 'Seigneur, je me joins déjà à la louange éternelle. Le salut est à toi! Gloire à l''Agneau! Amen.', NULL, NULL, 'louange');

-- ==========================================
-- CATÉGORIE: GRÂCE (grace)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('gr-001', 'Sauvés par grâce', 'Car c''est par la grâce que vous êtes sauvés, par le moyen de la foi. Et cela ne vient pas de vous, c''est le don de Dieu.', 'Éphésiens 2:8', 'Le salut est un don gratuit - la grâce de Dieu. Nous ne pouvons ni le mériter ni le gagner. Cette vérité nous libère de la performance religieuse et nous remplit de gratitude. Tout est grâce, du début à la fin.', 'Comprenez-vous vraiment la grâce? Ou essayez-vous encore de mériter votre salut par vos efforts?', 'Père, merci pour ta grâce imméritée. Je reçois le don du salut avec gratitude et humilité. Amen.', NULL, NULL, 'grace'),

('gr-002', 'La grâce suffisante', 'Ma grâce te suffit, car ma puissance s''accomplit dans la faiblesse.', '2 Corinthiens 12:9', 'Quand nous sommes faibles, la grâce de Dieu suffit. Nous n''avons pas besoin de plus de force personnelle - nous avons besoin de plus de grâce. Cette grâce est suffisante pour chaque situation, chaque combat, chaque jour.', 'Croyez-vous que la grâce de Dieu suffit pour vos défis actuels? Ou cherchez-vous d''autres sources de force?', 'Seigneur, ta grâce me suffit. Je m''appuie sur ta puissance, non sur ma force. Amen.', NULL, NULL, 'grace'),

('gr-003', 'Grâce sur grâce', 'Et nous avons tous reçu de sa plénitude, et grâce pour grâce.', 'Jean 1:16', 'La grâce de Dieu n''est pas donnée au compte-gouttes - elle coule abondamment, grâce sur grâce. Chaque nouvelle grâce remplace la précédente. Il n''y a pas de limite à la générosité de Dieu envers nous.', 'Expérimentez-vous l''abondance de la grâce? Ou vivez-vous comme si elle était rare et mesurée?', 'Père, merci pour ta grâce abondante. Que je vive dans la plénitude de ta générosité. Amen.', NULL, NULL, 'grace'),

('gr-004', 'La grâce qui enseigne', 'Car la grâce de Dieu, source de salut pour tous les hommes, a été manifestée. Elle nous enseigne à renoncer à l''impiété et aux convoitises mondaines.', 'Tite 2:11-12', 'La grâce n''est pas une licence pour pécher - elle nous enseigne à vivre saintement. C''est la grâce, pas la loi, qui nous transforme vraiment. Elle nous donne à la fois le pardon et la puissance de changer.', 'Laissez-vous la grâce vous enseigner à renoncer à l''impiété? Ou utilisez-vous la grâce comme excuse?', 'Seigneur, que ta grâce m''enseigne à vivre saintement. Qu''elle me transforme, pas seulement me pardonne. Amen.', NULL, NULL, 'grace'),

('gr-005', 'Accès à la grâce', 'C''est par lui que nous avons eu, par la foi, accès à cette grâce, dans laquelle nous demeurons fermes.', 'Romains 5:2', 'Par Christ, nous avons un accès permanent à la grâce. Nous y demeurons fermes - ce n''est pas un accès occasionnel mais une habitation. Nous vivons dans la grâce, respirons la grâce, marchons dans la grâce.', 'Vivez-vous dans la conscience de votre accès permanent à la grâce? Comment pouvez-vous y demeurer plus fermement?', 'Jésus, merci de m''avoir donné accès à la grâce. Aide-moi à y demeurer ferme chaque jour. Amen.', NULL, NULL, 'grace'),

('gr-006', 'La grâce qui fortifie', 'Toi donc, mon enfant, fortifie-toi dans la grâce qui est en Jésus-Christ.', '2 Timothée 2:1', 'La grâce n''est pas seulement pour le pardon - elle est aussi notre source de force. Nous nous fortifions dans la grâce, non dans notre propre détermination. Cette force gracieuse nous soutient dans les difficultés et le service.', 'Tirez-vous votre force de la grâce ou de vos propres ressources? Comment vous fortifier davantage dans la grâce?', 'Seigneur Jésus, je me fortifie dans ta grâce. Que cette force surnaturelle me soutienne. Amen.', NULL, NULL, 'grace'),

('gr-007', 'Grandir dans la grâce', 'Mais croissez dans la grâce et dans la connaissance de notre Seigneur et Sauveur Jésus-Christ.', '2 Pierre 3:18', 'La vie chrétienne est une croissance continue dans la grâce. Nous ne stagnons pas - nous grandissons. Plus nous connaissons Jésus, plus nous expérimentons sa grâce. C''est un voyage de découverte infinie.', 'Grandissez-vous dans la grâce ou stagnez-vous? Quelles étapes pouvez-vous prendre pour croître?', 'Seigneur Jésus, fais-moi croître dans ta grâce et ta connaissance. Je ne veux pas stagner. Amen.', NULL, NULL, 'grace'),

('gr-008', 'Grâce aux humbles', 'Dieu résiste aux orgueilleux, mais il fait grâce aux humbles.', 'Jacques 4:6', 'L''humilité est la posture qui reçoit la grâce. L''orgueil nous ferme à la grâce de Dieu, tandis que l''humilité nous ouvre à ses bénédictions. Reconnaître notre besoin est la première étape pour recevoir sa provision.', 'Êtes-vous humble devant Dieu? L''orgueil bloque-t-il l''écoulement de la grâce dans certains domaines?', 'Père, je m''humilie devant toi. Fais-moi grâce dans ma faiblesse reconnue. Amen.', NULL, NULL, 'grace'),

('gr-009', 'Ne pas recevoir en vain', 'Nous vous exhortons à ne pas recevoir la grâce de Dieu en vain.', '2 Corinthiens 6:1', 'La grâce de Dieu peut être reçue en vain si nous ne la laissons pas nous transformer et nous mobiliser. Elle nous est donnée pour un but - notre salut, notre sanctification, notre service. Ne gaspillons pas ce don précieux.', 'Recevez-vous la grâce de Dieu pour qu''elle porte du fruit? Ou la recevez-vous passivement, sans changement?', 'Seigneur, je ne veux pas recevoir ta grâce en vain. Qu''elle accomplisse son but en moi. Amen.', NULL, NULL, 'grace'),

('gr-010', 'La grâce de notre Seigneur', 'Que la grâce de notre Seigneur Jésus-Christ soit avec vous tous!', 'Philippiens 4:23', 'La grâce de Jésus-Christ est la bénédiction ultime. Elle nous accompagne, nous soutient, nous transforme. Paul termine ses lettres par cette bénédiction car il savait qu''avoir la grâce de Christ, c''est avoir tout ce dont nous avons besoin.', 'Vivez-vous dans la conscience quotidienne de la grâce de Jésus-Christ? Comment cette grâce vous transforme-t-elle?', 'Seigneur Jésus, que ta grâce soit avec moi dans chaque moment de cette journée. Amen.', NULL, NULL, 'grace');

-- ==========================================
-- CATÉGORIE: HUMILITÉ (humilite)
-- ==========================================

INSERT INTO meditations (id, title, verse, verse_ref, content, reflection, prayer, date, author, category) VALUES
('hu-001', 'L''humilité de Christ', 'Mais il s''est anéanti lui-même, prenant une forme de serviteur, en devenant semblable aux hommes.', 'Philippiens 2:7', 'Jésus, étant Dieu, s''est fait homme. Cette descente vertigineuse est le modèle ultime de l''humilité. Il n''a pas considéré son statut divin comme quelque chose à garder jalousement, mais s''est abaissé pour nous servir et nous sauver.', 'L''humilité de Christ vous inspire-t-elle? Comment pouvez-vous l''imiter dans votre vie?', 'Jésus, tu t''es anéanti par amour pour moi. Donne-moi cette même humilité. Amen.', NULL, NULL, 'humilite'),

('hu-002', 'Doux et humble de cœur', 'Prenez mon joug sur vous et recevez mes instructions, car je suis doux et humble de cœur.', 'Matthieu 11:29', 'Jésus se décrit lui-même comme doux et humble de cœur. C''est son caractère central que nous devons apprendre de lui. Son joug est facile parce qu''il marche humblement à nos côtés, pas en nous dominant.', 'Êtes-vous doux et humble de cœur comme Jésus? Comment pouvez-vous apprendre de lui?', 'Maître, enseigne-moi la douceur et l''humilité de cœur. Je prends ton joug sur moi. Amen.', NULL, NULL, 'humilite'),

('hu-003', 'L''humilité qui élève', 'Humiliez-vous devant le Seigneur, et il vous élèvera.', 'Jacques 4:10', 'Le chemin vers l''élévation passe par l''abaissement. C''est un paradoxe du Royaume: ceux qui s''humilient seront élevés, et ceux qui s''élèvent seront humiliés. L''humilité n''est pas une perte - c''est un investissement que Dieu récompense.', 'Cherchez-vous l''élévation par vos propres moyens ou par l''humilité? Faites-vous confiance à Dieu pour vous élever?', 'Seigneur, je m''humilie devant toi. J''attends que tu m''élèves en ton temps et à ta manière. Amen.', NULL, NULL, 'humilite'),

('hu-004', 'L''humilité envers les autres', 'Que chacun de vous, au lieu de considérer ses propres intérêts, considère aussi ceux des autres.', 'Philippiens 2:4', 'L''humilité se manifeste dans notre façon de considérer les autres. Au lieu de mettre nos intérêts en premier, nous pensons aux besoins des autres. Cette attitude désintéressée reflète le cœur de Christ et transforme nos relations.', 'Considérez-vous les intérêts des autres ou êtes-vous centré sur vous-même? Comment changer de perspective?', 'Seigneur, aide-moi à voir au-delà de mes propres intérêts. Que je considère aussi les besoins des autres. Amen.', NULL, NULL, 'humilite'),

('hu-005', 'L''humilité qui honore', 'Par humilité, estimez les autres supérieurs à vous-mêmes.', 'Philippiens 2:3', 'L''humilité nous fait estimer les autres supérieurs à nous. Ce n''est pas un faux abaissement, mais une reconnaissance que chaque personne a de la valeur et des dons que nous n''avons pas. Cette attitude honore Dieu et les autres.', 'Estimez-vous vraiment les autres supérieurs à vous? Ou vous comparez-vous constamment?', 'Père, délivre-moi de l''esprit de compétition. Aide-moi à honorer les autres par l''humilité. Amen.', NULL, NULL, 'humilite'),

('hu-006', 'L''humilité et la sagesse', 'Quand vient l''orgueil, vient aussi l''ignominie; mais la sagesse est avec les humbles.', 'Proverbes 11:2', 'L''orgueil conduit à la honte, mais l''humilité s''accompagne de sagesse. Les humbles sont enseignables, ouverts à la correction, prêts à apprendre. Cette posture attire la sagesse de Dieu et des hommes.', 'Êtes-vous enseignable et ouvert à la correction? Comment l''humilité peut-elle vous rendre plus sage?', 'Seigneur, préserve-moi de l''orgueil qui mène à la honte. Que l''humilité m''accompagne et m''apporte la sagesse. Amen.', NULL, NULL, 'humilite'),

('hu-007', 'Se revêtir d''humilité', 'De même, vous qui êtes jeunes, soyez soumis aux anciens. Et tous, dans vos rapports mutuels, revêtez-vous d''humilité.', '1 Pierre 5:5', 'L''humilité est un vêtement que nous devons revêtir intentionnellement. Ce n''est pas naturel - c''est un choix quotidien. Dans nos rapports mutuels, nous choisissons de mettre l''humilité comme on met un manteau.', 'Vous revêtez-vous d''humilité chaque matin? Comment pouvez-vous faire de l''humilité un choix conscient?', 'Seigneur, je choisis de me revêtir d''humilité aujourd''hui. Que ce soit mon vêtement visible. Amen.', NULL, NULL, 'humilite'),

('hu-008', 'L''humilité de Moïse', 'Or, Moïse était un homme très doux, plus qu''aucun homme sur la face de la terre.', 'Nombres 12:3', 'Moïse, le grand libérateur d''Israël, était l''homme le plus humble sur terre. Sa grandeur ne l''a pas rendu orgueilleux. Au contraire, plus il était proche de Dieu, plus il était humble. L''humilité accompagne la vraie spiritualité.', 'Plus vous connaissez Dieu, devenez-vous plus humble ou plus orgueilleux? Que révèle votre attitude?', 'Éternel, comme Moïse, rends-moi humble devant toi. Que la proximité avec toi produise la douceur. Amen.', NULL, NULL, 'humilite'),

('hu-009', 'L''humilité dans le service', 'Que le plus grand parmi vous soit comme le plus petit, et celui qui gouverne comme celui qui sert.', 'Luc 22:26', 'Dans le Royaume de Dieu, la grandeur se mesure au service. Les leaders servent, ils ne dominent pas. L''humilité renverse les hiérarchies du monde et établit un nouveau modèle où le plus grand est le serviteur de tous.', 'Comment exercez-vous votre influence ou autorité? Comme un serviteur humble ou comme un dominateur?', 'Seigneur Jésus, fais de moi un leader qui sert. Que je sois grand par l''humilité, non par le pouvoir. Amen.', NULL, NULL, 'humilite'),

('hu-010', 'Marcher humblement', 'On t''a fait connaître, ô homme, ce qui est bien; et ce que l''Éternel demande de toi, c''est que tu pratiques la justice, que tu aimes la miséricorde, et que tu marches humblement avec ton Dieu.', 'Michée 6:8', 'Marcher humblement avec Dieu résume notre appel. Ce n''est pas des sacrifices élaborés que Dieu demande, mais une marche quotidienne dans la justice, la miséricorde et l''humilité. C''est simple mais profond, accessible mais transformateur.', 'Votre marche avec Dieu est-elle humble? Comment pouvez-vous pratiquer l''humilité au quotidien?', 'Éternel, je veux marcher humblement avec toi. Que ma vie soit marquée par la justice et la miséricorde. Amen.', NULL, NULL, 'humilite');
